import React, { useState } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

// Icons
const PlusCircleIcon = ({ className = "w-6 h-6" }) => ( <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><circle cx="12" cy="12" r="10" /><line x1="12" y1="8" x2="12" y2="16" /><line x1="8" y1="12" x2="16" y2="12" /></svg>);
const Trash2 = ({ className = "w-6 h-6" }) => ( <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><polyline points="3 6 5 6 21 6" /><path d="M19 6v14a2 2 0 0 1-2 2H7a2 2 0 0 1-2-2V6m3 0V4a2 2 0 0 1 2-2h4a2 2 0 0 1 2 2v2" /><line x1="10" y1="11" x2="10" y2="17" /><line x1="14" y1="11" x2="14" y2="17" /></svg>);


const HSCL11Component = ({ patient, onAddResult, onDeleteResult, setAppError }) => {
    const [newHsclDate, setNewHsclDate] = useState(new Date().toISOString().split('T')[0]);
    const [newHsclAngst, setNewHsclAngst] = useState('');
    const [newHsclDepression, setNewHsclDepression] = useState('');

    const handleAdd = () => {
        const angst = parseInt(newHsclAngst, 10);
        const depression = parseInt(newHsclDepression, 10);

        if (!newHsclDate || isNaN(angst) || angst < 0 || angst > 20 || isNaN(depression) || depression < 0 || depression > 24) {
            setAppError("UngÃ¼ltige HSCL-Werte. Angst (0-20), Depression (0-24).");
            return;
        }

        onAddResult({ date: newHsclDate, angst, depression, cumulative: angst + depression });
        setNewHsclAngst('');
        setNewHsclDepression('');
        setAppError(null);
    };

    const inputStd = "mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm";
    const btnPrimary = "bg-teal-500 hover:bg-teal-600 text-white font-semibold py-2 px-4 rounded-md shadow-sm flex items-center justify-center h-10";
    const btnDanger = "bg-red-500 hover:bg-red-600 text-white font-semibold py-2 px-3 rounded-md flex items-center";

    return (
        <div>
            <h4 className="text-xl font-semibold text-gray-700 mb-4">HSCL-11 Ergebnisse</h4>
            <div className="bg-gray-50 p-4 rounded-lg mb-6 shadow">
                <h5 className="text-md font-semibold text-gray-600 mb-3">Neues HSCL-11 Ergebnis</h5>
                <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-3 items-end">
                    <div>
                        <label htmlFor="hsclDate" className="block text-sm font-medium text-gray-700">Datum</label>
                        <input type="date" id="hsclDate" value={newHsclDate} onChange={(e) => setNewHsclDate(e.target.value)} className={inputStd} />
                    </div>
                    <div>
                        <label htmlFor="hsclAngst" className="block text-sm font-medium text-gray-700">Angst (0-20)</label>
                        <input type="number" id="hsclAngst" value={newHsclAngst} onChange={(e) => setNewHsclAngst(e.target.value)} min="0" max="20" className={inputStd} />
                    </div>
                    <div>
                        <label htmlFor="hsclDepression" className="block text-sm font-medium text-gray-700">Depression (0-24)</label>
                        <input type="number" id="hsclDepression" value={newHsclDepression} onChange={(e) => setNewHsclDepression(e.target.value)} min="0" max="24" className={inputStd} />
                    </div>
                    <button onClick={handleAdd} className={btnPrimary}>
                        <PlusCircleIcon className="w-5 h-5 mr-2" /> Speichern
                    </button>
                </div>
            </div>

            <div className="mb-6">
                <h5 className="text-md font-semibold text-gray-600 mb-2">Protokollierte HSCL-11 Ergebnisse:</h5>
                <ul className="space-y-2">
                    {(patient.hscl11Results || []).map((result, index) => (
                        <li key={index} className="flex justify-between items-center bg-white border p-3 rounded-md shadow-sm">
                            <div>
                                <span className="font-medium">{new Date(result.date).toLocaleDateString('de-DE')}</span>: 
                                <span className="text-sm text-gray-600 ml-2">Angst: {result.angst}</span>, 
                                <span className="text-sm text-gray-600 ml-2">Depression: {result.depression}</span>, 
                                <span className="text-sm text-gray-600 ml-2">Gesamt: {result.cumulative}</span>
                            </div>
                            <button onClick={() => onDeleteResult(result.date)} className={`${btnDanger} p-1 text-xs`}><Trash2 className="w-4 h-4" /></button>
                        </li>
                    ))}
                    {(patient.hscl11Results || []).length === 0 && <p className="text-sm text-gray-500">Keine HSCL-11 Ergebnisse erfasst.</p>}
                </ul>
            </div>

            {(patient.hscl11Results || []).length > 0 && (
                <div>
                    <h5 className="text-md font-semibold text-gray-600 mb-3">Verlaufsgraph HSCL-11</h5>
                    <div className="bg-white p-4 rounded-lg shadow" style={{ height: '400px' }}>
                        <ResponsiveContainer width="100%" height="100%">
                            <LineChart data={(patient.hscl11Results || []).map(r => ({...r, date: new Date(r.date).toLocaleDateString('de-DE')}))} margin={{ top: 5, right: 30, left: 0, bottom: 5 }}>
                                <CartesianGrid strokeDasharray="3 3" />
                                <XAxis dataKey="date" />
                                <YAxis domain={[0, 44]} />
                                <Tooltip />
                                <Legend />
                                <Line type="monotone" dataKey="angst" name="Angst" stroke="#8884d8" activeDot={{ r: 8 }} />
                                <Line type="monotone" dataKey="depression" name="Depression" stroke="#82ca9d" />
                                <Line type="monotone" dataKey="cumulative" name="Gesamt" stroke="#ffc658" />
                            </LineChart>
                        </ResponsiveContainer>
                    </div>
                </div>
            )}
        </div>
    );
};

export default HSCL11Component;
