import React, { useState, useEffect, useCallback, useRef, useMemo } from 'react';
import { initializeApp } from 'firebase/app';
import {
    getAuth,
    onAuthStateChanged,
    createUserWithEmailAndPassword,
    signInWithEmailAndPassword,
    signOut
} from 'firebase/auth';
import { getFirestore, collection, doc, addDoc, getDocs, setDoc, deleteDoc, onSnapshot, query, writeBatch } from 'firebase/firestore';
import { getStorage, ref, uploadBytes, getDownloadURL } from "firebase/storage";
import { motion, AnimatePresence } from 'framer-motion';

// Components
import SCL90RComponent from './components/SCL90R.js';
import FEP2Component from './components/FEP2.js';
import BDI2Component from './components/BDI2.js';
import HSCL11Component from './components/HSCL11.js';
import ICD11ECTBrowser from './components/ICD11ECTBrowser.js';
import EBMBrowser from './components/EBMBrowser.js';
import BillingModal from './components/BillingModal.js';
import AbrechnungsUebersicht from './components/AbrechnungsUebersicht.js';
import DiagnostikUebersicht from './components/DiagnostikUebersicht.js';
import AddTestFlowModal from './components/AddTestFlowModal.js';
import ManualBillingSetupModal from './components/ManualBillingSetupModal.js';
import BillTestModal from './components/BillTestModal.js';
import Profile from './components/Profile.js';
import Billing from './components/Billing.js';

