async function generateGeminiText(prompt) { let chatHistory = [{ role: "user", parts: [{ text: prompt }] }]; const payload = { contents: chatHistory }; const GEMINI_API_KEY = "AIzaSyCWQFze_8cKDZ6W2DTqfqIcvhcsKWfuZLw"; const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.0-flash:generateContent?key=${GEMINI_API_KEY}`; try { const response = await fetch(apiUrl, { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify(payload) }); if (!response.ok) { const errorData = await response.json(); console.error("Gemini API error details:", errorData); const errorMessage = errorData.error?.message || `API request failed with status ${response.status}`; throw new Error(errorMessage); } const result = await response.json(); if (result.candidates && result.candidates.length > 0 && result.candidates[0].content && result.candidates[0].content.parts && result.candidates[0].content.parts.length > 0) { return result.candidates[0].content.parts[0].text; } else if (result.candidates && result.candidates.length > 0 && result.candidates[0].finishReason === "SAFETY") { console.warn("Gemini API response blocked due to safety reasons."); return "Die Antwort wurde aufgrund von Sicherheitsrichtlinien blockiert."; } else { console.error("Unexpected Gemini API response structure:", result); throw new Error("Unerwartete Antwortstruktur von der API."); } } catch (error) { console.error("Error calling Gemini API:", error); throw error; } }
const calculateAge = (birthdate) => { if (!birthdate) return 0; const birthDate = new Date(birthdate); const today = new Date(); let age = today.getFullYear() - birthDate.getFullYear(); const m = today.getMonth() - birthDate.getMonth(); if (m < 0 || (m === 0 && today.getDate() < birthDate.getDate())) { age--; } return age; };
const useDiagnostikAnalysis = (patient) => {
    return useMemo(() => {
        if (!patient) { return { pointsLimit: 0, instancesLimit: Infinity, currentPhase: 'Unbekannt', instancesInCurrentPhase: 0, quarterlyAnalysis: {}, availableQuarters: [], currentQuarterIdentifier: '', age: 0, }; }
        const age = calculateAge(patient.geburtsdatum);
        const pointsLimit = age < 22 ? DIAGNOSTIK_LIMITS.PUNKTE_U22 : DIAGNOSTIK_LIMITS.PUNKTE_UE22;
        const allTestSessions = (patient.sessions || []).filter(s => s.ebmEntries?.some(e => DIAGNOSTIK_PUNKTE[e.nummer]));
        const quarterlyAnalysis = {};
        allTestSessions.forEach(session => {
            const date = new Date(session.date);
            const year = date.getFullYear();
            const quarter = Math.floor(date.getMonth() / 3) + 1;
            const quarterIdentifier = `${year}-Q${quarter}`;
            if (!quarterlyAnalysis[quarterIdentifier]) { quarterlyAnalysis[quarterIdentifier] = { pointsUsed: 0, instancesUsed: 0, tests: [] }; }
            quarterlyAnalysis[quarterIdentifier].instancesUsed += 1;
            session.ebmEntries.forEach(entry => { if (DIAGNOSTIK_PUNKTE[entry.nummer]) { quarterlyAnalysis[quarterIdentifier].pointsUsed += DIAGNOSTIK_PUNKTE[entry.nummer]; quarterlyAnalysis[quarterIdentifier].tests.push({ ...entry, date: session.date }); } });
        });
        const today = new Date();
        const currentYear = today.getFullYear();
        const currentQuarter = Math.floor(today.getMonth() / 3) + 1;
        const currentQuarterIdentifier = `${currentYear}-Q${currentQuarter}`;
        if (!quarterlyAnalysis[currentQuarterIdentifier]) { quarterlyAnalysis[currentQuarterIdentifier] = { pointsUsed: 0, instancesUsed: 0, tests: [] }; }
        const availableQuarters = Object.keys(quarterlyAnalysis).sort().reverse();
        let currentPhase = 'Probatorik';
        let instancesLimit = Infinity;
        let instancesInCurrentPhase = 0;
        const lztSession = allTestSessions.find(s => s.structuredNotes?.Phase === 'Langzeittherapie');
        const kztSession = allTestSessions.find(s => s.structuredNotes?.Phase === 'Kurzzeittherapie');
        if (lztSession) { currentPhase = 'Langzeittherapie'; instancesLimit = DIAGNOSTIK_LIMITS.INSTANZEN_LZT; instancesInCurrentPhase = allTestSessions.filter(s => s.structuredNotes?.Phase === 'Langzeittherapie' || s.structuredNotes?.Phase === 'Kurzzeittherapie').length; } else if (kztSession) { currentPhase = 'Kurzzeittherapie'; instancesLimit = DIAGNOSTIK_LIMITS.INSTANZEN_KZT; instancesInCurrentPhase = allTestSessions.filter(s => s.structuredNotes?.Phase === 'Kurzzeittherapie').length; }
        return { pointsLimit, instancesLimit, currentPhase, instancesInCurrentPhase, quarterlyAnalysis, availableQuarters, currentQuarterIdentifier, age, };
    }, [patient]);
};

