import React, { useState, useEffect, useMemo } from 'react';

// Icons
const SaveIcon = ({ className = "w-4 h-4" }) => <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="M19 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h11l5 5v11a2 2 0 0 1-2 2z" /><polyline points="17 21 17 13 7 13 7 21" /><polyline points="7 3 7 8 15 8" /></svg>;
const PlusIcon = ({ className = "w-4 h-4" }) => <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><line x1="12" y1="5" x2="12" y2="19"></line><line x1="5" y1="12" x2="19" y2="12"></line></svg>;
const TrashIcon = ({ className = "w-4 h-4" }) => <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><polyline points="3 6 5 6 21 6"></polyline><path d="M19 6v14a2 2 0 0 1-2 2H7a2 2 0 0 1-2-2V6m3 0V4a2 2 0 0 1 2-2h4a2 2 0 0 1 2 2v2"></path></svg>;
const WandSparkles = ({ className = "w-5 h-5" }) => (<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="m9.5 2.55 1.294 1.294a1 1 0 0 0 1.414 0L13.5 2.55"/><path d="M4.94 4.94 6.22 6.22a1 1 0 0 0 1.41 0l1.29-1.29"/><path d="M2.55 9.51 3.84 10.8a1 1 0 0 0 1.41 0l1.3-1.29"/><path d="m19.07 4.93-1.29 1.29a1 1 0 0 1-1.41 0l-1.29-1.29"/><path d="M21.45 9.51l-1.29 1.29a1 1 0 0 1-1.41 0l-1.3-1.29"/><path d="M14.5 12.94a1 1 0 0 0 0 1.41l1.29 1.29"/><path d="M10.8 17.78a1 1 0 0 0 0 1.41l1.29 1.29"/><path d="M17.78 10.8a1 1 0 0 0 0 1.41l1.29 1.29"/><path d="M12 10.5V4.5"/><path d="M12 10.5C7 11 4 15 4 20"/><path d="M12 10.5c5 .5 8 4.5 8 9.5"/></svg>);

// Loader-Komponente fÃ¼r die KI-Analyse
const ParsingLoader = () => (
    <div className="absolute inset-0 bg-white bg-opacity-80 flex flex-col justify-center items-center z-20 rounded-lg">
        <svg className="animate-spin h-8 w-8 text-red-600" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
            <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
            <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
        </svg>
        <p className="mt-3 text-gray-700 font-semibold">Text wird analysiert...</p>
    </div>
);

// A simple modal just for text input
const PasteTextModal = ({ isOpen, onClose, onSave, isParsing }) => {
    const [text, setText] = useState('');
    if (!isOpen) return null;

    const handleSave = () => {
        onSave(text);
        onClose();
    };

    return (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-[60]">
            <div className="bg-white p-6 rounded-lg shadow-xl w-full max-w-2xl">
                <h3 className="text-lg font-semibold mb-4">Sitzungsdoku einfÃ¼gen</h3>
                <textarea
                    className="w-full h-64 p-2 border rounded-md"
                    value={text}
                    onChange={(e) => setText(e.target.value)}
                    placeholder="FÃ¼gen Sie hier den kompletten Text Ihrer Sitzungsdokumentation ein..."
                />
                <div className="flex justify-end space-x-3 mt-4">
                    <button onClick={onClose} className="px-4 py-2 text-sm text-gray-700 bg-gray-200 rounded-md">Abbrechen</button>
                    <button onClick={handleSave} disabled={isParsing || !text} className="px-4 py-2 text-sm text-white bg-indigo-600 rounded-md disabled:bg-gray-400">
                        {isParsing ? 'Analysiere...' : 'Text zuordnen'}
                    </button>
                </div>
            </div>
        </div>
    );
};


const BillingModal = ({ event, ebmData, onSave, onClose, generateGeminiText }) => {
    // States
    const [step, setStep] = useState(1);
    const [ebmEntries, setEbmEntries] = useState([]);
    const [ebmSearch, setEbmSearch] = useState('');
    const [selectedEbm, setSelectedEbm] = useState(null);
    const [anzahl, setAnzahl] = useState(1);
    const [faktor, setFaktor] = useState(1);
    const [isParsing, setIsParsing] = useState(false);
    const [isPasteModalOpen, setIsPasteModalOpen] = useState(false);

    // NEU: State fÃ¼r Notizen mit Hausaufgaben als Array
    const [notes, setNotes] = useState({
        thema: '',
        patientenbericht: '',
        therapeutischeInterventionen: '',
        gesamteindruck: '',
        risikoabschaetzung: '',
        hausaufgaben: [''] // Hausaufgaben ist jetzt ein Array von Strings
    });
    const [isSuicidal, setIsSuicidal] = useState(false);

    const riskTextStandard = 'Auf Nachfrage wurden suizidale Gedanken, Impulse sowie Handlungsabsichten glaubhaft und eindeutig verneint. Die AbsprachefÃ¤higkeit des Patienten ist gegeben. Es besteht kein Anhalt fÃ¼r eine akute Eigen- oder FremdgefÃ¤hrdung.';

    // NEU: Angepasste KI-Funktion zum Parsen
    const handleAutoParseNotes = async (rawNotes) => {
        setIsParsing(true);
        const prompt = `Du bist ein Experte fÃ¼r die Analyse klinischer Dokumentationen. Deine Aufgabe ist es, den folgenden Text in strukturierte Abschnitte zu zerlegen. Die Abschnitte sind: "Thema", "Patientenbericht", "Therapeutische Interventionen", "Gesamteindruck", "RisikoabschÃ¤tzung", "Hausaufgaben". Analysiere den Text und gib ein valides JSON-Objekt zurÃ¼ck, dessen SchlÃ¼ssel exakt diesen Titeln entsprechen (in camelCase: "thema", "patientenbericht", "therapeutischeInterventionen", "gesamteindruck", "risikoabschaetzung", "hausaufgaben"). 
        WICHTIG: FÃ¼r den SchlÃ¼ssel "hausaufgaben", wenn der Text mehrere Punkte enthÃ¤lt (oft durch "-" oder AufzÃ¤hlungszeichen getrennt), gib diese als Array von Strings zurÃ¼ck. Beispiel: ["Aufgabe 1", "Aufgabe 2"]. Wenn nur eine Aufgabe oder keine da ist, gib ein Array mit dem einen String oder ein leeres Array zurÃ¼ck.
        Gib NUR das JSON-Objekt zurÃ¼ck.
        Der zu parsende Text lautet:
        ---
        ${rawNotes}
        ---`;
        try {
            const result = await generateGeminiText(prompt);
            const cleanedResult = result.replace(/```json/g, '').replace(/```/g, '').trim();
            const parsedJson = JSON.parse(cleanedResult);

            if (parsedJson.risikoabschaetzung && parsedJson.risikoabschaetzung !== riskTextStandard) {
                setIsSuicidal(true);
            }

            // NEU: Stelle sicher, dass Hausaufgaben immer ein Array ist
            const parsedHomework = (parsedJson.hausaufgaben && Array.isArray(parsedJson.hausaufgaben) && parsedJson.hausaufgaben.length > 0)
                ? parsedJson.hausaufgaben
                : ['']; // Fallback auf ein Array mit einem leeren String

            setNotes(prev => ({
                ...prev,
                ...parsedJson,
                hausaufgaben: parsedHomework
            }));

        } catch (error) {
            console.error("Error parsing notes with AI:", error);
            console.error("Die automatische Zuordnung ist fehlgeschlagen. Bitte Ã¼berprÃ¼fen Sie den Text oder fÃ¼llen Sie die Felder manuell aus.");
        } finally {
            setIsParsing(false);
        }
    };

    const handleNoteChange = (e) => { setNotes(prev => ({ ...prev, [e.target.name]: e.target.value })); };

    // NEU: Handler fÃ¼r die dynamische Hausaufgabenliste
    const handleHomeworkChange = (e, index) => {
        const newHomeworks = [...notes.hausaufgaben];
        newHomeworks[index] = e.target.value;
        setNotes(prev => ({ ...prev, hausaufgaben: newHomeworks }));
    };

    const handleAddHomework = () => {
        setNotes(prev => ({ ...prev, hausaufgaben: [...prev.hausaufgaben, ''] }));
    };

    const handleDeleteHomework = (index) => {
        if (notes.hausaufgaben.length > 1) {
            const newHomeworks = notes.hausaufgaben.filter((_, i) => i !== index);
            setNotes(prev => ({ ...prev, hausaufgaben: newHomeworks }));
        }
    };

    useEffect(() => {
        if (!isParsing) {
            setNotes(prev => ({ ...prev, risikoabschaetzung: isSuicidal ? prev.risikoabschaetzung : riskTextStandard }));
        }
    }, [isSuicidal, isParsing]);

    const searchResults = useMemo(() => {
        if (!ebmSearch) return [];
        return ebmData.filter(item =>
            item.Nummer.startsWith(ebmSearch) ||
            item.Titel.toLowerCase().includes(ebmSearch.toLowerCase())
        ).slice(0, 5);
    }, [ebmSearch, ebmData]);

    const handleSelectEbm = (item) => { setSelectedEbm(item); setEbmSearch(`${item.Nummer} - ${item.Titel}`); };
    const handleAddEbmEntry = () => { if (selectedEbm) { setEbmEntries(p => [...p, { ...selectedEbm, anzahl, faktor }]); setSelectedEbm(null); setEbmSearch(''); setAnzahl(1); setFaktor(1); } };
    const handleDeleteEbmEntry = (index) => setEbmEntries(p => p.filter((_, i) => i !== index));
    const totalPoints = ebmEntries.reduce((sum, entry) => sum + ((parseInt(entry.Punkte, 10) || 0) * entry.anzahl * entry.faktor), 0);
    const handleSave = () => {
        const m = event.summary.match(/[A-Z][0-9]{6}/);
        onSave({ eventId: event.id, eventStart: event.start.dateTime || event.start.date, chiffre: m ? m[0] : null, ebmEntries: ebmEntries.map(e => ({ nummer: e.Nummer, anzahl: e.anzahl, faktor: e.faktor })), notes });
    };

    // Styling
    const inputStyle = "block w-full px-3 py-2 bg-gray-50 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-red-500 focus:border-red-500 sm:text-sm";
    const textareaStyle = `${inputStyle} min-h-[90px] resize-y`;
    const accentButton = "bg-red-600 text-white font-semibold py-2 px-3 text-sm rounded-md shadow-sm hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500 flex items-center justify-center";

    return (
        <>
            <PasteTextModal isOpen={isPasteModalOpen} onClose={() => setIsPasteModalOpen(false)} onSave={handleAutoParseNotes} isParsing={isParsing} />
            <div className="p-1">
                <div className="flex justify-center items-center space-x-4 mb-6">
                    <div className={`flex items-center ${step >= 1 ? 'text-red-600' : 'text-gray-400'}`}><div className={`w-8 h-8 rounded-full flex items-center justify-center font-bold border-2 ${step >= 1 ? 'bg-red-600 border-red-700 text-white' : 'bg-gray-200 border-gray-300'}`}>1</div><span className="ml-2 font-semibold">Leistungen</span></div>
                    <div className={`h-0.5 w-16 ${step >= 2 ? 'bg-red-500' : 'bg-gray-200'}`}></div>
                    <div className={`flex items-center ${step >= 2 ? 'text-red-600' : 'text-gray-400'}`}><div className={`w-8 h-8 rounded-full flex items-center justify-center font-bold border-2 ${step >= 2 ? 'bg-red-600 border-red-700 text-white' : 'bg-gray-200 border-gray-300'}`}>2</div><span className="ml-2 font-semibold">Dokumentation</span></div>
                </div>

                {step === 1 && (
                    <div className="animate-fade-in space-y-4">
                        <div className="p-4 border rounded-lg bg-white">
                            <h3 className="font-semibold text-lg text-gray-800 mb-3">Leistung hinzufÃ¼gen</h3>
                            <div className="relative"><input type="text" placeholder="EBM-Ziffer oder Titel suchen..." value={ebmSearch} onChange={e => { setEbmSearch(e.target.value); setSelectedEbm(null); }} className={inputStyle} />{searchResults.length > 0 && ebmSearch && !selectedEbm && (<ul className="absolute z-10 w-full bg-white border border-gray-300 rounded-md mt-1 shadow-lg max-h-48 overflow-y-auto">{searchResults.map(item => <li key={item.Nummer} onClick={() => handleSelectEbm(item)} className="px-3 py-2 cursor-pointer hover:bg-red-50">{item.Nummer} - {item.Titel}</li>)}</ul>)}</div>
                            <div className="grid grid-cols-3 gap-3 mt-3">
                                <div><label className="text-xs font-medium text-gray-600">Anzahl</label><input type="number" value={anzahl} onChange={e => setAnzahl(Math.max(1, parseInt(e.target.value, 10) || 1))} min="1" className={`${inputStyle} text-center`} /></div>
                                <div><label className="text-xs font-medium text-gray-600">Faktor</label><input type="number" step="0.1" value={faktor} onChange={e => setFaktor(parseFloat(e.target.value) || 1)} min="1" className={`${inputStyle} text-center`} /></div>
                                <div className="self-end"><button onClick={handleAddEbmEntry} disabled={!selectedEbm} className="w-full bg-gray-700 text-white p-2 rounded-md hover:bg-gray-800 disabled:bg-gray-300 flex items-center justify-center"><PlusIcon className="w-4 h-4 mr-1"/> HinzufÃ¼gen</button></div>
                            </div>
                        </div>
                        <div className="p-4 border rounded-lg bg-white mt-4">
                            <h3 className="font-semibold text-lg text-gray-800 mb-3">Abzurechnende Leistungen</h3>
                            <div className="space-y-2">{ebmEntries.map((entry, index) => (<div key={index} className="flex justify-between items-center bg-gray-50 p-2 rounded-md"><div><p className="font-semibold">{entry.Nummer} <span className="text-gray-600 font-normal">({entry.Titel})</span></p><p className="text-xs text-gray-500">Anzahl: {entry.anzahl}, Faktor: {entry.faktor}</p></div><div className="flex items-center"><span className="mr-4 font-mono text-gray-800">{(parseInt(entry.Punkte, 10) * entry.anzahl * entry.faktor).toFixed(0)} Pkt.</span><button onClick={() => handleDeleteEbmEntry(index)} className="text-red-500 hover:text-red-700 p-1"><TrashIcon /></button></div></div>))}{ebmEntries.length === 0 && <p className="text-sm text-gray-500 text-center py-4">Noch keine Leistungen hinzugefÃ¼gt.</p>}<div className="text-right font-bold text-xl mt-4 pt-4 border-t">Gesamt: {totalPoints.toFixed(0)} Punkte</div></div>
                        </div>
                    </div>
                )}

                {step === 2 && (
                    <div className="relative">
                        {isParsing && <ParsingLoader />}
                        <div className="animate-fade-in space-y-4">
                            <div className="flex justify-end">
                                {/* NEU: Angepasster Button fÃ¼r KI-Analyse */}
                                <button onClick={() => setIsPasteModalOpen(true)} className="bg-gray-700 text-white py-1.5 px-3 text-xs rounded-md hover:bg-gray-800 flex items-center font-semibold">
                                    <WandSparkles className="w-4 h-4 mr-1.5" />
                                    KI-Analyse
                                </button>
                            </div>
                            <div className="p-4 border rounded-lg bg-white space-y-3">
                                <h3 className="font-semibold text-lg text-gray-800">Strukturierte Dokumentation</h3>
                                <div><label className="text-sm font-medium text-gray-600">Thema der Sitzung</label><input name="thema" value={notes.thema} onChange={handleNoteChange} className={inputStyle} /></div>
                                <div><label className="text-sm font-medium text-gray-600">Patientenbericht</label><textarea name="patientenbericht" value={notes.patientenbericht} onChange={handleNoteChange} className={textareaStyle} /></div>
                                <div><label className="text-sm font-medium text-gray-600">Therapeutische Interventionen</label><textarea name="therapeutischeInterventionen" value={notes.therapeutischeInterventionen} onChange={handleNoteChange} className={textareaStyle} /></div>
                                <div><label className="text-sm font-medium text-gray-600">Gesamteindruck</label><textarea name="gesamteindruck" value={notes.gesamteindruck} onChange={handleNoteChange} className={textareaStyle} /></div>
                                <div>
                                    <label className="text-sm font-medium text-gray-600">RisikoabschÃ¤tzung (SuizidalitÃ¤t)</label>
                                    <div className="flex items-center space-x-4 mt-1 mb-2">
                                        <label className="flex items-center"><input type="radio" checked={!isSuicidal} onChange={() => setIsSuicidal(false)} className="h-4 w-4 text-red-600 focus:ring-red-500"/> <span className="ml-2 text-sm">Nein</span></label>
                                        <label className="flex items-center"><input type="radio" checked={isSuicidal} onChange={() => setIsSuicidal(true)} className="h-4 w-4 text-red-600 focus:ring-red-500"/> <span className="ml-2 text-sm">Ja, Interventionsbedarf</span></label>
                                    </div>
                                    <textarea name="risikoabschaetzung" value={notes.risikoabschaetzung} onChange={handleNoteChange} disabled={!isSuicidal} className={`${textareaStyle} ${!isSuicidal ? 'bg-gray-100' : ''}`} />
                                </div>
                                {/* NEU: Dynamische Hausaufgaben-Liste */}
                                <div>
                                    <label className="text-sm font-medium text-gray-600 mb-1 block">Hausaufgaben</label>
                                    <div className="space-y-2">
                                        {notes.hausaufgaben.map((task, index) => (
                                            <div key={index} className="flex items-center space-x-2">
                                                <input name={`hausaufgabe-${index}`} value={task} onChange={(e) => handleHomeworkChange(e, index)} className={inputStyle} placeholder={`Aufgabe ${index + 1}`}/>
                                                {notes.hausaufgaben.length > 1 && (
                                                    <button type="button" onClick={() => handleDeleteHomework(index)} className="p-1 text-gray-400 hover:text-red-500 rounded-full flex-shrink-0">
                                                        <TrashIcon className="w-4 h-4" />
                                                    </button>
                                                )}
                                            </div>
                                        ))}
                                    </div>
                                    <button type="button" onClick={handleAddHomework} className="mt-2 flex items-center text-sm text-red-600 hover:text-red-800 font-semibold">
                                        <PlusIcon className="w-4 h-4 mr-1" />
                                        Aufgabe hinzufÃ¼gen
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>
                )}

                <div className="flex justify-between pt-6 mt-6 border-t">
                    <button onClick={() => step > 1 ? setStep(step - 1) : onClose()} className="px-4 py-2 text-sm font-medium text-gray-700 bg-gray-200 rounded-md hover:bg-gray-300">{step === 1 ? 'Abbrechen' : 'ZurÃ¼ck'}</button>
                    {step === 1 && <button onClick={() => setStep(2)} className={accentButton}>Weiter</button>}
                    {step === 2 && <button onClick={handleSave} className={`${accentButton} bg-green-600 hover:bg-green-700`}><SaveIcon className="w-4 h-4 mr-2"/> Abrechnung speichern</button>}
                </div>
            </div>
        </>
    );
};

export default BillingModal;
