  const [editingPatient, setEditingPatient] = useState(null);
  const [isConfirmDeleteModalOpen, setIsConfirmDeleteModalOpen] = useState(false);
  const [patientToDeleteId, setPatientToDeleteId] = useState(null);

  const [newDiagnosis, setNewDiagnosis] = useState('');
  const [newDiagnosisCode, setNewDiagnosisCode] = useState(''); // Für den Code-Teil der Diagnose
  const [lookedUpDiagnosisName, setLookedUpDiagnosisName] = useState(''); // Für den Namen aus JSON

