  const [newBdiScore, setNewBdiScore] = useState(''); 

  const [newFepDate, setNewFepDate] = useState(new Date().toISOString().split('T')[0]);
  const [fepItemScores, setFepItemScores] = useState(Array(40).fill(0)); 

  const [sessionSummaries, setSessionSummaries] = useState({}); 
  const [diagnosisInfo, setDiagnosisInfo] = useState({ code: '', name: '', beschreibung: '', inkl: [], exkl: [], hinweise: [], zusatz_info_code: '', isLoading: false, error: null, isOpen: false, source: '' });

  const [localIcd10Data, setLocalIcd10Data] = useState({}); // Wird mit { code: details } gefüllt

  // Lokale ICD-10 Daten laden und indizieren
  useEffect(() => {
    // In einer echten lokalen App würden Sie hier Ihre JSON-Datei laden, z.B. mit fetch('/icd10f_data.json')
    // Für dieses Beispiel verwenden wir die eingebetteten icd10F0SampleData
    const indexedData = {};
    icd10F0SampleData.forEach(item => {
        if (item.code) { // Nur Einträge mit einem spezifischen 'code' indizieren
            indexedData[item.code] = item;
        }
        // Optional: Kategorien auch indizieren, falls benötigt
        if (item.code_kategorie) {
            // Um Überschreibungen zu vermeiden, wenn mehrere Einträge dieselbe code_kategorie haben,
            // könnte man hier eine komplexere Logik oder ein Array verwenden.
            // Für eine einfache Suche nach spezifischen Codes ist dies erstmal nicht primär.
            // indexedData[item.code_kategorie] = item; 
        }
    });
    setLocalIcd10Data(indexedData);
    console.log("Lokale ICD-10 Daten indiziert:", indexedData);
  }, []);

  // Funktion zum Suchen eines ICD-10 Codes in den lokalen Daten
  const findIcd10CodeLocal = useCallback((codeToFind) => {
    // Versuche exakte Übereinstimmung
    let found = localIcd10Data[codeToFind];
    if (found) return found;

    // Versuche Übereinstimmung ohne Suffixe wie ".-*" oder "*"
    const baseCode = codeToFind.replace(/(\.-\*|\.\*|\*|-)$/, '');
    found = localIcd10Data[`${baseCode}.-*`] || localIcd10Data[`${baseCode}.*`] || localIcd10Data[baseCode];
    if (found) return found;

    // Fallback: Suche nach Codes, die mit dem eingegebenen Code beginnen (für breitere Kategorien)
    // Dies ist nützlich, wenn der Benutzer z.B. "F00" eingibt und Infos zu "F00.-*" sehen möchte.
    const possibleCategoryCode = Object.keys(localIcd10Data).find(
        key => key.startsWith(codeToFind) && (localIcd10Data[key].code_kategorie || localIcd10Data[key].code) // Sicherstellen, dass es ein relevanter Eintrag ist
    );
    if (possibleCategoryCode) return localIcd10Data[possibleCategoryCode];

    return null;
  }, [localIcd10Data]);

  // Effekt für die Suche bei Eingabe einer neuen Diagnose
  useEffect(() => {
    if (newDiagnosisCode.trim() !== '') {
        const found = findIcd10CodeLocal(newDiagnosisCode.trim().toUpperCase());
        if (found && found.name) {
            setLookedUpDiagnosisName(found.name);
        } else {
            setLookedUpDiagnosisName('');
        }
    } else {
        setLookedUpDiagnosisName('');
    }
  }, [newDiagnosisCode, findIcd10CodeLocal]);


  const getPatientsCollectionPath = useCallback(() => { 
    if (!userId) return null; 
    return `/artifacts/${appId}/users/${userId}/patients`; 
  }, [userId]);

  useEffect(() => { 
    const unsubscribe = onAuthStateChanged(auth, async (user) => { 
      if (user) { 
        setUserId(user.uid); 
      } else { 
        try { 
          if (typeof __initial_auth_token !== 'undefined' && __initial_auth_token) { 
            await signInWithCustomToken(auth, __initial_auth_token); 
          } else { 
            await signInAnonymously(auth); 
          } 
        } catch (e) { 
          console.error("Error signing in: ", e); 
          setError("Fehler bei der Authentifizierung."); 
        } 
      } 
      setIsAuthReady(true); 
    }); 
    return () => unsubscribe(); 
  }, []);

  const seedInitialData = useCallback(async () => { 
    if (!userId) return; 
    const collectionPath = getPatientsCollectionPath(); 
    if (!collectionPath) return; 
    try { 
      const patientsCollection = collection(db, collectionPath); 
      const querySnapshot = await getDocs(patientsCollection); 
      if (querySnapshot.empty) { 
        const batch = writeBatch(db); 
        initialPatientsData.forEach(patientData => { 
          const newPatientRef = doc(patientsCollection); 
          batch.set(newPatientRef, { ...patientData, hscl11Results: patientData.hscl11Results || [], bdi2Results: patientData.bdi2Results || [], fep2Results: patientData.fep2Results || [] }); 
        }); 
        await batch.commit(); 
        console.log("Initiale Daten erfolgreich geschrieben.");
      } else {
        console.log("Vorhandene Patientendaten gefunden, Seed wird übersprungen.");
      }
    } catch (err) { 
      console.error("Error seeding initial data:", err); 
      setError("Fehler beim Laden der initialen Daten."); 
    } 
  }, [userId, getPatientsCollectionPath]);

  useEffect(() => {
    if (!isAuthReady || !userId) { 
      if(isAuthReady && !userId) setError("Authentifizierung fehlgeschlagen. Bitte laden Sie die Seite neu.");
      return; 
    }
    const collectionPath = getPatientsCollectionPath(); 
    if (!collectionPath) {
        console.log("Collection path ist nicht verfügbar für Firestore Listener.");
        return;
    }

    setIsLoading(true); 
    seedInitialData(); 

    const patientsQuery = query(collection(db, collectionPath));
    const unsubscribe = onSnapshot(patientsQuery, (snapshot) => {
      let patientsData = snapshot.docs.map(docSnap => ({ 
        id: docSnap.id, 
        ...docSnap.data(), 
        hscl11Results: docSnap.data().hscl11Results || [], 
        bdi2Results: docSnap.data().bdi2Results || [], 
        fep2Results: docSnap.data().fep2Results || [] 
      }));
      patientsData.sort((a, b) => a.name.localeCompare(b.name)); 
      setPatients(patientsData); 
      setIsLoading(false); 
    }, (err) => {
      console.error("Error fetching patients:", err);
      setError("Fehler beim Laden der Patientendaten. Stellen Sie sicher, dass Firestore korrekt konfiguriert ist.");
      setIsLoading(false);
    });

    return () => unsubscribe(); 
  }, [isAuthReady, userId, getPatientsCollectionPath, seedInitialData]); 

  useEffect(() => {
    if (selectedPatient && selectedPatient.id && patients.length > 0) {
        const currentPatientDataFromList = patients.find(p => p.id === selectedPatient.id);
        if (currentPatientDataFromList) {
            if (JSON.stringify(currentPatientDataFromList) !== JSON.stringify(selectedPatient)) {
                 setSelectedPatient(currentPatientDataFromList);
            }
        } else {
            setSelectedPatient(null);
        }
    }
  // eslint-disable-next-line react-hooks/exhaustive-deps 
  }, [patients, selectedPatient?.id]); 


  const handleSavePatient = async (patientData) => { 
    if (!userId) { setError("Benutzer nicht authentifiziert."); return; } 
    const collectionPath = getPatientsCollectionPath(); 
    if (!collectionPath) return; 
    const dataToSave = { ...patientData, hscl11Results: patientData.hscl11Results || [], bdi2Results: patientData.bdi2Results || [], fep2Results: patientData.fep2Results || [] }; 
    try { 
      if (editingPatient && editingPatient.id) { 
        const patientRef = doc(db, collectionPath, editingPatient.id); 
        await setDoc(patientRef, dataToSave, { merge: true }); 
      } else { 
        await addDoc(collection(db, collectionPath), dataToSave); 
      } 
      setIsPatientModalOpen(false); 
      setEditingPatient(null); 
    } catch (err) { 
      console.error("Error saving patient:", err); 
      setError("Fehler beim Speichern des Patienten."); 
    } 
  };
