  const confirmDeletePatient = async () => { 
    if (!patientToDeleteId || !userId) { setError("Löschvorgang fehlgeschlagen."); setIsConfirmDeleteModalOpen(false); return; } 
    const collectionPath = getPatientsCollectionPath(); 
    if (!collectionPath) return; 
    try { 
      const patientRef = doc(db, collectionPath, patientToDeleteId); 
      await deleteDoc(patientRef); 
      if (selectedPatient && selectedPatient.id === patientToDeleteId) { 
        setSelectedPatient(null); 
      } 
      setPatientToDeleteId(null); 
      setIsConfirmDeleteModalOpen(false); 
    } catch (err) { 
      console.error("Error deleting patient:", err); 
      setError("Fehler beim Löschen des Patienten."); 
      setIsConfirmDeleteModalOpen(false); 
    } 
  };

  const handleAddDiagnosis = async () => {
    const codeToAdd = newDiagnosisCode.trim().toUpperCase();
    if (!selectedPatient || !codeToAdd || !userId) return;

    const collectionPath = getPatientsCollectionPath(); if (!collectionPath) return;

    // Verwende den Code, optional mit dem gefundenen Namen, wenn der Benutzer keinen eigenen Namen eingegeben hat
    const diagnosisString = newDiagnosis.trim() ? `${codeToAdd}: ${newDiagnosis.trim()}` : (lookedUpDiagnosisName ? `${codeToAdd}: ${lookedUpDiagnosisName}` : codeToAdd);

    const updatedDiagnoses = [...(selectedPatient.diagnoses || []), diagnosisString];
    const patientRef = doc(db, collectionPath, selectedPatient.id);
    try { 
        await setDoc(patientRef, { diagnoses: updatedDiagnoses }, { merge: true }); 
        setNewDiagnosisCode(''); // Feld für Code zurücksetzen
        setNewDiagnosis('');      // Feld für manuelle Beschreibung zurücksetzen
        setLookedUpDiagnosisName(''); // Nachschlagefeld zurücksetzen
    } 
    catch (err) { console.error("Error adding diagnosis:", err); setError("Fehler beim Hinzufügen der Diagnose.");}
  };
  const handleRemoveDiagnosis = async (diagnosisStringToRemove) => { // Nimmt jetzt den ganzen String
    if (!selectedPatient || !userId) return;
    const collectionPath = getPatientsCollectionPath(); if (!collectionPath) return;
    const updatedDiagnoses = selectedPatient.diagnoses.filter(d => d !== diagnosisStringToRemove);
    const patientRef = doc(db, collectionPath, selectedPatient.id);
    try { await setDoc(patientRef, { diagnoses: updatedDiagnoses }, { merge: true }); }
    catch (err) { console.error("Error removing diagnosis:", err); setError("Fehler beim Entfernen der Diagnose.");}
  };

