  const fetchSessionSummary = async (sessionNotes, sessionIndex) => { /* ... wie zuvor ... */ };

  // Modifizierte Funktion, um lokale ICD-10 Daten anzuzeigen
  const showDiagnosisInfo = (diagnosisString) => {
    const codeMatch = diagnosisString.match(/^([A-Z][0-9]{1,2}(\.[0-9A-Z]{1,2})?[\*\-\!]?)/i); // Extrahiert den Code am Anfang
    const code = codeMatch ? codeMatch[0].toUpperCase() : diagnosisString.toUpperCase();

    const localData = findIcd10CodeLocal(code);
    if (localData) {
        setDiagnosisInfo({
            code: localData.code || code,
            name: localData.name || '',
            beschreibung: localData.beschreibung || localData.beschreibung_kategorie || '',
