  const fetchDiagnosisInfoGemini = async (diagnosisCode) => {
    setDiagnosisInfo({ code: diagnosisCode, info: '', isLoading: true, error: null, isOpen: true, source: 'gemini' });
    const prompt = `Erkläre die ICD-10 Diagnose '${diagnosisCode}' kurz und verständlich für einen Laien. Nenne auch typische Symptome und ggf. wichtige Differentialdiagnosen oder Komorbiditäten, falls relevant. Antworte auf Deutsch.`;
