  const btnInfo = "bg-sky-500 hover:bg-sky-600 text-white font-semibold py-1 px-2 text-xs rounded-md flex items-center";


  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-100 to-sky-100 font-sans">
      <header className="bg-indigo-700 text-white shadow-lg"><div className="container mx-auto px-4 sm:px-6 lg:px-8 py-4 flex justify-between items-center"><h1 className="text-3xl font-bold tracking-tight">Patientenverwaltung</h1>{userId && <p className="text-xs opacity-75">UserID: {userId}</p>}</div></header>
      <main className="container mx-auto p-4 sm:p-6 lg:p-8">
