        <div className="bg-white shadow-xl rounded-lg overflow-hidden mb-8"><div className="overflow-x-auto"><table className="w-full min-w-max table-auto text-left">
            <thead className="bg-gray-100 border-b border-gray-300"><tr>{['Name', 'Chiffre', 'Therapie', 'Genehm. Std.', 'Durchgef. Std.', 'Fortschritt', 'Aktionen'].map(header => (<th key={header} className="py-3 px-4 text-sm font-semibold text-gray-600 uppercase tracking-wider">{header}</th>))}</tr></thead>
