                        <div className="flex-grow">
                            <label htmlFor="newDiagnosisCode" className="block text-xs font-medium text-gray-700">Code (z.B. F32.2)</label>
                            <input type="text" id="newDiagnosisCode" value={newDiagnosisCode} onChange={(e) => setNewDiagnosisCode(e.target.value.toUpperCase())} placeholder="F-Code" className={`${inputStd} mt-1`}/>
