                                            nextAppointment={nextAppointment}
                                            onUpdateHomeworkStatus={handleUpdateHomeworkStatus}
                                        />
                                    )}

                                    {activeDetailTab === 'diagnosen' && (<div><div className="mb-6"><h4 className="text-xl font-semibold text-gray-700 mb-3">ICD-10 F-Diagnosen</h4><div className="mb-3 p-4 bg-gray-50 rounded-md border"><div className="grid grid-cols-1 md:grid-cols-3 gap-4 items-end"><div><label htmlFor="newDiagnosisCode" className="block text-xs font-medium text-gray-700">Code (z.B. F32.2)</label><input type="text" id="newDiagnosisCode" value={newDiagnosisCode} onChange={(e) => setNewDiagnosisCode(e.target.value.toUpperCase())} placeholder="F-Code" className={`${inputStd} mt-1`} /></div><div><label htmlFor="newDiagnosisSicherheit" className="block text-xs font-medium text-gray-700">Sicherheit</label><select id="newDiagnosisSicherheit" value={newDiagnosisSicherheit} onChange={(e) => setNewDiagnosisSicherheit(e.target.value)} className={`${inputStd} mt-1`}><option value="G">G (Gesichert)</option><option value="V">V (Verdacht auf)</option><option value="A">A (Ausschluss)</option><option value="ZN">ZN (Zustand nach)</option></select></div><div className="md:col-span-1"><label htmlFor="newDiagnosisName" className="block text-xs font-medium text-gray-700">Manuelle Beschreibung (optional)</label><input type="text" id="newDiagnosisName" value={newDiagnosis} onChange={(e) => setNewDiagnosis(e.target.value)} placeholder="Eigene Beschreibung" className={`${inputStd} mt-1`} /></div><button onClick={handleAddDiagnosis} className={`${btnPrimary} bg-green-500 hover:bg-green-600 py-2 px-3 h-10 md:self-end`}><PlusCircleIcon className="w-5 h-5 mr-1" /> HinzufÃ¼gen</button></div>{lookedUpDiagnosisName && <p className="text-xs text-teal-600 mt-1">Gefunden: {lookedUpDiagnosisName}</p>}</div><ul className="space-y-1 mb-3">{(selectedPatient.diagnoses || []).map((diagString, index) => (<li key={index} className="flex justify-between items-center bg-gray-50 p-2 rounded-md"><span className="text-sm text-gray-700">{diagString}</span><div className="flex items-center space-x-2"><button onClick={() => showDiagnosisInfo(diagString)} className={`${btnInfo} p-1`} title="Info zu dieser Diagnose abrufen"><FileJson2 className="w-4 h-4 mr-1" /> Info</button><button onClick={() => handleRemoveDiagnosis(diagString)} className={`${btnDanger} p-1 text-xs`}><Trash2 className="w-4 h-4" /></button></div></li>))}{(selectedPatient.diagnoses || []).length === 0 && <p className="text-sm text-gray-500">Keine Diagnosen erfasst.</p>}</ul></div><div className="mb-6"><h4 className="text-xl font-semibold text-gray-700 mb-3">ICD-11 Diagnosen</h4><ul className="space-y-1">{(selectedPatient.diagnoses_icd11 || []).map((diag, index) => (<li key={index} className="flex justify-between items-center bg-purple-50 p-2 rounded-md hover:bg-purple-100 cursor-pointer" onClick={() => fetchIcd11Details(diag)}><span className="text-sm text-purple-800"><strong className="font-mono">{diag.code}</strong> - {diag.title}</span><button onClick={(e) => { e.stopPropagation(); handleRemoveIcd11Diagnosis(diag); }} className="text-red-500 hover:text-red-700 p-1 text-xs" title="ICD-11 Diagnose entfernen"><Trash2 className="w-4 h-4" /></button></li>))}{(selectedPatient.diagnoses_icd11 || []).length === 0 && <p className="text-sm text-gray-500">Keine ICD-11 Diagnosen erfasst.</p>}</ul></div></div>)}
                                    {activeDetailTab === 'abrechnung' && (<AbrechnungsUebersicht patient={selectedPatient} ebmData={ebmData} onEditSession={handleEditSession} onDeleteSession={handleDeleteSession} />)}
                                    {activeDetailTab === 'krankheitsmodell' && (<div className="space-y-6"><div><label htmlFor="krankheitsanamnese" className="block text-lg font-semibold text-gray-700 mb-1">Krankheitsanamnese</label><textarea id="krankheitsanamnese" name="krankheitsanamnese" rows="6" className={textareaStd} value={editableKrankheitsanamnese} onChange={(e) => setEditableKrankheitsanamnese(e.target.value)} onBlur={(e) => handleKrankheitsmodellChange('krankheitsanamnese', e.target.value)} placeholder="Hier die Krankheitsanamnese eintragen..."></textarea></div><div><label htmlFor="lebensgeschichte" className="block text-lg font-semibold text-gray-700 mb-1">Lebensgeschichte / Biographie</label><textarea id="lebensgeschichte" name="lebensgeschichte" rows="6" className={textareaStd} value={editableLebensgeschichte} onChange={(e) => setEditableLebensgeschichte(e.target.value)} onBlur={(e) => handleKrankheitsmodellChange('lebensgeschichte', e.target.value)} placeholder="Wichtige Aspekte der Lebensgeschichte..."></textarea></div><div><label htmlFor="problemanalyseMakro" className="block text-lg font-semibold text-gray-700 mb-1">Problemanalyse auf Makroebene</label><textarea id="problemanalyseMakro" name="problemanalyseMakro" rows="6" className={textareaStd} value={editableProblemanalyseMakro} onChange={(e) => setEditableProblemanalyseMakro(e.target.value)} onBlur={(e) => handleKrankheitsmodellChange('problemanalyseMakro', e.target.value)} placeholder="Analyse der Ã¼bergeordneten Problembereiche..."></textarea></div><div><label htmlFor="therapieplan" className="block text-lg font-semibold text-gray-700 mb-1">Therapieplan</label><textarea id="therapieplan" name="therapieplan" rows="6" className={textareaStd} value={editableTherapieplan} onChange={(e) => setEditableTherapieplan(e.target.value)} onBlur={(e) => handleKrankheitsmodellChange('therapieplan', e.target.value)} placeholder="Therapieziele und -maÃŸnahmen..."></textarea></div></div>)}
                                    {activeDetailTab === 'diagnostik' && (<div><div className="border-b border-gray-200 mb-4"><nav className="-mb-px flex space-x-6" aria-label="Diagnostik Sub-Tabs"><button onClick={() => setActiveDiagnostikSubTab('overview')} className={`whitespace-nowrap pb-2 px-1 border-b-2 font-medium text-sm ${activeDiagnostikSubTab === 'overview' ? 'border-purple-500 text-purple-600' : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'}`}>Ãœbersicht</button>{availableTests.map(test => ((selectedPatient[`${test.id}Results`]?.length > 0) && <button key={test.id} onClick={() => setActiveDiagnostikSubTab(test.id)} className={`whitespace-nowrap pb-2 px-1 border-b-2 font-medium text-sm ${activeDiagnostikSubTab === test.id ? 'border-purple-500 text-purple-600' : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'}`}>{test.id.toUpperCase()}</button>))}</nav></div>
                                        {activeDiagnostikSubTab === 'overview' && (<DiagnostikUebersicht patient={selectedPatient} availableTests={availableTests} onAddTest={(test) => { setCurrentTestToAdd(test); setIsAddTestModalOpen(true); }} onShowTestDetails={(testId) => setActiveDiagnostikSubTab(testId)} onStartRetroBilling={handleStartRetroBilling} diagnostikAnalysis={diagnostikAnalysis} />)}
                                        {activeDiagnostikSubTab === 'scl90r' && <SCL90RComponent patient={selectedPatient} onAddResult={handleAddScl90rResult} onDeleteResult={handleRemoveScl90rResult} />}
                                        {activeDiagnostikSubTab === 'fep2' && <FEP2Component patient={selectedPatient} onAddResult={handleAddFepResult} onDeleteResult={handleRemoveFepResult} />}
                                        {activeDiagnostikSubTab === 'bdi2' && <BDI2Component patient={selectedPatient} onAddResult={handleAddBdiResult} onDeleteResult={handleRemoveBdiResult} bdiCutoffs={bdiCutoffs} bdiAxisTicks={bdiAxisTicks} CustomBdiLabel={CustomBdiLabel} />}
                                        {activeDiagnostikSubTab === 'hscl11' && <HSCL11Component patient={selectedPatient} onAddResult={handleAddHsclResult} onDeleteResult={handleRemoveHsclResult} />}</div>)}
                                    {activeDetailTab === 'assistenz' && (<div><h3 className="text-xl font-semibold text-gray-700 mb-4">Therapeutische Assistenz</h3><button onClick={handleTherapeutischeAssistenzAnfordern} disabled={isLoadingTherapeutischeAssistenz} className={`${btnPrimary} bg-purple-600 hover:bg-purple-700 mb-4 flex items-center`}><WandSparkles className="mr-2 h-5 w-5" />{isLoadingTherapeutischeAssistenz ? "Analysiere Daten..." : "Expertenfeedback anfordern"}</button>{isLoadingTherapeutischeAssistenz && (<div className="text-center py-10"><div className="animate-spin rounded-full h-12 w-12 border-b-2 border-purple-700 mx-auto"></div><p className="mt-3 text-gray-600">Bitte warten, die KI analysiert die Patientendaten...</p></div>)}{assistenzError && (<div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4" role="alert"><p className="font-bold">Fehler bei der Assistenz</p><p>{assistenzError}</p></div>)}{therapeutischeAssistenzOutput && !isLoadingTherapeutischeAssistenz && (<div className="mt-4 p-4 bg-gray-50 rounded-md border"><h4 className="text-lg font-semibold text-gray-800 mb-2">VorschlÃ¤ge des Expertenteams:</h4><div className="whitespace-pre-wrap text-sm text-gray-700 leading-relaxed">{therapeutischeAssistenzOutput}</div></div>)}</div>)}
                                </div>
                            )}
                        </div>
                    )}
                    {mainView === 'kalender' && (
                        <div>
                            <h3 className="text-2xl font-semibold text-gray-800 mb-4">Terminkalender</h3>
                            <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-4 relative min-h-[600px]">
                                {isCalendarLoading && (
                                    <div className="absolute inset-0 flex flex-col items-center justify-center bg-slate-50 z-10">
                                        <dotlottie-player
                                            src="https://lottie.host/5a99e4b7-42e6-47ae-9706-1e931f930492/HSS5faWD7S.lottie"
                                            background="transparent"
                                            speed="2"
                                            style="width: 300px; height: 300px"
                                            loop
                                            autoplay
                                        ></dotlottie-player>
                                        <p className="text-gray-500 -mt-8">Kalender wird geladen...</p>
                                    </div>
                                )}
                                <iframe
                                    title="Google Calendar"
                                    src="https://calendar.google.com/calendar/embed?src=julisycomorepsy%40gmail.com&ctz=Europe%2FBerlin"
                                    style={{ border: 0 }}
                                    width="100%"
                                    height="600"
                                    frameBorder="0"
                                    scrolling="no"
                                    onLoad={() => setIsCalendarLoading(false)}
                                    className={`transition-opacity duration-500 ${isCalendarLoading ? 'opacity-0' : 'opacity-100'}`}
                                ></iframe>
                            </div>
                        </div>
                    )}
                    {mainView === 'formulare' && (<div><h3 className="text-2xl font-semibold text-gray-800 mb-4">Formular- & Dokumentenverwaltung</h3><div className="bg-white p-6 border border-gray-200 rounded-lg shadow-sm"><label htmlFor="file-upload" className="mb-4 block w-full cursor-pointer"><div className="border-2 border-dashed border-gray-300 rounded-lg p-8 text-center hover:border-teal-400 transition-colors"><UploadCloudIcon className="mx-auto h-12 w-12 text-gray-400" /><span className="mt-2 block text-sm font-medium text-gray-900">Dateien zum Hochladen auswÃ¤hlen</span><span className="mt-1 block text-xs text-gray-500">PDF, DOCX, etc.</span></div><input id="file-upload" name="file-upload" type="file" className="sr-only" multiple onChange={handleFileUpload} /></label>{uploadedFiles.length > 0 && (<div><h4 className="text-lg font-medium text-gray-900 mb-2">Hochgeladene Dateien</h4><ul className="divide-y divide-gray-200 border rounded-lg">{uploadedFiles.map((file, index) => (<li key={index} className="flex items-center justify-between p-3 hover:bg-gray-50"><div className="flex items-center"><FileIcon className="h-6 w-6 text-gray-400 mr-3" /><div><p className="text-sm font-medium text-gray-900">{file.name}</p><p className="text-xs text-gray-500">{(file.size / 1024).toFixed(2)} KB - Zuletzt geÃ¤ndert: {file.lastModified}</p></div></div><button onClick={() => handleRemoveFile(file.name)} className="text-gray-400 hover:text-red-600"><Trash2 className="h-5 w-5" /></button></li>))}</ul></div>)}</div></div>)}
                    {mainView === 'ebm' && (<EBMBrowser />)}
                    {mainView === 'icd11' && (<ICD11ECTBrowser patients={patients} selectedPatient={selectedPatient} onAssignDiagnosis={handleAssignIcd11Diagnosis} />)}
                </main>
            </div>
            {/* MODALS AND GLOBAL ELEMENTS */}
            <Modal isOpen={diagnosisInfo.isOpen} onClose={() => setDiagnosisInfo(prev => ({ ...prev, isOpen: false }))} title={`Diagnose-Info: ${diagnosisInfo.code || ''}`} size="3xl">{diagnosisInfo.isLoading && (<p className="text-center py-4">Informationen werden geladen...</p>)}{diagnosisInfo.error && (<p className="text-red-600 bg-red-100 p-3 rounded-md">Fehler: {diagnosisInfo.error}</p>)}{!diagnosisInfo.isLoading && diagnosisInfo.source === 'local' && (<div className="text-sm text-gray-700 leading-relaxed p-2 space-y-3"><h3 className="text-lg font-semibold text-teal-700">{diagnosisInfo.name}<span className="text-gray-500 text-base ml-2">({diagnosisInfo.code})</span></h3>{diagnosisInfo.beschreibung && (<div><strong className="font-medium text-gray-800 block mb-1">Beschreibung:</strong><p className="whitespace-pre-wrap">{diagnosisInfo.beschreibung}</p></div>)}{diagnosisInfo.inkl?.length > 0 && (<div><strong className="font-medium text-gray-800 block mb-1">Inklusive:</strong><ul className="list-disc list-inside">{diagnosisInfo.inkl.map((item, idx) => (<li key={idx}>{item}</li>))}</ul></div>)}{diagnosisInfo.exkl?.length > 0 && (<div><strong className="font-medium text-gray-800 block mb-1">Exklusive:</strong><ul className="list-disc list-inside">{diagnosisInfo.exkl.map((item, idx) => (<li key={idx}>{item}</li>))}</ul></div>)}</div>)}{!diagnosisInfo.isLoading && diagnosisInfo.source === 'api_detail' && (<div className="text-sm text-gray-700 leading-relaxed p-2 space-y-4"><h3 className="text-lg font-semibold text-purple-700">{diagnosisInfo.name}<span className="text-gray-500 text-base ml-2 font-mono">({diagnosisInfo.code})</span></h3>{diagnosisInfo.beschreibung && (<div><strong className="font-medium text-gray-800 block mb-1">Beschreibung (Definition):</strong><p className="whitespace-pre-wrap bg-gray-50 p-2 rounded-md">{diagnosisInfo.beschreibung}</p></div>)}{diagnosisInfo.inclusions?.length > 0 && (<div><strong className="font-medium text-gray-800 block mb-1">Inklusive:</strong><ul className="list-disc list-inside pl-2 space-y-1">{diagnosisInfo.inclusions.map((item, idx) => (<li key={idx}>{item.label['@value']}</li>))}</ul></div>)}{diagnosisInfo.exclusions?.length > 0 && (<div><strong className="font-medium text-gray-800 block mb-1">Exklusive:</strong><ul className="list-disc list-inside pl-2 space-y-1">{diagnosisInfo.exclusions.map((item, idx) => (<li key={idx}>{item.label['@value']}{item.foundationReference && <span className="text-xs text-gray-400 ml-2">({item.foundationReference.split('/').pop()})</span>}</li>))}</ul></div>)}</div>)}</Modal>
            <Modal isOpen={improvementState.isOpen} onClose={() => setImprovementState({ ...improvementState, isOpen: false })} title="Sitzungsdokumentation verbessern" size="5xl">{improvementState.error && <p className="text-red-500 bg-red-100 p-3 rounded-md mb-4">{improvementState.error}</p>}<div className="grid grid-cols-1 md:grid-cols-2 gap-6"><div><h4 className="font-semibold text-lg text-gray-700 mb-2">Original</h4><pre className="text-sm bg-gray-100 p-3 rounded-md whitespace-pre-wrap font-sans h-96 overflow-y-auto border">{improvementState.originalText}</pre></div><div><h4 className="font-semibold text-lg text-gray-700 mb-2">Vorschlag</h4><pre className="text-sm bg-gray-800 text-green-300 font-mono p-3 rounded-md whitespace-pre-wrap h-96 overflow-y-auto">{improvementState.improvedText}</pre></div></div><div className="flex justify-end space-x-4 mt-6"><button type="button" onClick={() => setImprovementState({ ...improvementState, isOpen: false })} className="px-4 py-2 text-sm font-medium text-gray-700 bg-gray-200 rounded-md hover:bg-gray-300">Abbrechen</button><button onClick={handleAcceptImprovement} className={`${btnPrimary} flex items-center`}><Save className="w-4 h-4 mr-2" /> Original ersetzen</button></div></Modal>
            {appError && (<div className="fixed bottom-4 right-4 p-4 max-w-sm text-sm text-red-700 bg-red-100 border border-red-400 rounded-md shadow-lg z-[100]">{appError}<button onClick={() => setAppError(null)} className="ml-4 mt-2 sm:mt-0 sm:ml-2 px-2 py-1 text-red-900 underline rounded-md hover:bg-red-200 text-xs">Ok</button></div>)}
            <AddTestFlowModal isOpen={isAddTestModalOpen} onClose={() => setIsAddTestModalOpen(false)} onSave={handleSaveTestAndResults} testInfo={currentTestToAdd} patient={selectedPatient} />
            <ManualBillingSetupModal isOpen={isManualBillingSetupOpen} onClose={() => setIsManualBillingSetupOpen(false)} patients={sortedPatients} onStart={handleStartManualBilling} />
            <BillTestModal isOpen={retroBillingState.isOpen} onClose={() => setRetroBillingState({ isOpen: false, test: null })} onSave={handleSaveRetroactiveBilling} testToBill={retroBillingState.test} patientSessions={selectedPatient?.sessions || []} />
            {billingModalState.isOpen && (<Modal isOpen={billingModalState.isOpen} onClose={() => setBillingModalState({ isOpen: false, event: null, isEditing: false })} title={billingModalState.isEditing ? "Sitzung bearbeiten" : `Termin abrechnen: ${billingModalState.event.summary}`} size="4xl"><BillingModal event={billingModalState.event} ebmData={ebmData} onSave={(data) => handleSaveBilling(data, billingModalState.isEditing)} onClose={() => setBillingModalState({ isOpen: false, event: null, isEditing: false })} generateGeminiText={generateGeminiText} isEditing={billingModalState.isEditing} /></Modal>)}
        </div>
    );
}

const CustomBdiLabel = (props) => { const { x, y, width, text } = props; if (typeof x !== 'number' || isNaN(x) || typeof y !== 'number' || isNaN(y) || typeof width !== 'number' || isNaN(width) || width <= 0) { return null; } const labelX = x + width / 2; return (<text x={labelX} y={y} fill="#666" textAnchor="middle" dy={-4} fontSize="10">{text}</text>); };

export default App;
