import { 
    AreaChart, Area, BarChart, Bar, ResponsiveContainer, 
    XAxis, YAxis, CartesianGrid, Tooltip, Legend, PieChart, Pie, Cell 
} from 'recharts';

// --- ICONS (Behalte deine vorhandenen Icons) ---
const DollarSignIcon = ({ className = "w-6 h-6" }) => <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><line x1="12" y1="1" x2="12" y2="23"></line><path d="M17 5H9.5a3.5 3.5 0 0 0 0 7h5a3.5 3.5 0 0 1 0 7H6"></path></svg>;
const TrendingUpIcon = ({ className = "w-5 h-5" }) => <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><polyline points="23 6 13.5 15.5 8.5 10.5 1 18"></polyline><polyline points="17 6 23 6 23 12"></polyline></svg>;
const ClockIcon = ({ className = "w-5 h-5" }) => <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><circle cx="12" cy="12" r="10"></circle><polyline points="12 6 12 12 16 14"></polyline></svg>;
const ChevronDownIcon = ({ className = "w-4 h-4" }) => <svg className={className} xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><path d="m6 9 6 6 6-6"/></svg>;

// --- Komponenten ---
const StatCard = ({ title, value, icon, subtext, colorClass }) => (
    <div className="bg-white p-6 rounded-lg shadow-sm border-2 border-gray-100 flex flex-col justify-between">
        <div className="flex justify-between items-start">
            <h3 className="text-md font-medium text-gray-600">{title}</h3>
            <div className={`p-2 rounded-md ${colorClass}`}>
                {icon}
            </div>
        </div>
        <div>
            <p className="text-3xl font-bold text-gray-800 mt-2">{value}</p>
            <p className="text-sm text-gray-400">{subtext}</p>
        </div>
    </div>
);

const CustomTooltip = ({ active, payload, label }) => {
    if (active && payload && payload.length) {
        return (
            <div className="bg-white p-3 border border-gray-200 rounded-md shadow-sm">
                <p className="text-sm font-semibold text-gray-800">{label}</p>
                <p className="text-sm text-blue-600">{`Umsatz: â‚¬${payload[0].value.toFixed(2)}`}</p>
                <p className="text-sm text-rose-600">{`Sitzungen: ${payload[1].value}`}</p>
            </div>
        );
    }
    return null;
};

const Dashboard = ({ patients, profile }) => {
    // State to control which chart type is shown
    const [chartType, setChartType] = useState('area'); 

    const dashboardData = useMemo(() => {
        const data = {
            totalRevenue: 0,
            totalSessions: 0,
            totalPatients: patients.length,
            chartData: [],
            annualGoalData: [],
        };

        const punktwert = profile.punktwert || 0.116;
        let conductedSessions = 0;
        const dailyData = {};

        // Calculate totals and aggregate data by day
        patients.forEach(patient => {
            conductedSessions += Math.max(0, ...(patient.sessions || []).map(s => s.structuredNotes?.sitzungsnummer || 0));

            (patient.sessions || []).forEach(session => {
                const sessionDate = new Date(session.date).toLocaleDateString('de-DE');
                const sessionRevenue = (session.ebmEntries || [])
                    .reduce((sum, entry) => sum + (entry.punkte || 0), 0) * punktwert;

                if (!dailyData[sessionDate]) {
                    dailyData[sessionDate] = { revenue: 0, sessions: 0 };
                }
                dailyData[sessionDate].revenue += sessionRevenue;
                dailyData[sessionDate].sessions += 1;
                data.totalRevenue += sessionRevenue;
            });
        });

        data.totalSessions = conductedSessions;

        // Format data for the chart (last 30 days)
        const sortedDates = Object.keys(dailyData).sort((a, b) => new Date(a.split('.').reverse().join('-')) - new Date(b.split('.').reverse().join('-')));
        const last30Days = sortedDates.slice(-30);
        data.chartData = last30Days.map(date => ({
            date,
            Umsatz: dailyData[date].revenue,
            Sitzungen: dailyData[date].sessions,
        }));

        // Data for the annual goal pie chart
        const YEAR_GOAL = 600;
        data.annualGoalData = [
            { name: 'Erreicht', value: data.totalSessions },
            { name: 'Offen', value: Math.max(0, YEAR_GOAL - data.totalSessions) }
        ];

        return data;
    }, [patients, profile]);

    const PIE_COLORS = ['#3b82f6', '#e5e7eb']; // Blue for completed, Gray for remaining
    const getGreeting = () => {
        const hour = new Date().getHours();
        if (hour < 12) return "Guten Morgen";
        if (hour < 18) return "Guten Tag";
        return "Guten Abend";
    };

    return (
        <div className="p-4 md:p-6 lg:p-8 bg-slate-50 min-h-full font-sans">
            {/* Header */}
            <header className="bg-blue-600 text-white p-6 rounded-lg -mx-4 -mt-4 md:-mx-6 md:-mt-6 lg:-mx-8 lg:-mt-8 mb-6">
                <h1 className="text-3xl font-bold">Willkommen zurÃ¼ck, {profile.vorname || 'Therapeut/in'}! ðŸ‘‹</h1>
                <p className="text-blue-200 mt-1">Hier ist dein finanzieller Ãœberblick.</p>
                <div className="mt-4 flex space-x-2">
                    {/* Mock Filter Buttons */}
                    <button className="flex items-center bg-white/20 hover:bg-white/30 text-white text-sm font-medium py-1.5 px-3 rounded-md">
                        Letzte 30 Tage <ChevronDownIcon className="ml-2"/>
                    </button>
                </div>
            </header>

            {/* Stat Cards */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mb-6">
                <StatCard 
                    title="Gesamtumsatz (letzte 30T)" 
                    value={`â‚¬ ${dashboardData.chartData.reduce((sum, d) => sum + d.Umsatz, 0).toFixed(2)}`}
                    subtext="Umsatz in der Periode"
                    icon={<TrendingUpIcon className="text-green-600"/>}
                    colorClass="bg-green-100"
                />
                <StatCard 
                    title="Sitzungen (letzte 30T)" 
                    value={dashboardData.chartData.reduce((sum, d) => sum + d.Sitzungen, 0)}
                    subtext="Anzahl der Sitzungen"
                    icon={<ClockIcon className="text-blue-600"/>}
                    colorClass="bg-blue-100"
                />
                 <StatCard 
                    title="Durchschnitt / Sitzung" 
                    value={`â‚¬ ${(dashboardData.chartData.reduce((sum, d) => sum + d.Umsatz, 0) / dashboardData.chartData.reduce((sum, d) => sum + d.Sitzungen, 1)).toFixed(2)}`}
                    subtext="Durchschnittlicher Umsatz"
                    icon={<DollarSignIcon className="text-indigo-600"/>}
                    colorClass="bg-indigo-100"
                />
            </div>

            {/* Main Content Area */}
            <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
                {/* Main Chart Card */}
                <div className="lg:col-span-2 bg-white p-6 rounded-lg shadow-sm border-2 border-gray-100">
                    <div className="flex justify-between items-center mb-4">
                        <h2 className="text-lg font-semibold text-gray-700">Analyse</h2>
                        <div className="flex border border-gray-200 rounded-md">
                            <button onClick={() => setChartType('area')} className={`px-3 py-1 text-sm rounded-l-md ${chartType === 'area' ? 'bg-gray-200 font-semibold' : 'bg-white'}`}>FlÃ¤che</button>
                            <button onClick={() => setChartType('bar')} className={`px-3 py-1 text-sm rounded-r-md border-l ${chartType === 'bar' ? 'bg-gray-200 font-semibold' : 'bg-white'}`}>Balken</button>
                        </div>
                    </div>
                    <ResponsiveContainer width="100%" height={300}>
                        {chartType === 'area' ? (
                            <AreaChart data={dashboardData.chartData}>
                                <CartesianGrid strokeDasharray="3 3" vertical={false}/>
                                <XAxis dataKey="date" tick={{ fontSize: 12 }}/>
                                <YAxis yAxisId="left" orientation="left" stroke="#3b82f6" tick={{ fontSize: 12 }} tickFormatter={(value) => `â‚¬${value}`}/>
                                <YAxis yAxisId="right" orientation="right" stroke="#f43f5e" tick={{ fontSize: 12 }}/>
                                <Tooltip content={<CustomTooltip />}/>
                                <Legend />
                                <Area yAxisId="left" type="monotone" dataKey="Umsatz" stroke="#3b82f6" fill="#bfdbfe" />
                                <Area yAxisId="right" type="monotone" dataKey="Sitzungen" stroke="#f43f5e" fill="#ffe4e6" />
                            </AreaChart>
                        ) : (
                            <BarChart data={dashboardData.chartData}>
                                <CartesianGrid strokeDasharray="3 3" vertical={false}/>
                                <XAxis dataKey="date" tick={{ fontSize: 12 }}/>
                                <YAxis yAxisId="left" orientation="left" stroke="#3b82f6" tick={{ fontSize: 12 }} tickFormatter={(value) => `â‚¬${value}`}/>
                                <YAxis yAxisId="right" orientation="right" stroke="#f43f5e" tick={{ fontSize: 12 }}/>
                                <Tooltip content={<CustomTooltip />}/>
                                <Legend />
                                <Bar yAxisId="left" dataKey="Umsatz" fill="#3b82f6" />
                                <Bar yAxisId="right" dataKey="Sitzungen" fill="#f43f5e" />
                            </BarChart>
                        )}
                    </ResponsiveContainer>
                </div>

                {/* Annual Goal Pie Chart */}
                <div className="bg-white p-6 rounded-lg shadow-sm border-2 border-gray-100">
                    <h2 className="text-lg font-semibold text-gray-700 mb-4">Jahresziel</h2>
                    <ResponsiveContainer width="100%" height={300}>
                        <PieChart>
                            <Pie
                                data={dashboardData.annualGoalData}
                                cx="50%"
                                cy="50%"
                                innerRadius={80}
                                outerRadius={110}
                                paddingAngle={2}
                                dataKey="value"
                            >
                                {dashboardData.annualGoalData.map((entry, index) => (
                                    <Cell key={`cell-${index}`} fill={PIE_COLORS[index % PIE_COLORS.length]} stroke={PIE_COLORS[index % PIE_COLORS.length]}/>
                                ))}
                            </Pie>
                            <Tooltip formatter={(value, name) => [`${value} Stunden`, name]}/>
                            <Legend />
                        </PieChart>
                    </ResponsiveContainer>
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
