// src/components/ICD11ECTBrowser.js
import React, { useEffect, useRef, useState } from 'react';

// Da die Bibliothek jetzt global in index.html geladen wird,
// greifen wir über das window-Objekt darauf zu.
const ECT = window.ECT;

const ICD11ECTBrowser = () => {
    // Ein ref, um sicherzustellen, dass die Initialisierung nur einmal ausgeführt wird.
    const isInitialized = useRef(false);

    // State, um den aktiven Kapitel-Filter zu speichern
    const [activeFilter, setActiveFilter] = useState(null);

    useEffect(() => {
        // Überprüft, ob die Komponente bereits initialisiert wurde oder ob das ECT-Objekt noch nicht verfügbar ist
        if (isInitialized.current || !ECT) {
            return;
        }
        isInitialized.current = true;

        // Definition der Grundeinstellungen für den Embedded Browser
const mySettings = {
    apiServerUrl: "https://id.who.int/icd/release/11/2024-01/mms",
    apiSecured: true,
    language: "de",
    linearizationName: "mms",
    releaseId: "2024-01",
    browserHierarchyAvailable: true,
    browserAdvancedSearchAvailable: true,
    includeDiagnosticCriteria: true,
    hierarchyResizable: true,
    height: "75vh"
};

        const myCallbacks = {
            getNewTokenFunction: async () => {
                try {
                    const response = await fetch('http://localhost:3001/api/icd11-token');
                    if (!response.ok) {
                        console.error('Fehler beim Abrufen des Tokens vom Backend.');
                        throw new Error('Could not fetch token');
                    }
                    const result = await response.json();
                    return result.token; 
                } catch (e) {
                    console.error("Fehler bei der Token-Anfrage:", e);
                    return null;
                }
            }
        };

        // Konfiguriert und initialisiert den Browser. Da `autoBind` standardmäßig `true` ist,
        // sucht die Bibliothek nach dem Rendern automatisch nach dem Anker-div.
        ECT.Handler.configure(mySettings, myCallbacks);

    }, []); 

    // Filter-Handler, der die Konfiguration überschreibt.
    const handleFilterChange = (newFilter) => {
        if (isInitialized.current && ECT) {
            setActiveFilter(newFilter);
            ECT.Handler.overwriteConfiguration("1", { // "1" ist die ID unseres Browsers
                chaptersFilter: newFilter || "" // Leerer String setzt den Filter zurück
            });
        }
    };

    const filterButtons = [
        { label: "Alle Kapitel", filter: null },
        { label: "Psychische Störungen (06)", filter: "06" },
        { label: "Neurolog. Störungen (08)", filter: "08" },
        { label: "Schlaf-Wach-Störungen (07)", filter: "07"},
    ];

    return (
        <div className="p-4 bg-white rounded-lg shadow-lg">
             <h2 className="text-2xl font-semibold text-gray-800 mb-4">ICD-11 Nachschlagewerk</h2>
            <div className="flex flex-wrap gap-2 mb-4">
                <span className="text-sm font-semibold self-center text-gray-600">Schnellfilter:</span>
                {filterButtons.map(({label, filter}) => (
                     <button 
                        key={label}
                        onClick={() => handleFilterChange(filter)}
                        className={`px-3 py-1 text-sm rounded-full transition-colors ${
                            activeFilter === filter 
                            ? 'bg-indigo-600 text-white shadow' 
                            : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                        }`}
                     >
                        {label}
                     </button>
                ))}
            </div>

            {/* Wichtig: Dies ist der Ankerpunkt für den Embedded BROWSER */}
            <div className="ctw-eb-window" data-ctw-ino="1"></div>
        </div>
    );
};

export default ICD11ECTBrowser;
