/* global __GEMINI_API_KEY__ */
import React, { useState, useEffect, useCallback, useRef } from 'react';
import { initializeApp } from 'firebase/app';
import {
    getAuth,
    onAuthStateChanged,
    createUserWithEmailAndPassword,
    signInWithEmailAndPassword,
    signOut
} from 'firebase/auth';
import { getFirestore, collection, doc, addDoc, getDocs, setDoc, deleteDoc, onSnapshot, query, writeBatch } from 'firebase/firestore';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, ReferenceArea } from 'recharts';
import SCL90RComponent from './components/SCL90R.js';
import FEP2Component from './components/FEP2.js';
import BDI2Component from './components/BDI2.js';
import HSCL11Component from './components/HSCL11.js';
import ICD11ECTBrowser from './components/ICD11ECTBrowser.js';
import EBMBrowser from './components/EBMBrowser.js';
import BillingModal from './components/BillingModal.js';


// --- ICONS ---
const UserPlus = ({ className = "w-6 h-6" }) => (<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="M16 21v-2a4 4 0 0 0-4-4H6a4 4 0 0 0-4 4v2" /><circle cx="9" cy="7" r="4" /><line x1="19" x2="19" y1="8" y2="14" /><line x1="22" x2="16" y1="11" y2="11" /></svg>);
const Edit3 = ({ className = "w-6 h-6" }) => (<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="M12 20h9" /><path d="M16.5 3.5a2.121 2.121 0 0 1 3 3L7 19l-4 1 1-4L16.5 3.5z" /></svg>);
const Trash2 = ({ className = "w-6 h-6" }) => (<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><polyline points="3 6 5 6 21 6" /><path d="M19 6v14a2 2 0 0 1-2 2H7a2 2 0 0 1-2-2V6m3 0V4a2 2 0 0 1 2-2h4a2 2 0 0 1 2 2v2" /><line x1="10" y1="11" x2="10" y2="17" /><line x1="14" y1="11" x2="14" y2="17" /></svg>);
const PlusCircleIcon = ({ className = "w-6 h-6" }) => (<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><circle cx="12" cy="12" r="10" /><line x1="12" y1="8" x2="12" y2="16" /><line x1="8" y1="12" x2="16" y2="12" /></svg>);
const XCircle = ({ className = "w-6 h-6" }) => (<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><circle cx="12" cy="12" r="10"></circle><line x1="15" y1="9" x2="9" y2="15"></line><line x1="9" y1="9" x2="15" y2="15"></line></svg>);
const Save = ({ className = "w-6 h-6" }) => (<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="M19 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h11l5 5v11a2 2 0 0 1-2 2z" /><polyline points="17 21 17 13 7 13 7 21" /><polyline points="7 3 7 8 15 8" /></svg>);
const AlertTriangle = ({ className = "w-6 h-6" }) => (<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="M10.29 3.86L1.82 18a2 2 0 0 0 1.71 3h16.94a2 2 0 0 0 1.71-3L13.71 3.86a2 2 0 0 0-3.42 0z"></path><line x1="12" y1="9" x2="12" y2="13"></line><line x1="12" y1="17" x2="12.01" y2="17"></line></svg>);
const InfoIcon = ({ className = "w-5 h-5" }) => (<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><circle cx="12" cy="12" r="10"></circle><line x1="12" y1="16" x2="12" y2="12"></line><line x1="12" y1="8" x2="12.01" y2="8"></line></svg>);
const FileJson2 = ({ className = "w-5 h-5" }) => (<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="M4 22h14a2 2 0 0 0 2-2V7.5L14.5 2H6a2 2 0 0 0-2 2v4" /><path d="M14 2v6h6" /><path d="M4 12h1.6a2 2 0 0 0 1.9-3.4L6 6" /><path d="M4 18h1.6a2 2 0 0 1 1.9 3.4L6 24" /><path d="M12 12h1.6a2 2 0 0 1 1.9-3.4L14 6" /><path d="M12 18h1.6a2 2 0 0 0 1.9 3.4L14 24" /></svg>);
const WandSparkles = ({ className = "w-5 h-5" }) => (<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="m9.5 2.55 1.294 1.294a1 1 0 0 0 1.414 0L13.5 2.55" /><path d="M4.94 4.94 6.22 6.22a1 1 0 0 0 1.41 0l1.29-1.29" /><path d="M2.55 9.51 3.84 10.8a1 1 0 0 0 1.41 0l1.3-1.29" /><path d="m19.07 4.93-1.29 1.29a1 1 0 0 1-1.41 0l-1.29-1.29" /><path d="M21.45 9.51l-1.29 1.29a1 1 0 0 1-1.41 0l-1.3-1.29" /><path d="M14.5 12.94a1 1 0 0 0 0 1.41l1.29 1.29" /><path d="M10.8 17.78a1 1 0 0 0 0 1.41l1.29 1.29" /><path d="M17.78 10.8a1 1 0 0 0 0 1.41l1.29 1.29" /><path d="M12 10.5V4.5" /><path d="M12 10.5C7 11 4 15 4 20" /><path d="M12 10.5c5 .5 8 4.5 8 9.5" /></svg>);
const LogOut = ({ className = "w-6 h-6" }) => (<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="M9 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h4" /><polyline points="16 17 21 12 16 7" /><line x1="21" y1="12" x2="9" y2="12" /></svg>);
const ClipboardIcon = ({ className = "w-4 h-4" }) => (<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><rect x="9" y="9" width="13" height="13" rx="2" ry="2"></rect><path d="M5 15H4a2 2 0 0 1-2-2V4a2 2 0 0 1 2-2h9a2 2 0 0 1 2 2v1"></path></svg>);
const CheckIcon = ({ className = "w-4 h-4" }) => (<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="3" strokeLinecap="round" strokeLinejoin="round" className={className}><polyline points="20 6 9 17 4 12"></polyline></svg>);
const ChevronRightIcon = ({ className = "w-4 h-4" }) => (<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><polyline points="9 18 15 12 9 6"></polyline></svg>);

// Ihre Firebase Konfiguration:
const firebaseConfig = {
    apiKey: "AIzaSyDZ_qqqKW7WDCZ9botek2VBwXNMQO3JYwU",
    authDomain: "psychotherapie0512.firebaseapp.com",
    projectId: "psychotherapie0512",
    storageBucket: "psychotherapie0512.firebasestorage.app",
    messagingSenderId: "131279046361",
    appId: "1:131279046361:web:e5dd2d18b206fb820b60c8"
};

const app = initializeApp(firebaseConfig);
const auth = getAuth(app);
const db = getFirestore(app);
const firestoreAppId = 'default-patient-app-v5';


const initialPatientsData = [
    {
        name: "Mustermann",
        geburtsdatum: "1998-04-04",
        chiffre: "M040498",
        therapyType: "LZT",
        approvedHours: 60,
        conductedHours: 24,
        diagnoses: ["[G] F00.0*: Demenz bei Alzheimer-Krankheit, mit frühem Beginn (Typ 2)", "[V] F01.1: Multiinfarkt-Demenz", "[A] F20.0: Paranoide Schizophrenie", "[ZN] F32.1: Mittelgradige depressive Episode", "F40.0: Agoraphobie"],
        sessions: [{ date: "2025-05-20", notes: "Erste Sitzung, Exploration der Hauptbeschwerden." }, { date: "2025-05-27", notes: "Vertiefung der Anamnese, erste Verhaltensanalysen." }],
        hscl11Results: [],
        bdi2Results: [],
        fep2Results: [],
        scl90rResults: [],
        krankheitsanamnese: "Patient berichtet über seit ca. 2 Jahren zunehmende Vergesslichkeit und Orientierungsprobleme.",
        lebensgeschichte: "Geboren 1955, verheiratet, 2 Kinder, früher als Buchhalter tätig.",
        problemanalyseMakro: "Kognitive Defizite im Vordergrund, affektive Belastung durch erlebten Kontrollverlust.",
        therapieplan: "Supportive Gespräche, kognitives Training, Psychoedukation für Angehörige."
    },
];

const Modal = ({ isOpen, onClose, title, children, size = "lg" }) => { if (!isOpen) return null; const sizeClasses = { sm: 'max-w-sm', md: 'max-w-md', lg: 'max-w-lg', xl: 'max-w-xl', '2xl': 'max-w-2xl', '3xl': 'max-w-3xl', '4xl': 'max-w-4xl', '5xl': 'max-w-5xl' }; return (<div className="fixed inset-0 bg-black bg-opacity-60 backdrop-blur-sm flex justify-center items-center z-50 p-4"><div className={`bg-white p-6 rounded-lg shadow-xl w-full ${sizeClasses[size]} max-h-[90vh] overflow-y-auto`}><div className="flex justify-between items-center mb-4"><h2 className="text-2xl font-semibold text-gray-800">{title}</h2><button onClick={onClose} className="text-gray-500 hover:text-gray-700"><XCircle className="w-7 h-7" /></button></div>{children}</div></div>); };
const ConfirmationModal = ({ isOpen, onClose, onConfirm, title, message }) => { if (!isOpen) return null; return (<Modal isOpen={isOpen} onClose={onClose} title={title} size="sm"><div className="text-center"><AlertTriangle className="mx-auto mb-4 w-12 h-12 text-red-500" /><p className="text-gray-700 mb-6">{message}</p><div className="flex justify-center space-x-4"><button onClick={onClose} className="px-4 py-2 text-sm font-medium text-gray-700 bg-gray-200 rounded-md hover:bg-gray-300 focus:outline-none">Abbrechen</button><button onClick={onConfirm} className="px-4 py-2 text-sm font-medium text-white bg-red-600 rounded-md hover:bg-red-700 focus:outline-none">Bestätigen</button></div></div></Modal>); };
const PatientForm = ({ patient, onSave, onCancel }) => {
    const [formData, setFormData] = useState(
        patient || {
            name: '',
            vorname: '',
            chiffre: '',
            geburtsdatum: '',
            adresse: '',
            email: '',
            telefon: '',
            therapyType: 'LZT',
            approvedHours: 0,
            conductedHours: 0,
            diagnoses: [],
            sessions: [],
            hscl11Results: [],
            bdi2Results: [],
            fep2Results: [],
            scl90rResults: [],
            krankheitsanamnese: "",
            lebensgeschichte: "",
            problemanalyseMakro: "",
            therapieplan: ""
        }
    );

    useEffect(() => {
        if (patient) {
            setFormData({
                ...patient,
                geburtsdatum: patient.geburtsdatum || '', // Sicherstellen, dass das Feld existiert
                hscl11Results: patient.hscl11Results || [],
                bdi2Results: patient.bdi2Results || [],
                fep2Results: patient.fep2Results || [],
                scl90rResults: patient.scl90rResults || [],
                krankheitsanamnese: patient.krankheitsanamnese || "",
                lebensgeschichte: patient.lebensgeschichte || "",
                problemanalyseMakro: patient.problemanalyseMakro || "",
                therapieplan: patient.therapieplan || ""
            });
        } else {
            setFormData({
                name: '',
                chiffre: '',
                geburtsdatum: '',
                therapyType: 'LZT',
                approvedHours: 0,
                conductedHours: 0,
                diagnoses: [],
                sessions: [],
                hscl11Results: [],
                bdi2Results: [],
                fep2Results: [],
                scl90rResults: [],
                krankheitsanamnese: "",
                lebensgeschichte: "",
                problemanalyseMakro: "",
                therapieplan: ""
            });
        }
    }, [patient]);

    useEffect(() => {
        const { name, geburtsdatum } = formData;
        if (name && geburtsdatum) {
            try {
                const firstLetter = name.charAt(0).toUpperCase();
                const [year, month, day] = geburtsdatum.split('-');

                if (year && month && day) {
                    const yy = year.slice(-2);
                    const newChiffre = `${firstLetter}${day}${month}${yy}`;
                    setFormData(prev => ({ ...prev, chiffre: newChiffre }));
                }
            } catch (e) {
                console.error("Fehler bei der Chiffre-Generierung", e);
                setFormData(prev => ({ ...prev, chiffre: '' }));
            }
        } else {
            if (formData.chiffre !== '') {
                setFormData(prev => ({ ...prev, chiffre: '' }));
            }
        }
    }, [formData]); // Add formData to the dependency array

    const handleChange = (e) => { const { name, value, type } = e.target; setFormData((prev) => ({ ...prev, [name]: type === 'number' ? parseInt(value, 10) || 0 : value })); };
    const handleSubmit = (e) => { e.preventDefault(); onSave(formData); };



    return (
        <form onSubmit={handleSubmit} className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                    <label htmlFor="vorname" className="block text-sm font-medium text-gray-700">Vorname</label>
                    <input type="text" name="vorname" id="vorname" value={formData.vorname} onChange={handleChange} required className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm" />
                </div>
                <div>
                    <label htmlFor="name" className="block text-sm font-medium text-gray-700">Nachname</label>
                    <input type="text" name="name" id="name" value={formData.name} onChange={handleChange} required className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm" />
                </div>
                <div>
                    <label htmlFor="geburtsdatum" className="block text-sm font-medium text-gray-700">Geburtsdatum</label>
                    <input type="date" name="geburtsdatum" id="geburtsdatum" value={formData.geburtsdatum} onChange={handleChange} required className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm" />
                </div>
                <div>
                    <label htmlFor="chiffre" className="block text-sm font-medium text-gray-700">Chiffre</label>
                    <input type="text" name="chiffre" id="chiffre" value={formData.chiffre} readOnly className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm bg-gray-100 cursor-not-allowed sm:text-sm" />
                </div>
            </div>
            <div>
                <label htmlFor="adresse" className="block text-sm font-medium text-gray-700">Adresse</label>
                <input type="text" name="adresse" id="adresse" value={formData.adresse} onChange={handleChange} className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm" />
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                    <label htmlFor="email" className="block text-sm font-medium text-gray-700">E-Mail</label>
                    <input type="email" name="email" id="email" value={formData.email} onChange={handleChange} className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm" />
                </div>
                <div>
                    <label htmlFor="telefon" className="block text-sm font-medium text-gray-700">Telefon</label>
                    <input type="tel" name="telefon" id="telefon" value={formData.telefon} onChange={handleChange} className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm" />
                </div>
            </div>

            <div>
                <label htmlFor="therapyType" className="block text-sm font-medium text-gray-700">Therapieform</label>
                <select name="therapyType" id="therapyType" value={formData.therapyType} onChange={handleChange} className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm">
                    <option value="LZT">Langzeittherapie (LZT)</option>
                    <option value="KZT">Kurzzeittherapie (KZT)</option>
                </select>
            </div>
            <div>
                <label htmlFor="approvedHours" className="block text-sm font-medium text-gray-700">Genehmigte Stunden</label>
                <input type="number" name="approvedHours" id="approvedHours" value={formData.approvedHours} onChange={handleChange} min="0" className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm" />
            </div>
            <div>
                <label htmlFor="conductedHours" className="block text-sm font-medium text-gray-700">Durchgeführte Stunden</label>
                <input type="number" name="conductedHours" id="conductedHours" value={formData.conductedHours} onChange={handleChange} min="0" className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm" />
            </div>
            <div className="flex justify-end space-x-3 pt-4">
                <button type="button" onClick={onCancel} className="px-4 py-2 text-sm font-medium text-gray-700 bg-gray-100 rounded-md hover:bg-gray-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-500">Abbrechen</button>
                <button type="submit" className="px-4 py-2 text-sm font-medium text-white bg-indigo-600 rounded-md hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 flex items-center"><Save className="w-4 h-4 mr-2" /> Speichern</button>
            </div>
        </form>
    );
};

async function generateGeminiText(prompt) {
    let chatHistory = [{ role: "user", parts: [{ text: prompt }] }];
    const payload = { contents: chatHistory };
    const GEMINI_API_KEY = "AIzaSyCWQFze_8cKDZ6W2DTqfqIcvhcsKWfuZLw"; // Tragen Sie hier Ihren lokalen Schlüssel ein
    const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.0-flash:generateContent?key=${GEMINI_API_KEY}`;
    try {
        const response = await fetch(apiUrl, { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify(payload) });
        if (!response.ok) { const errorData = await response.json(); console.error("Gemini API error details:", errorData); const errorMessage = errorData.error?.message || `API request failed with status ${response.status}`; throw new Error(errorMessage); }
        const result = await response.json();
        if (result.candidates && result.candidates.length > 0 && result.candidates[0].content && result.candidates[0].content.parts && result.candidates[0].content.parts.length > 0) { return result.candidates[0].content.parts[0].text; }
        else if (result.candidates && result.candidates.length > 0 && result.candidates[0].finishReason === "SAFETY") { console.warn("Gemini API response blocked due to safety reasons."); return "Die Antwort wurde aufgrund von Sicherheitsrichtlinien blockiert."; }
        else { console.error("Unexpected Gemini API response structure:", result); throw new Error("Unerwartete Antwortstruktur von der API."); }
    } catch (error) { console.error("Error calling Gemini API:", error); throw error; }
}

