import React, { useState, useEffect } from 'react';

import { BDI2_EntryForm } from './BDI2.js';
import { FEP2_EntryForm } from './FEP2.js';
import { HSCL11_EntryForm } from './HSCL11.js';
import { SCL90R_EntryForm } from './SCL90R.js';

const MetadataStep = ({ initialData, onNext, onClose }) => {
    const [data, setData] = useState(initialData);

    useEffect(() => {
        setData(initialData);
    }, [initialData]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setData(prev => ({...prev, [name]: value}));
    }

    // Sicherheitsabfrage, falls data doch einmal null sein sollte
    if (!data) return null; 

    return (
        <div className="space-y-4">
            <div>
                <label className="block text-sm font-medium text-gray-700">Testdatum</label>
                <input type="date" name="date" value={data.date} onChange={handleChange} className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm"/>
            </div>
            <div>
                <label className="block text-sm font-medium text-gray-700">Multiplikator (x 5 Minuten)</label>
                <div className="flex items-center">
                    <input type="number" name="multiplier" value={data.multiplier} onChange={handleChange} min="1" className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm"/>
                    <span className="ml-3 text-sm text-gray-500 whitespace-nowrap">x vollendete fünf Minuten</span>
                </div>
            </div>
            <div>
                <label className="block text-sm font-medium text-gray-700">Testung während:</label>
                <div className="mt-2 flex items-center space-x-4">
                     <label className="flex items-center"><input type="radio" name="phase" value="Probatorik / Schluss" checked={data.phase === 'Probatorik / Schluss'} onChange={handleChange} /> <span className="ml-2">Probatorik / Schluss</span></label>
                     <label className="flex items-center"><input type="radio" name="phase" value="Kurzzeittherapie" checked={data.phase === 'Kurzzeittherapie'} onChange={handleChange} /> <span className="ml-2">Kurzzeittherapie</span></label>
                     <label className="flex items-center"><input type="radio" name="phase" value="Langzeittherapie" checked={data.phase === 'Langzeittherapie'} onChange={handleChange} /> <span className="ml-2">Langzeittherapie</span></label>
                </div>
            </div>
            <div className="flex justify-end space-x-3 mt-6">
                <button onClick={onClose} className="px-4 py-2 text-sm text-gray-700 bg-gray-200 rounded-md">Abbrechen</button>
                <button onClick={() => onNext(data)} className="px-4 py-2 text-sm text-white bg-indigo-600 rounded-md hover:bg-indigo-700">Weiter zur Eingabe</button>
            </div>
        </div>
    );
};

const AddTestFlowModal = ({ isOpen, onClose, onSave, testInfo, patient }) => {
    const [step, setStep] = useState(1);
    const [metadata, setMetadata] = useState(null);

    useEffect(() => {
        if (isOpen && testInfo) {
            setStep(1);
            const patientAge = patient?.geburtsdatum ? 
                (new Date().getFullYear() - new Date(patient.geburtsdatum).getFullYear()) : 
                '';

            setMetadata({
                date: new Date().toISOString().split('T')[0],
                multiplier: testInfo.recommendation || 1,
                phase: 'Probatorik / Schluss',
                age: patientAge,
                gender: patient?.gender || '1'
            });
        }
    }, [isOpen, testInfo, patient]);

    if (!isOpen || !testInfo) return null;

    const handleMetadataSave = (data) => {
        setMetadata(data);
        setStep(2);
    };

    const handleResultsSave = (testResults) => {
        onSave({
            ...metadata,
            testId: testInfo.id,
            results: testResults,
        });
        onClose();
    };

    const renderTestEntryForm = () => {
        switch (testInfo.id) {
            case 'bdi2':
                return <BDI2_EntryForm onSave={handleResultsSave} />;
            case 'fep2':
                return <FEP2_EntryForm onSave={handleResultsSave} />;
            case 'hscl11':
                 return <HSCL11_EntryForm onSave={handleResultsSave} />;
            case 'scl90r':
                return <SCL90R_EntryForm onSave={handleResultsSave} patientAge={metadata.age} patientGender={metadata.gender} />;
            default:
                return <p>Kein Eingabeformular für diesen Testtyp gefunden.</p>;
        }
    };

    return (
        <Modal isOpen={isOpen} onClose={onClose} title={`Test hinzufügen: ${testInfo.name}`} size={step === 2 ? '5xl' : 'lg'}>
             {step === 1 && metadata && (
                <MetadataStep 
                    initialData={metadata}
                    onNext={handleMetadataSave} 
                    onClose={onClose} 
                />
            )}

            {step === 2 && (
                <div>
                    <div className="mb-4 p-2 bg-gray-100 rounded-md text-xs text-gray-600">
                        <strong>Datum:</strong> {new Date(metadata.date).toLocaleDateString('de-DE')} | 
                        <strong> Multiplikator:</strong> {metadata.multiplier} | 
                        <strong> Phase:</strong> {metadata.phase}
                    </div>
                    {renderTestEntryForm()}
                </div>
            )}
        </Modal>
    );
};


export default AddTestFlowModal;
