import React, { useState, useMemo } from 'react';

// Icons
const SaveIcon = ({ className = "w-4 h-4" }) => <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="M19 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h11l5 5v11a2 2 0 0 1-2 2z" /><polyline points="17 21 17 13 7 13 7 21" /><polyline points="7 3 7 8 15 8" /></svg>;

// Diese Komponente rendert die spezifischen Eingabefelder für den ausgewählten Test.
const TestInputForm = ({ testId, onResultsChange }) => {
    const [results, setResults] = useState({});

    const handleChange = (e) => {
        const { name, value, type } = e.target;
        const newResults = { ...results, [name]: type === 'number' ? parseFloat(value) || 0 : value };
        setResults(newResults);
        onResultsChange(newResults);
    };

    // Stellt sicher, dass die Felder zurückgesetzt werden, wenn sich der Test ändert.
    useEffect(() => {
        setResults({});
    }, [testId]);

    const inputStyle = "mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-teal-500 sm:text-sm";

    switch (testId) {
        case 'hscl11':
            return (
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-4">
                    <div>
                        <label className="block text-sm font-medium text-gray-700">Angstwert (GSI)</label>
                        <input type="number" name="angstwert" onChange={handleChange} className={inputStyle} />
                    </div>
                    <div>
                        <label className="block text-sm font-medium text-gray-700">Depressionswert (GSI)</label>
                        <input type="number" name="depressionswert" onChange={handleChange} className={inputStyle} />
                    </div>
                </div>
            );
        case 'bdi2':
            return (
                 <div className="mt-4">
                    <label className="block text-sm font-medium text-gray-700">BDI-II Summenwert</label>
                    <input type="number" name="summenwert" onChange={handleChange} className={inputStyle} />
                </div>
            );
        // Fügen Sie hier bei Bedarf weitere Cases für andere Tests hinzu.
        // case 'scl90r':
        //     return <div>SCL-90-R Inputs...</div>;
        // case 'fep2':
        //     return <div>FEP-2 Inputs...</div>;
        default:
            return <p className="mt-4 text-sm text-gray-500">Für diesen Test ist noch kein Eingabeformular definiert.</p>;
    }
};


const ZentraleDiagnostik = ({ patients, availableTests, onSave }) => {
    const [selectedPatientId, setSelectedPatientId] = useState('');
    const [selectedTestId, setSelectedTestId] = useState('');
    const [testDate, setTestDate] = useState(new Date().toISOString().split('T')[0]);
    const [testResults, setTestResults] = useState({});

    const handleSave = () => {
        if (!selectedPatientId || !selectedTestId) {
            alert("Bitte wählen Sie einen Patienten und einen Test aus.");
            return;
        }

        const testData = {
            testId: selectedTestId,
            results: testResults,
            date: testDate
        };

        onSave(selectedPatientId, testData);

        // Formular zurücksetzen für die nächste Eingabe
        setSelectedPatientId('');
        setSelectedTestId('');
        setTestDate(new Date().toISOString().split('T')[0]);
        setTestResults({});
    };

    const inputStyle = "block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-teal-500 sm:text-sm";
    const btnPrimary = "bg-teal-600 hover:bg-teal-700 text-white font-semibold py-2 px-4 rounded-md shadow-sm flex items-center justify-center transition-colors disabled:bg-gray-400";


    return (
        <div className="max-w-4xl mx-auto">
            <h2 className="text-2xl font-semibold text-gray-800 mb-6">Zentrale Diagnostik-Erfassung</h2>
            <div className="bg-white p-6 rounded-lg shadow-md border border-gray-200 space-y-6">

                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div>
                        <label htmlFor="patient-select" className="block text-sm font-medium text-gray-700">1. Patient auswählen</label>
                        <select
                            id="patient-select"
                            value={selectedPatientId}
                            onChange={(e) => setSelectedPatientId(e.target.value)}
                            className={inputStyle}
                        >
                            <option value="" disabled>Bitte wählen...</option>
                            {patients.map(p => (
                                <option key={p.id} value={p.id}>{p.name}, {p.vorname} ({p.chiffre})</option>
                            ))}
                        </select>
                    </div>
                     <div>
                        <label htmlFor="test-select" className="block text-sm font-medium text-gray-700">2. Test auswählen</label>
                        <select
                            id="test-select"
                            value={selectedTestId}
                            onChange={(e) => setSelectedTestId(e.target.value)}
                            className={inputStyle}
                            disabled={!selectedPatientId}
                        >
                            <option value="" disabled>Bitte wählen...</option>
                            {availableTests.map(t => (
                                <option key={t.id} value={t.id}>{t.name}</option>
                            ))}
                        </select>
                    </div>
                </div>

                {selectedTestId && (
                    <div className="border-t pt-6">
                         <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                             <div>
                                <label htmlFor="test-date" className="block text-sm font-medium text-gray-700">3. Testdatum festlegen</label>
                                <input
                                    type="date"
                                    id="test-date"
                                    value={testDate}
                                    onChange={(e) => setTestDate(e.target.value)}
                                    className={inputStyle}
                                />
                            </div>
                        </div>
                        <div>
                            <label className="block text-sm font-medium text-gray-700 mt-4">4. Ergebnisse eintragen</label>
                            <TestInputForm 
                                testId={selectedTestId}
                                onResultsChange={setTestResults}
                            />
                        </div>
                    </div>
                )}

                <div className="flex justify-end pt-6 border-t">
                    <button 
                        onClick={handleSave}
                        disabled={!selectedPatientId || !selectedTestId || Object.keys(testResults).length === 0}
                        className={btnPrimary}
                    >
                        <SaveIcon className="w-4 h-4 mr-2" /> Testergebnis speichern
                    </button>
                </div>
            </div>
        </div>
    );
};

export default ZentraleDiagnostik;
