const PatientForm = ({ patient, onSave, onCancel }) => {
    const [formData, setFormData] = useState(
        patient || {
            name: '',
            vorname: '',
            chiffre: '',
            geburtsdatum: '',
            adresse: '',
            email: '',
            telefon: '',
            krankenkasse: '',
            versichertennummer: '',
            therapeuticMethods: [],
            approvedHours: 0,
            diagnoses: [],
            sessions: [],
            hscl11Results: [],
            bdi2Results: [],
            fep2Results: [],
            scl90rResults: [],
            krankheitsanamnese: "",
            lebensgeschichte: "",
            problemanalyseMakro: "",
            therapieplan: ""
        }
    );

    const [newMethod, setNewMethod] = useState("");

    useEffect(() => {
        const initialData = {
            name: '', vorname: '', chiffre: '', geburtsdatum: '', adresse: '', email: '', telefon: '',
            krankenkasse: '', versichertennummer: '', therapeuticMethods: [], approvedHours: 0,
            diagnoses: [], sessions: [], hscl11Results: [], bdi2Results: [], fep2Results: [], scl90rResults: [],
            krankheitsanamnese: "", lebensgeschichte: "", problemanalyseMakro: "", therapieplan: ""
        };

        if (patient) {
            setFormData({ ...initialData, ...patient });
        } else {
            setFormData(initialData);
        }
    }, [patient]);

    useEffect(() => {
        const { name, geburtsdatum } = formData;
        if (name && geburtsdatum) {
            try {
                const firstLetter = name.charAt(0).toUpperCase();
                const [year, month, day] = geburtsdatum.split('-');

                if (year && month && day) {
                    const yy = year.slice(-2);
                    const newChiffre = `${firstLetter}${day}${month}${yy}`;
                    setFormData(prev => ({ ...prev, chiffre: newChiffre }));
                }
            } catch (e) {
                console.error("Fehler bei der Chiffre-Generierung", e);
                setFormData(prev => ({ ...prev, chiffre: '' }));
            }
        } else {
            if (formData.chiffre !== '') {
                setFormData(prev => ({ ...prev, chiffre: '' }));
            }
        }
    }, [formData.name, formData.geburtsdatum]); 

    const handleChange = (e) => { const { name, value, type } = e.target; setFormData((prev) => ({ ...prev, [name]: type === 'number' ? parseInt(value, 10) || 0 : value })); };

    const handleAddMethod = () => {
        if (newMethod && !formData.therapeuticMethods.includes(newMethod)) {
            setFormData(prev => ({ ...prev, therapeuticMethods: [...prev.therapeuticMethods, newMethod] }));
            setNewMethod("");
        }
    };

    const handleRemoveMethod = (methodToRemove) => {
        setFormData(prev => ({ ...prev, therapeuticMethods: prev.therapeuticMethods.filter(m => m !== methodToRemove) }));
    };

    const handleSubmit = (e) => { e.preventDefault(); onSave(formData); };

    const inputStd = "mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-teal-500 focus:border-teal-500 sm:text-sm";
    const btnPrimary = "px-4 py-2 text-sm font-medium text-white bg-teal-600 rounded-md hover:bg-teal-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-teal-500 flex items-center";

    return (
        <form onSubmit={handleSubmit} className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div><label htmlFor="vorname" className="block text-sm font-medium text-gray-700">Vorname</label><input type="text" name="vorname" id="vorname" value={formData.vorname} onChange={handleChange} required className={inputStd} /></div>
                <div><label htmlFor="name" className="block text-sm font-medium text-gray-700">Nachname</label><input type="text" name="name" id="name" value={formData.name} onChange={handleChange} required className={inputStd} /></div>
                <div><label htmlFor="geburtsdatum" className="block text-sm font-medium text-gray-700">Geburtsdatum</label><input type="date" name="geburtsdatum" id="geburtsdatum" value={formData.geburtsdatum} onChange={handleChange} required className={inputStd} /></div>
                <div><label htmlFor="chiffre" className="block text-sm font-medium text-gray-700">Chiffre</label><input type="text" name="chiffre" id="chiffre" value={formData.chiffre} readOnly className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm bg-gray-100 sm:text-sm" /></div>
            </div>
            <div><label htmlFor="adresse" className="block text-sm font-medium text-gray-700">Adresse</label><input type="text" name="adresse" id="adresse" value={formData.adresse} onChange={handleChange} className={inputStd} /></div>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div><label htmlFor="email" className="block text-sm font-medium text-gray-700">E-Mail</label><input type="email" name="email" id="email" value={formData.email} onChange={handleChange} className={inputStd} /></div>
                <div><label htmlFor="telefon" className="block text-sm font-medium text-gray-700">Telefon</label><input type="tel" name="telefon" id="telefon" value={formData.telefon} onChange={handleChange} className={inputStd} /></div>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div><label htmlFor="krankenkasse" className="block text-sm font-medium text-gray-700">Krankenkasse</label><input type="text" name="krankenkasse" id="krankenkasse" value={formData.krankenkasse} onChange={handleChange} className={inputStd} /></div>
                <div><label htmlFor="versichertennummer" className="block text-sm font-medium text-gray-700">Versichertennummer</label><input type="text" name="versichertennummer" id="versichertennummer" value={formData.versichertennummer} onChange={handleChange} className={inputStd} /></div>
            </div>
             <div>
                <label className="block text-sm font-medium text-gray-700">Therapieverfahren</label>
                <div className="mt-1 flex flex-wrap gap-2">
                    {(formData.therapeuticMethods || []).map(method => (
                        <span key={method} className="flex items-center bg-teal-100 text-teal-800 text-xs font-medium px-2.5 py-0.5 rounded-full">
                            {method}
                            <button type="button" onClick={() => handleRemoveMethod(method)} className="ml-1.5 text-teal-500 hover:text-teal-700">
                                <XCircle className="h-3 w-3" />
                            </button>
                        </span>
                    ))}
                </div>
                <div className="mt-2 flex items-center gap-2">
                    <select value={newMethod} onChange={(e) => setNewMethod(e.target.value)} className={inputStd}>
                        <option value="">Verfahren auswählen...</option>
                        {availableTherapeuticMethods.filter(m => !(formData.therapeuticMethods || []).includes(m)).map(m => <option key={m} value={m}>{m}</option>)}
                    </select>
                    <button type="button" onClick={handleAddMethod} className="bg-gray-200 p-2 rounded-md hover:bg-gray-300">
                        <PlusCircleIcon className="h-5 w-5 text-gray-600"/>
                    </button>
                </div>
            </div>
            <div><label htmlFor="approvedHours" className="block text-sm font-medium text-gray-700">Genehmigte Stunden</label><input type="number" name="approvedHours" id="approvedHours" value={formData.approvedHours} onChange={handleChange} min="0" className={inputStd} /></div>
            <div className="flex justify-end space-x-3 pt-4">
                <button type="button" onClick={onCancel} className="px-4 py-2 text-sm font-medium text-gray-700 bg-gray-100 rounded-md hover:bg-gray-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-500">Abbrechen</button>
                <button type="submit" className={btnPrimary}><Save className="w-4 h-4 mr-2" /> Speichern</button>
            </div>
        </form>
    );
};

async function generateGeminiText(prompt) {
    let chatHistory = [{ role: "user", parts: [{ text: prompt }] }];
    const payload = { contents: chatHistory };
    const GEMINI_API_KEY = "AIzaSyCWQFze_8cKDZ6W2DTqfqIcvhcsKWfuZLw"; 
    const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.0-flash:generateContent?key=${GEMINI_API_KEY}`;
    try {
        const response = await fetch(apiUrl, { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify(payload) });
        if (!response.ok) { const errorData = await response.json(); console.error("Gemini API error details:", errorData); const errorMessage = errorData.error?.message || `API request failed with status ${response.status}`; throw new Error(errorMessage); }
        const result = await response.json();
        if (result.candidates && result.candidates.length > 0 && result.candidates[0].content && result.candidates[0].content.parts && result.candidates[0].content.parts.length > 0) { return result.candidates[0].content.parts[0].text; }
        else if (result.candidates && result.candidates.length > 0 && result.candidates[0].finishReason === "SAFETY") { console.warn("Gemini API response blocked due to safety reasons."); return "Die Antwort wurde aufgrund von Sicherheitsrichtlinien blockiert."; }
        else { console.error("Unexpected Gemini API response structure:", result); throw new Error("Unerwartete Antwortstruktur von der API."); }
    } catch (error) { console.error("Error calling Gemini API:", error); throw error; }
}

