            if (response.status === 404) {
                setNextAppointment({ isLoading: false, text: 'Kein zukünftiger Termin gefunden.', error: null });
                return;
            }
            if (!response.ok) {
                throw new Error('Kalender-API-Anfrage fehlgeschlagen.');
            }
            const event = await response.json();

            const startDate = new Date(event.start);
            const endDate = new Date(event.end);
            const dateOptions = { day: '2-digit', month: '2-digit', year: 'numeric' };
            const timeOptions = { hour: '2-digit', minute: '2-digit' };

            const formattedDate = startDate.toLocaleDateString('de-DE', dateOptions);
            const formattedStartTime = startDate.toLocaleTimeString('de-DE', timeOptions);
            const formattedEndTime = endDate.toLocaleTimeString('de-DE', timeOptions);

            setNextAppointment({
                isLoading: false,
                text: `Nächster Termin: ${formattedDate} um ${formattedStartTime} bis ${formattedEndTime} Uhr`,
                error: null
            });

