        const sicherheit = sicherheitMatch ? sicherheitMatch[1].toUpperCase() : '';

        const codeAndName = sicherheitMatch
            ? diagnosisString.substring(sicherheitMatch[0].length)
            : diagnosisString;

        const codeMatch = codeAndName.match(/^([A-Z][0-9]{1,2}(\.[0-9A-Z]{1,2})?[*†]?)/i);
        const code = codeMatch
            ? codeMatch[0].toUpperCase()
            : codeAndName.split(':')[0].trim().toUpperCase();

        const localData = findIcd10CodeLocal(code);

        if (localData) {
            setDiagnosisInfo({
                code: localData.code || code,
                name: localData.name || '',
                beschreibung: localData.beschreibung || localData.beschreibung_kategorie || '',
                inkl: localData.inkl || [],
                exkl: localData.exkl || [],
                hinweise: localData.hinweise || [],
                zusatz_info_code: localData.zusatz_info_code || '',
                sicherheit: sicherheit,
                isLoading: false,
                error: null,
                isOpen: true,
                source: 'local'
            });
        } else {
            setDiagnosisInfo({
                code: code,
                name: "Unbekannter ICD-10 Code",
                isLoading: false,
                error: `Für den ICD-10 Code "${code}" wurden keine Informationen in der lokalen Datenbank gefunden.`,
                isOpen: true,
                source: 'error'
            });
        }
    };
    const fetchIcd11Details = async (diagnosis) => {
        if (!diagnosis || !diagnosis.uri) {
            setAppError("Keine gültige URI für die ICD-11 Diagnose vorhanden.");
            return;
        }
        const { code, title, uri } = diagnosis;
        setDiagnosisInfo({ code: code, name: title, isLoading: true, error: null, isOpen: true, source: 'api' });
        try {
            const response = await fetch(`http://localhost:3001/api/icd11-info?uri=${encodeURIComponent(uri)}`);
            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.message || 'API-Anfrage fehlgeschlagen');
            }
            const fullData = await response.json();
            setDiagnosisInfo({
                code: fullData.code || code,
                name: fullData.title?.['@value'] || title,
                beschreibung: fullData.definition?.['@value'] || '',
                diagnosticCriteria: fullData.diagnosticCriteria,
                inclusions: fullData.inclusion,
                exclusions: fullData.exclusion,
                isLoading: false, error: null, isOpen: true,
                source: 'api_detail'
            });
        } catch (error) {
            console.error('Fehler beim Abrufen der ICD-11 Details:', error);
            setDiagnosisInfo({ code: code, isLoading: false, error: `Konnte Informationen für ${code} nicht laden: ${error.message}`, isOpen: true, source: 'api' });
