                            <div>
                                <h3 className="text-xl font-semibold text-gray-800 mb-4">Terminkalender</h3>
                                <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-4">
                                    <div className="bg-blue-50 border-l-4 border-blue-400 p-4 mb-4 text-sm text-blue-700">
                                        <p className="font-bold">Anleitung zum Einbetten Ihres Google Kalenders:</p>
                                        <ol className="list-decimal list-inside mt-2 space-y-1">
                                            <li>Öffnen Sie Ihren <a href="https://calendar.google.com/" target="_blank" rel="noopener noreferrer" className="text-blue-600 underline">Google Kalender</a> im Browser.</li>
                                            <li>Gehen Sie zu "Einstellungen" (Zahnrad-Symbol) &rarr; "Einstellungen".</li>
                                            <li>Wählen Sie links unter "Einstellungen für meine Kalender" den Kalender aus, den Sie einbetten möchten.</li>
                                            <li>Scrollen Sie zu "Kalender integrieren" und kopieren Sie den Link aus dem Feld <b>"Öffentlich freigegebener Link zu diesem Kalender"</b> oder den HTML-Code aus <b>"Einbettungscode"</b>.</li>
                                            <li>Ersetzen Sie den <code>src</code>-Link im <code>iframe</code> unten im Code durch den Link aus Ihrem Kalender.</li>
                                        </ol>
                                    </div>
                                    <iframe
                                        title="Google Calendar"
                                        src="https://calendar.google.com/calendar/embed?src=julisycomorepsy%40gmail.com&ctz=Europe%2FBerlin"
                                        style={{ border: 0 }}
                                        width="100%"
                                        height="600"
                                        frameBorder="0"
                                        scrolling="no"
                                    ></iframe>
                                </div>
                            </div>
                        </div>
                    )}

                    {mainView === 'formulare' && (
                        <div>
                            <h3 className="text-2xl font-semibold text-gray-800 mb-4">Formular- & Dokumentenverwaltung</h3>
                            <div className="bg-white p-6 border border-gray-200 rounded-lg shadow-sm">
                                <label htmlFor="file-upload" className="mb-4 block w-full cursor-pointer">
                                    <div className="border-2 border-dashed border-gray-300 rounded-lg p-8 text-center hover:border-teal-400 transition-colors">
                                        <UploadCloudIcon className="mx-auto h-12 w-12 text-gray-400" />
                                        <span className="mt-2 block text-sm font-medium text-gray-900">
                                            Dateien zum Hochladen auswählen
                                        </span>
                                        <span className="mt-1 block text-xs text-gray-500">
                                            PDF, DOCX, etc.
                                        </span>
                                    </div>
                                    <input id="file-upload" name="file-upload" type="file" className="sr-only" multiple onChange={handleFileUpload} />
                                </label>

                                {uploadedFiles.length > 0 && (
                                    <div>
                                        <h4 className="text-lg font-medium text-gray-900 mb-2">Hochgeladene Dateien</h4>
                                        <ul className="divide-y divide-gray-200 border rounded-lg">
                                            {uploadedFiles.map((file, index) => (
                                                <li key={index} className="flex items-center justify-between p-3 hover:bg-gray-50">
                                                    <div className="flex items-center">
                                                        <FileIcon className="h-6 w-6 text-gray-400 mr-3"/>
                                                        <div>
                                                            <p className="text-sm font-medium text-gray-900">{file.name}</p>
                                                            <p className="text-xs text-gray-500">{(file.size / 1024).toFixed(2)} KB - Zuletzt geändert: {file.lastModified}</p>
                                                        </div>
                                                    </div>
                                                    <button onClick={() => handleRemoveFile(file.name)} className="text-gray-400 hover:text-red-600">
                                                        <Trash2 className="h-5 w-5"/>
                                                    </button>
                                                </li>
                                            ))}
                                        </ul>
                                    </div>
                                )}
                            </div>
                        </div>
                    )}

                    {mainView === 'ebm' && (<EBMBrowser />)}

                    {mainView === 'icd11' && (
                        <ICD11ECTBrowser
                            patients={patients}
                            selectedPatient={selectedPatient}
                            onAssignDiagnosis={handleAssignIcd11Diagnosis}
                        />
                    )}

                </main>
            </div>

            {/* MODALS AND GLOBAL ELEMENTS */}
            <Modal
                isOpen={diagnosisInfo.isOpen}
                onClose={() => setDiagnosisInfo(prev => ({ ...prev, isOpen: false }))}
                title={`Diagnose-Info: ${diagnosisInfo.code || ''}`}
                size="3xl"
            >
                {diagnosisInfo.isLoading && (<p className="text-center py-4">Informationen werden geladen...</p>)}
                {diagnosisInfo.error && (<p className="text-red-600 bg-red-100 p-3 rounded-md">Fehler: {diagnosisInfo.error}</p>)}
                {!diagnosisInfo.isLoading && diagnosisInfo.source === 'local' && (
                    <div className="text-sm text-gray-700 leading-relaxed p-2 space-y-3">
                        <h3 className="text-lg font-semibold text-teal-700">{diagnosisInfo.name}<span className="text-gray-500 text-base ml-2">({diagnosisInfo.code})</span></h3>
                        {diagnosisInfo.beschreibung && (<div><strong className="font-medium text-gray-800 block mb-1">Beschreibung:</strong><p className="whitespace-pre-wrap">{diagnosisInfo.beschreibung}</p></div>)}
                        {diagnosisInfo.inkl?.length > 0 && (<div><strong className="font-medium text-gray-800 block mb-1">Inklusive:</strong><ul className="list-disc list-inside">{diagnosisInfo.inkl.map((item, idx) => (<li key={idx}>{item}</li>))}</ul></div>)}
                        {diagnosisInfo.exkl?.length > 0 && (<div><strong className="font-medium text-gray-800 block mb-1">Exklusive:</strong><ul className="list-disc list-inside">{diagnosisInfo.exkl.map((item, idx) => (<li key={idx}>{item}</li>))}</ul></div>)}
                    </div>
                )}
                {!diagnosisInfo.isLoading && diagnosisInfo.source === 'api_detail' && (
                    <div className="text-sm text-gray-700 leading-relaxed p-2 space-y-4">
                        <h3 className="text-lg font-semibold text-purple-700">{diagnosisInfo.name}<span className="text-gray-500 text-base ml-2 font-mono">({diagnosisInfo.code})</span></h3>
                        {diagnosisInfo.beschreibung && (<div><strong className="font-medium text-gray-800 block mb-1">Beschreibung (Definition):</strong><p className="whitespace-pre-wrap bg-gray-50 p-2 rounded-md">{diagnosisInfo.beschreibung}</p></div>)}
                        {diagnosisInfo.inclusions?.length > 0 && (<div><strong className="font-medium text-gray-800 block mb-1">Inklusive:</strong><ul className="list-disc list-inside pl-2 space-y-1">{diagnosisInfo.inclusions.map((item, idx) => (<li key={idx}>{item.label['@value']}</li>))}</ul></div>)}
                        {diagnosisInfo.exclusions?.length > 0 && (<div><strong className="font-medium text-gray-800 block mb-1">Exklusive:</strong><ul className="list-disc list-inside pl-2 space-y-1">{diagnosisInfo.exclusions.map((item, idx) => (<li key={idx}>{item.label['@value']}{item.foundationReference &&<span className="text-xs text-gray-400 ml-2">({item.linearizationReference.split('/').pop()})</span>}</li>))}</ul></div>)}
                    </div>
                )}
            </Modal>

            <Modal
                isOpen={improvementState.isOpen}
                onClose={() => setImprovementState({ ...improvementState, isOpen: false })}
                title="Sitzungsdokumentation verbessern"
                size="5xl"
            >
                {improvementState.error && <p className="text-red-500 bg-red-100 p-3 rounded-md mb-4">{improvementState.error}</p>}
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div>
                        <h4 className="font-semibold text-lg text-gray-700 mb-2">Original</h4>
                        <pre className="text-sm bg-gray-100 p-3 rounded-md whitespace-pre-wrap font-sans h-96 overflow-y-auto border">{improvementState.originalText}</pre>
                    </div>
                    <div>
                        <h4 className="font-semibold text-lg text-gray-700 mb-2">Vorschlag</h4>
                        <pre className="text-sm bg-gray-800 text-green-300 font-mono p-3 rounded-md whitespace-pre-wrap h-96 overflow-y-auto">{improvementState.improvedText}</pre>
                    </div>
                </div>
                <div className="flex justify-end space-x-4 mt-6">
                    <button type="button" onClick={() => setImprovementState({ ...improvementState, isOpen: false })} className="px-4 py-2 text-sm font-medium text-gray-700 bg-gray-200 rounded-md hover:bg-gray-300">Abbrechen</button>
                    <button onClick={handleAcceptImprovement} className={`${btnPrimary} flex items-center`}>
                        <Save className="w-4 h-4 mr-2" /> Original ersetzen
                    </button>
                </div>
            </Modal>

            {appError && (<div className="fixed bottom-4 right-4 p-4 max-w-sm text-sm text-red-700 bg-red-100 border border-red-400 rounded-md shadow-lg z-[100]">{appError}<button onClick={() => setAppError(null)} className="ml-4 mt-2 sm:mt-0 sm:ml-2 px-2 py-1 text-red-900 underline rounded-md hover:bg-red-200 text-xs">Ok</button></div>)}

            <AddTestFlowModal
                isOpen={isAddTestModalOpen}
                onClose={() => setIsAddTestModalOpen(false)}
                onSave={handleSaveTestAndResults}
                testInfo={currentTestToAdd}
                patient={selectedPatient}
            />

            <ManualBillingSetupModal
                isOpen={isManualBillingSetupOpen}
                onClose={() => setIsManualBillingSetupOpen(false)}
                patients={sortedPatients}
                onStart={handleStartManualBilling}
            />

            <BillTestModal
                isOpen={retroBillingState.isOpen}
                onClose={() => setRetroBillingState({ isOpen: false, test: null })}
                onSave={handleSaveRetroactiveBilling}
                testToBill={retroBillingState.test}
                patientSessions={selectedPatient?.sessions || []}
            />

            {billingModalState.isOpen && (
                <Modal
                    isOpen={billingModalState.isOpen}
                    onClose={() => setBillingModalState({ isOpen: false, event: null, isEditing: false })}
                    title={billingModalState.isEditing ? "Sitzung bearbeiten" : `Termin abrechnen: ${billingModalState.event.summary}`}
                    size="4xl"
                >
                    <BillingModal
                        event={billingModalState.event}
                        ebmData={ebmData}
                        onSave={(data) => handleSaveBilling(data, billingModalState.isEditing)}
                        onClose={() => setBillingModalState({ isOpen: false, event: null, isEditing: false })}
                        generateGeminiText={generateGeminiText}
                        isEditing={billingModalState.isEditing}
                    />
                </Modal>
            )}
        </div>
    );
}


const CustomBdiLabel = (props) => {
    const { x, y, width, text } = props;
    if (typeof x !== 'number' || isNaN(x) ||
        typeof y !== 'number' || isNaN(y) ||
        typeof width !== 'number' || isNaN(width) || width <= 0) {
        return null;
    }
    const labelX = x + width / 2;
    return (<text x={labelX} y={y} fill="#666" textAnchor="middle" dy={-4} fontSize="10">{text}</text>);
};

export default App;
