        const newDiagnosis = { code: icd11Entity.code, title: icd11Entity.selectedText, uri: icd11Entity.linearizationUri || icd11Entity.foundationUri, date: new Date().toISOString() };
        const updatedDiagnoses = [...(targetPatient.diagnoses_icd11 || []), newDiagnosis];
        try { await setDoc(patientRef, { diagnoses_icd11: updatedDiagnoses }, { merge: true }); } 
        catch (err) { console.error("Error assigning ICD-11 diagnosis:", err); setAppError("Fehler beim Zuweisen der ICD-11 Diagnose."); }
    };

    const handleRemoveIcd11Diagnosis = async (diagnosisToRemove) => {
        if (!selectedPatient || !userId) return;
        const collectionPath = getPatientsCollectionPath();
        if (!collectionPath) return;
        const updatedDiagnoses = selectedPatient.diagnoses_icd11.filter(d => d.date !== diagnosisToRemove.date);
        const patientRef = doc(db, collectionPath, selectedPatient.id);
        try { await setDoc(patientRef, { diagnoses_icd11: updatedDiagnoses }, { merge: true }); } 
        catch (err) { console.error("Error removing ICD-11 diagnosis:", err); setAppError("Fehler beim Entfernen der ICD-11 Diagnose."); }
    };

