const handleImproveSessionNote = async (session, index) => {
    // Setzt den Ladezustand für den spezifischen Button
    setImprovementState({ ...improvementState, isLoading: true, sessionIndex: index, error: null });

    // Die Sitzungsnummer wird berechnet (angenommen, die neuste Sitzung ist oben)
    const sessionNumber = selectedPatient.sessions.length - index;

    // Der von Ihnen definierte Prompt wird mit den aktuellen Sitzungsdaten kombiniert
    const fullPrompt = `Stelle dir vor du bist ein Team aus erfahrenen Psychotherapeut/Verhaltenstherapeut wie wie Jürgen Margraf, Dr. Stavemann, Aaron T. Beck, Judith Beck und Jonathan S. Abramowitz, welche großen Wert auf fachlich präzise Dokumentation und Äußerungen legen. Du hast ein großes fachliches Wissen über die klassische VT sowie auch in dritte Welle Verfahren wie ACT durch Steve Hayes und Russ Harris. Kannst du meine Therapiedokumentation verbessern? Kannst du die Doku weiterhin wie folgt organisieren: Sitzungsnummer, Thema, Patientenbericht, Therapeutische Interventionen, Gesamteindruck, Risikoabschätzung, ggf. falls benannt Hausaufgaben, Plan für die nächste Sitzung. Das macht die Dokumentation übersichtlicher. Der Teil des Pateintenberichts soll im Fließtext geschrieben sein. Kannst du den fertigen Text mir dann auch im Markdownformat ausgeben, aber bitte ohne * oder # (also nicht fett oder mit Titeln) oder em-dashes. Zeig den Text als Codeblock, wie in einem Terminal. Atme einmal tief ein und aus und nehme dir Zeit für die Aufgabe. Mache bitte weiter, bis das Problem vollständig gelöst ist. Planen bitte ausführlich vor jeder Aktion.

Hier ist die zu überarbeitende Dokumentation für die Sitzung ${sessionNumber}:
---
${session.notes}
---
`;

    try {
        const improvedText = await generateGeminiText(fullPrompt);
        // Öffnet das Modal mit dem Vergleich
        setImprovementState({
            isOpen: true,
            isLoading: false,
            originalText: session.notes,
            improvedText: improvedText.replace(/^```(json|text)?\s*|```$/gm, ''), // Entfernt mögliche ``` Markierungen der KI
            sessionIndex: index,
            error: null
        });
    } catch (e) {
        const errorMsg = `Fehler bei der Verbesserung der Notiz: ${e.message}`;
        setImprovementState({ isLoading: false, error: errorMsg, sessionIndex: null, isOpen: false });
        setAppError(errorMsg);
    }
};

