    updatedSessions[improvementState.sessionIndex].notes = improvementState.improvedText;

    const patientRef = doc(db, collectionPath, selectedPatient.id);
    try {
        await setDoc(patientRef, { sessions: updatedSessions }, { merge: true });
        // Schließt das Modal und setzt den State zurück
