    </div>
)}
                                {activeDetailTab === 'krankheitsmodell' && ( <div className="space-y-6"><div><label htmlFor="krankheitsanamnese" className="block text-lg font-semibold text-gray-700 mb-1">Krankheitsanamnese</label><textarea id="krankheitsanamnese" name="krankheitsanamnese" rows="6" className={textareaStd} value={editableKrankheitsanamnese} onChange={(e) => setEditableKrankheitsanamnese(e.target.value)} onBlur={(e) => handleKrankheitsmodellChange('krankheitsanamnese', e.target.value)} placeholder="Hier die Krankheitsanamnese eintragen..."></textarea></div><div><label htmlFor="lebensgeschichte" className="block text-lg font-semibold text-gray-700 mb-1">Lebensgeschichte / Biographie</label><textarea id="lebensgeschichte" name="lebensgeschichte" rows="6" className={textareaStd} value={editableLebensgeschichte} onChange={(e) => setEditableLebensgeschichte(e.target.value)} onBlur={(e) => handleKrankheitsmodellChange('lebensgeschichte', e.target.value)} placeholder="Wichtige Aspekte der Lebensgeschichte..."></textarea></div><div><label htmlFor="problemanalyseMakro" className="block text-lg font-semibold text-gray-700 mb-1">Problemanalyse auf Makroebene</label><textarea id="problemanalyseMakro" name="problemanalyseMakro" rows="6" className={textareaStd} value={editableProblemanalyseMakro} onChange={(e) => setEditableProblemanalyseMakro(e.target.value)} onBlur={(e) => handleKrankheitsmodellChange('problemanalyseMakro', e.target.value)} placeholder="Analyse der übergeordneten Problembereiche..."></textarea></div><div><label htmlFor="therapieplan" className="block text-lg font-semibold text-gray-700 mb-1">Therapieplan</label><textarea id="therapieplan" name="therapieplan" rows="6" className={textareaStd} value={editableTherapieplan} onChange={(e) => setEditableTherapieplan(e.target.value)} onBlur={(e) => handleKrankheitsmodellChange('therapieplan', e.target.value)} placeholder="Therapieziele und -maßnahmen..."></textarea></div></div>)}
                                {activeDetailTab === 'diagnostik' && (<div><div className="border-b border-gray-200 mb-4"><nav className="-mb-px flex space-x-6" aria-label="Diagnostik Sub-Tabs">{['scl90r', 'fep2', 'bdi2', 'hscl11'].map(subTab => (<button key={subTab} onClick={() => setActiveDiagnostikSubTab(subTab)} className={`whitespace-nowrap pb-2 px-1 border-b-2 font-medium text-sm ${activeDiagnostikSubTab === subTab ? 'border-purple-500 text-purple-600' : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'}`}>{subTab.toUpperCase()}</button>))}</nav></div>{activeDiagnostikSubTab === 'scl90r' && ( <SCL90RComponent patient={selectedPatient} onSaveResult={handleAddScl90rResult} onDeleteResult={handleRemoveScl90rResult} /> )}{activeDiagnostikSubTab === 'fep2' && ( <FEP2Component patient={selectedPatient} onSaveResult={handleAddFepResult} onDeleteResult={handleRemoveFepResult} setAppError={setAppError} /> )}{activeDiagnostikSubTab === 'bdi2' && ( <div><h4 className="text-xl font-semibold text-gray-700 mb-4">BDI-II Ergebnisse</h4><div className="bg-gray-50 p-4 rounded-lg mb-6 shadow"><h5 className="text-md font-semibold text-gray-600 mb-3">Neues BDI-II Ergebnis</h5><div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-3 items-end"><div><label htmlFor="bdiDate" className="block text-sm font-medium text-gray-700">Datum</label><input type="date" id="bdiDate" value={newBdiDate} onChange={(e) => setNewBdiDate(e.target.value)} className={inputStd} /></div><div><label htmlFor="bdiScore" className="block text-sm font-medium text-gray-700">BDI-II Wert (0-63)</label><input type="number" id="bdiScore" value={newBdiScore} onChange={(e) => setNewBdiScore(e.target.value)} min="0" max="63" className={inputStd} /></div><button onClick={handleAddBdiResult} className={`${btnPrimary} h-10`}><PlusCircleIcon className="w-5 h-5 mr-2" /> Speichern</button></div></div><div className="mb-6"><h5 className="text-md font-semibold text-gray-600 mb-2">Protokollierte BDI-II Ergebnisse:</h5><ul className="space-y-2">{(selectedPatient.bdi2Results || []).map((result, index) => ( <li key={index} className="flex justify-between items-center bg-white border p-3 rounded-md shadow-sm"><div><span className="font-medium">{new Date(result.date).toLocaleDateString('de-DE')}</span>: <span className="text-gray-700">Score {result.score}</span></div><button onClick={() => handleRemoveBdiResult(result.date)} className={`${btnDanger} p-1 text-xs`}><Trash2 className="w-4 h-4" /></button></li>))}{(selectedPatient.bdi2Results || []).length === 0 && <p className="text-sm text-gray-500">Keine BDI-II Ergebnisse erfasst.</p>}</ul></div>{(selectedPatient.bdi2Results || []).length > 0 && (<div><h5 className="text-md font-semibold text-gray-600 mb-3">Verlaufsgraph BDI-II</h5><div className="bg-white p-4 rounded-lg shadow" style={{ height: '400px' }}><ResponsiveContainer width="100%" height="100%"><LineChart data={(selectedPatient.bdi2Results || []).map(r => ({...r, date: new Date(r.date).toLocaleDateString('de-DE')}))} margin={{ top: 5, right: 30, left: 0, bottom: 20 }}><CartesianGrid strokeDasharray="3 3" /><XAxis dataKey="date" /><YAxis domain={[0, 63]} ticks={bdiAxisTicks} allowDataOverflow={true}/><Tooltip /><Legend verticalAlign="top" height={36}/>{bdiCutoffs.map(cutoff => ( <ReferenceArea key={cutoff.label} y1={cutoff.y1} y2={cutoff.y2} fill={cutoff.color} strokeOpacity={0.3} label={<CustomBdiLabel text={cutoff.label} y={cutoff.y2 - (cutoff.y2-cutoff.y1)/2} />}/> )) }<Line type="monotone" dataKey="score" name="BDI-II Score" stroke="#3b82f6" strokeWidth={2} activeDot={{ r: 8 }} /></LineChart></ResponsiveContainer></div></div>)}</div>)}{activeDiagnostikSubTab === 'hscl11' && ( <div><h4 className="text-xl font-semibold text-gray-700 mb-4">HSCL-11 Ergebnisse</h4><div className="bg-gray-50 p-4 rounded-lg mb-6 shadow"><h5 className="text-md font-semibold text-gray-600 mb-3">Neues HSCL-11 Ergebnis</h5><div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-3 items-end"><div><label htmlFor="hsclDate" className="block text-sm font-medium text-gray-700">Datum</label><input type="date" id="hsclDate" value={newHsclDate} onChange={(e) => setNewHsclDate(e.target.value)} className={inputStd} /></div><div><label htmlFor="hsclAngst" className="block text-sm font-medium text-gray-700">Angst (0-20)</label><input type="number" id="hsclAngst" value={newHsclAngst} onChange={(e) => setNewHsclAngst(e.target.value)} min="0" max="20" className={inputStd} /></div><div><label htmlFor="hsclDepression" className="block text-sm font-medium text-gray-700">Depression (0-24)</label><input type="number" id="hsclDepression" value={newHsclDepression} onChange={(e) => setNewHsclDepression(e.target.value)} min="0" max="24" className={inputStd} /></div><button onClick={handleAddHsclResult} className={`${btnPrimary} bg-teal-500 hover:bg-teal-600 h-10`}><PlusCircleIcon className="w-5 h-5 mr-2" /> Speichern</button></div></div><div className="mb-6"><h5 className="text-md font-semibold text-gray-600 mb-2">Protokollierte HSCL-11 Ergebnisse:</h5><ul className="space-y-2">{(selectedPatient.hscl11Results || []).map((result, index) => ( <li key={index} className="flex justify-between items-center bg-white border p-3 rounded-md shadow-sm"><div><span className="font-medium">{new Date(result.date).toLocaleDateString('de-DE')}</span>: <span className="text-sm text-gray-600 ml-2">Angst: {result.angst}</span>, <span className="text-sm text-gray-600 ml-2">Depression: {result.depression}</span>, <span className="text-sm text-gray-600 ml-2">Gesamt: {result.cumulative}</span></div><button onClick={() => handleRemoveHsclResult(result.date)} className={`${btnDanger} p-1 text-xs`}><Trash2 className="w-4 h-4" /></button></li>))}{(selectedPatient.hscl11Results || []).length === 0 && <p className="text-sm text-gray-500">Keine HSCL-11 Ergebnisse erfasst.</p>}</ul></div>{(selectedPatient.hscl11Results || []).length > 0 && (<div><h5 className="text-md font-semibold text-gray-600 mb-3">Verlaufsgraph HSCL-11</h5><div className="bg-white p-4 rounded-lg shadow" style={{ height: '400px' }}><ResponsiveContainer width="100%" height="100%"><LineChart data={(selectedPatient.hscl11Results || []).map(r => ({...r, date: new Date(r.date).toLocaleDateString('de-DE')}))} margin={{ top: 5, right: 30, left: 0, bottom: 5 }}><CartesianGrid strokeDasharray="3 3" /><XAxis dataKey="date" /><YAxis domain={[0, 44]} /><Tooltip /><Legend /><Line type="monotone" dataKey="angst" name="Angst" stroke="#8884d8" activeDot={{ r: 8 }} /><Line type="monotone" dataKey="depression" name="Depression" stroke="#82ca9d" /><Line type="monotone" dataKey="cumulative" name="Gesamt" stroke="#ffc658" /></LineChart></ResponsiveContainer></div></div>)}</div>)}</div>)}
                                {activeDetailTab === 'assistenz' && (<div><h3 className="text-xl font-semibold text-gray-700 mb-4">Therapeutische Assistenz</h3><button onClick={handleTherapeutischeAssistenzAnfordern} disabled={isLoadingTherapeutischeAssistenz} className={`${btnPrimary} bg-purple-600 hover:bg-purple-700 mb-4 flex items-center`}><WandSparkles className="mr-2 h-5 w-5"/>{isLoadingTherapeutischeAssistenz ? "Analysiere Daten..." : "Expertenfeedback anfordern"}</button>{isLoadingTherapeutischeAssistenz && (<div className="text-center py-10"><div className="animate-spin rounded-full h-12 w-12 border-b-2 border-purple-700 mx-auto"></div><p className="mt-3 text-gray-600">Bitte warten, die KI analysiert die Patientendaten...</p></div>)}{assistenzError && (<div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4" role="alert"><p className="font-bold">Fehler bei der Assistenz</p><p>{assistenzError}</p></div>)}{therapeutischeAssistenzOutput && !isLoadingTherapeutischeAssistenz && (<div className="mt-4 p-4 bg-gray-50 rounded-md border"><h4 className="text-lg font-semibold text-gray-800 mb-2">Vorschläge des Expertenteams:</h4><div className="whitespace-pre-wrap text-sm text-gray-700 leading-relaxed">{therapeutischeAssistenzOutput}</div></div>)}</div>)}
                           </div>
                        )}
                    </>
                )}

               {mainView === 'icd11' && (
                    <ICD11ECTBrowser 
                        patients={patients}
                        selectedPatient={selectedPatient}
                        onAssignDiagnosis={handleAssignIcd11Diagnosis}
                    />
                )}

               <Modal 
    isOpen={diagnosisInfo.isOpen} 
    onClose={() => setDiagnosisInfo(prev => ({...prev, isOpen: false}))} 
    title={`Diagnose-Info: ${diagnosisInfo.code || ''}`} 
    size="3xl"
>
    {diagnosisInfo.isLoading && (
        <p className="text-center py-4">Informationen werden geladen...</p>
    )}
    {diagnosisInfo.error && (
        <p className="text-red-600 bg-red-100 p-3 rounded-md">
            Fehler: {diagnosisInfo.error}
        </p>
    )}

    {/* --- Bestehender Block für ICD-10 --- */}
    {!diagnosisInfo.isLoading && diagnosisInfo.source === 'local' && (
        <div className="text-sm text-gray-700 leading-relaxed p-2 space-y-3">
            <h3 className="text-lg font-semibold text-indigo-700">
                {diagnosisInfo.name} 
                <span className="text-gray-500 text-base ml-2">
                    ({diagnosisInfo.code})
                </span>
            </h3>
            {diagnosisInfo.beschreibung && (
                <div>
                    <strong className="font-medium text-gray-800 block mb-1">
                        Beschreibung:
                    </strong>
                    <p className="whitespace-pre-wrap">
                        {diagnosisInfo.beschreibung}
                    </p>
                </div>
            )}
            {diagnosisInfo.inkl?.length > 0 && (
                <div>
                    <strong className="font-medium text-gray-800 block mb-1">
                        Inklusive:
                    </strong>
                    <ul className="list-disc list-inside">
                        {diagnosisInfo.inkl.map((item, idx) => (
                            <li key={idx}>{item}</li>
                        ))}
                    </ul>
                </div>
            )}
            {diagnosisInfo.exkl?.length > 0 && (
                <div>
                    <strong className="font-medium text-gray-800 block mb-1">
                        Exklusive:
                    </strong>
                    <ul className="list-disc list-inside">
                        {diagnosisInfo.exkl.map((item, idx) => (
                            <li key={idx}>{item}</li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    )}

    {/* --- NEUER Block für ICD-11 --- */}
    {!diagnosisInfo.isLoading && diagnosisInfo.source === 'api_detail' && (
        <div className="text-sm text-gray-700 leading-relaxed p-2 space-y-4">
            <h3 className="text-lg font-semibold text-purple-700">
                {diagnosisInfo.name} 
                <span className="text-gray-500 text-base ml-2 font-mono">
                    ({diagnosisInfo.code})
                </span>
            </h3>

            {/* Die Beschreibung kommt aus dem 'definition' Feld der API  */}
            {diagnosisInfo.beschreibung && (
                <div>
                    <strong className="font-medium text-gray-800 block mb-1">
                        Beschreibung (Definition):
                    </strong>
                    <p className="whitespace-pre-wrap bg-gray-50 p-2 rounded-md">
                        {diagnosisInfo.beschreibung}
                    </p>
                </div>
            )}

            {/* Inklusionen sind ein Array von Objekten  */}
            {diagnosisInfo.inclusions?.length > 0 && (
                <div>
                    <strong className="font-medium text-gray-800 block mb-1">
                        Inklusive:
                    </strong>
                    <ul className="list-disc list-inside pl-2 space-y-1">
                        {diagnosisInfo.inclusions.map((item, idx) => (
                            <li key={idx}>{item.label['@value']}</li>
                        ))}
                    </ul>
                </div>
            )}

            {/* Exklusionen sind ebenfalls ein Array von Objekten  */}
            {diagnosisInfo.exclusions?.length > 0 && (
                <div>
                    <strong className="font-medium text-gray-800 block mb-1">
                        Exklusive:
                    </strong>
                    <ul className="list-disc list-inside pl-2 space-y-1">
                        {diagnosisInfo.exclusions.map((item, idx) => (
                             <li key={idx}>
                                {item.label['@value']} 
                                {item.foundationReference && 
                                 <span className="text-xs text-gray-400 ml-2">({item.linearizationReference.split('/').pop()})</span>}
                            </li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    )}
</Modal>
                {appError && (<div className="fixed bottom-4 right-4 p-4 max-w-sm text-sm text-red-700 bg-red-100 border border-red-400 rounded-md shadow-lg z-[100]">{appError}<button onClick={() => setAppError(null)} className="ml-4 mt-2 sm:mt-0 sm:ml-2 px-2 py-1 text-red-900 underline rounded-md hover:bg-red-200 text-xs">Ok</button></div>)}
            </main>
            <footer className="text-center py-6 text-sm text-gray-500 border-t border-gray-200 mt-10">Therapie-Dashboard &copy; {new Date().getFullYear()}</footer>
        </div>
    );
}


const CustomBdiLabel = (props) => {
    const { x, y, width, text } = props;
    if (typeof x !== 'number' || isNaN(x) || 
        typeof y !== 'number' || isNaN(y) ||
        typeof width !== 'number' || isNaN(width) || width <= 0) {
        return null; 
    }
    const labelX = x + width / 2;
    return ( <text x={labelX} y={y} fill="#666" textAnchor="middle" dy={-4} fontSize="10">{text}</text> );
};

export default App;
