import React, { useMemo, useState } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

const PlusCircle = ({ className = "w-5 h-5" }) => <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><circle cx="12" cy="12" r="10"></circle><line x1="12" y1="8" x2="12" y2="16"></line><line x1="8" y1="12" x2="16" y2="12"></line></svg>;
const ChevronRight = ({ className = "w-5 h-5" }) => <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><polyline points="9 18 15 12 9 6"></polyline></svg>;

const ProgressBar = ({ value, max, label, colorClass = 'bg-teal-500' }) => {
    const percentage = max > 0 ? (value / max) * 100 : 0;
    return (
        <div>
            <div className="flex justify-between mb-1">
                <span className="text-sm font-medium text-gray-700">{label}</span>
                <span className="text-sm font-medium text-gray-500">{value} / {max}</span>
            </div>
            <div className="w-full bg-gray-200 rounded-full h-2.5">
                <div className={`${colorClass} h-2.5 rounded-full`} style={{ width: `${Math.min(percentage, 100)}%` }}></div>
            </div>
        </div>
    );
};

const TestFrequencyChart = ({ patient }) => {
    const chartData = useMemo(() => {
        const allTests = [];
        ['scl90rResults', 'fep2Results', 'bdi2Results', 'hscl11Results'].forEach(key => {
            if (patient[key]) {
                allTests.push(...patient[key]);
            }
        });

        const countsByDate = allTests.reduce((acc, test) => {
            const date = new Date(test.date).toLocaleDateString('de-DE');
            acc[date] = (acc[date] || 0) + 1;
            return acc;
        }, {});

        return Object.entries(countsByDate)
            .map(([date, count]) => ({ date, anzahl: count }))
            .sort((a, b) => new Date(a.date.split('.').reverse().join('-')) - new Date(b.date.split('.').reverse().join('-')));
    }, [patient]);

    if (chartData.length === 0) return null;

    return (
        <div className="mt-6">
            <h4 className="text-lg font-semibold text-gray-700 mb-3">Testfrequenz nach Datum</h4>
            <div style={{ width: '100%', height: 300 }}>
                <ResponsiveContainer>
                    <BarChart data={chartData} margin={{ top: 5, right: 20, left: -10, bottom: 5 }}>
                        <CartesianGrid strokeDasharray="3 3" />
                        <XAxis dataKey="date" />
                        <YAxis allowDecimals={false} />
                        <Tooltip />
                        <Legend />
                        <Bar dataKey="anzahl" fill="#4fd1c5" name="Anzahl der Tests" />
                    </BarChart>
                </ResponsiveContainer>
            </div>
        </div>
    );
};


const DiagnostikUebersicht = ({ patient, availableTests, onAddTest, onShowTestDetails, diagnostikAnalysis }) => {
    const [selectedQuarter, setSelectedQuarter] = useState(diagnostikAnalysis.currentQuarterIdentifier);

    const analysisForSelectedQuarter = useMemo(() => {
        return diagnostikAnalysis.quarterlyAnalysis[selectedQuarter] || {
            pointsUsed: 0,
            instancesUsed: 0,
            tests: []
        };
    }, [selectedQuarter, diagnostikAnalysis]);

    const conductedTests = useMemo(() => {
        return availableTests.map(test => {
            const results = patient[`${test.id}Results`] || [];
            return { ...test, count: results.length, results };
        }).filter(test => test.count > 0);
    }, [patient, availableTests]);

    const unconductedTests = useMemo(() => {
        return availableTests.filter(test => !(patient[`${test.id}Results`] || []).length);
    }, [patient, availableTests]);

    return (
        <div className="space-y-8">
            <div className="p-4 bg-white rounded-lg shadow-sm border border-gray-200">
                <div className="flex justify-between items-center mb-4">
                    <h3 className="text-lg font-bold text-gray-800">Diagnostik-Kontingent</h3>
                    <select
                        value={selectedQuarter}
                        onChange={(e) => setSelectedQuarter(e.target.value)}
                        className="text-sm border-gray-300 rounded-md shadow-sm focus:border-teal-300 focus:ring focus:ring-teal-200 focus:ring-opacity-50"
                    >
                        {diagnostikAnalysis.availableQuarters.map(q => <option key={q} value={q}>{q}</option>)}
                    </select>
                </div>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div>
                        <h4 className="font-semibold text-gray-700 mb-2">Punkte pro Quartal</h4>
                        <ProgressBar
                            value={analysisForSelectedQuarter.pointsUsed}
                            max={diagnostikAnalysis.pointsLimit}
                            label={`Altersgruppe ${diagnostikAnalysis.age < 22 ? '< 22' : '≥ 22'} Jahre`}
                            colorClass="bg-blue-500"
                        />
                         <p className="text-xs text-gray-500 mt-1">Verbleibend: {Math.max(0, diagnostikAnalysis.pointsLimit - analysisForSelectedQuarter.pointsUsed)} Punkte</p>
                    </div>
                    <div>
                        <h4 className="font-semibold text-gray-700 mb-2">Test-Instanzen pro Therapiephase</h4>
                        <ProgressBar
                            value={diagnostikAnalysis.instancesInCurrentPhase}
                            max={diagnostikAnalysis.instancesLimit}
                            label={`Phase: ${diagnostikAnalysis.currentPhase}`}
                            colorClass="bg-purple-500"
                        />
                         <p className="text-xs text-gray-500 mt-1">Verbleibend: {Math.max(0, diagnostikAnalysis.instancesLimit - diagnostikAnalysis.instancesInCurrentPhase)} Instanzen</p>
                    </div>
                </div>
                <TestFrequencyChart patient={patient} />
            </div>

            <div>
                <h4 className="text-lg font-semibold text-gray-700 mb-3">Durchgeführte Testdiagnostik</h4>
                {conductedTests.length > 0 ? (
                    <div className="space-y-3">
                        {conductedTests.map(test => (
                            <div key={test.id} className="bg-white p-3 rounded-md border shadow-sm flex justify-between items-center">
                                <div>
                                    <p className="font-semibold text-gray-800">{test.name}</p>
                                    <p className="text-sm text-gray-500">{test.count} mal durchgeführt</p>
                                </div>
                                <button onClick={() => onShowTestDetails(test.id)} className="text-teal-600 hover:text-teal-800 font-semibold text-sm flex items-center">Details <ChevronRight /></button>
                            </div>
                        ))}
                    </div>
                ) : <p className="text-sm text-gray-500 p-3 bg-gray-50 rounded-md">Noch keine Tests für diesen Patienten durchgeführt.</p>}
            </div>

            <div>
                <h4 className="text-lg font-semibold text-gray-700 mb-3">Verfügbare Tests</h4>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                    {unconductedTests.map(test => (
                        <div key={test.id} className="bg-white p-3 rounded-md border flex justify-between items-center">
                            <div>
                                <p className="font-semibold text-gray-800">{test.name}</p>
                                <p className="text-xs text-gray-500">EBM: {test.ebm}, Empfehlung: {test.recommendation}x 5min</p>
                            </div>
                            <button onClick={() => onAddTest(test)} className="text-teal-600 hover:text-teal-800 p-2 rounded-full"><PlusCircle /></button>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default DiagnostikUebersicht;
