import React, { useMemo, useState, useEffect } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

const PlusCircle = ({ className = "w-5 h-5" }) => <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><circle cx="12" cy="12" r="10"></circle><line x1="12" y1="8" x2="12" y2="16"></line><line x1="8" y1="12" x2="16" y2="12"></line></svg>;
const ChevronRight = ({ className = "w-5 h-5" }) => <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><polyline points="9 18 15 12 9 6"></polyline></svg>;

const ProgressBar = ({ value, max, label, colorClass = 'bg-teal-500' }) => {
    const percentage = max > 0 ? (value / max) * 100 : 0;
    return (
        <div>
            <div className="flex justify-between mb-1">
                <span className="text-sm font-medium text-gray-700">{label}</span>
                <span className="text-sm font-medium text-gray-500">{value} / {max}</span>
            </div>
            <div className="w-full bg-gray-200 rounded-full h-2.5">
                <div className={`${colorClass} h-2.5 rounded-full`} style={{ width: `${Math.min(percentage, 100)}%` }}></div>
            </div>
        </div>
    );
};

const DiagnostikUebersicht = ({ patient, availableTests, onAddTest, onShowTestDetails, onAssignTestToPeriod, diagnostikAnalysis }) => {
    const [selectedPeriodId, setSelectedPeriodId] = useState(patient?.billingPeriods?.[0]?.id || null);

    useEffect(() => {
        if (patient?.billingPeriods?.length > 0) {
            const firstPeriodId = patient.billingPeriods[0].id;
            if (!patient.billingPeriods.some(p => p.id === selectedPeriodId)) {
                 setSelectedPeriodId(firstPeriodId);
            } else if (selectedPeriodId === null) {
                setSelectedPeriodId(firstPeriodId);
            }
        } else {
            setSelectedPeriodId(null);
        }
    }, [patient, selectedPeriodId]);

    const analysisForSelectedPeriod = useMemo(() => {
        if (selectedPeriodId && diagnostikAnalysis[selectedPeriodId]) {
            return diagnostikAnalysis[selectedPeriodId];
        }
        return { pointsUsed: 0, pointsLimit: 0, name: 'Kein Zeitraum gewählt' };
    }, [selectedPeriodId, diagnostikAnalysis]);

    const unassignedTests = useMemo(() => {
        const tests = [];
        availableTests.forEach(testDef => {
            const results = patient[`${testDef.id}Results`] || [];
            results.forEach(result => {
                if (result.billingPeriodId === null || result.billingPeriodId === undefined) {
                    tests.push({
                        ...testDef,
                        ...result,
                        testType: testDef.id
                    });
                }
            });
        });
        return tests.sort((a, b) => new Date(b.date) - new Date(a.date));
    }, [patient, availableTests]);

    const conductedTests = useMemo(() => {
        return availableTests.map(test => {
            const results = patient[`${test.id}Results`] || [];
            return { ...test, count: results.length, results };
        }).filter(test => test.count > 0);
    }, [patient, availableTests]);

    return (
        <div className="space-y-8">
            <div className="p-4 bg-white rounded-lg shadow-sm border border-gray-200">
                <div className="flex justify-between items-center mb-4">
                    <h3 className="text-lg font-bold text-gray-800">Diagnostik-Kontingent</h3>
                    {(patient?.billingPeriods || []).length > 0 ? (
                        <select
                            value={selectedPeriodId || ''}
                            onChange={(e) => setSelectedPeriodId(e.target.value)}
                            className="text-sm border-gray-300 rounded-md shadow-sm focus:border-teal-300 focus:ring focus:ring-teal-200 focus:ring-opacity-50"
                        >
                            {patient.billingPeriods.map(p => <option key={p.id} value={p.id}>{p.name}</option>)}
                        </select>
                    ) : (
                         <p className="text-sm text-gray-500">Bitte im Reiter "Abrechnung" Zeiträume definieren.</p>
                    )}
                </div>
                {selectedPeriodId && (
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                        <div>
                            <h4 className="font-semibold text-gray-700 mb-2">Punkte für "{analysisForSelectedPeriod.name}"</h4>
                            <ProgressBar
                                value={analysisForSelectedPeriod.pointsUsed}
                                max={analysisForSelectedPeriod.pointsLimit}
                                label={`Verbraucht / Limit`}
                                colorClass="bg-blue-500"
                            />
                             <p className="text-xs text-gray-500 mt-1">Verbleibend: {Math.max(0, analysisForSelectedPeriod.pointsLimit - analysisForSelectedPeriod.pointsUsed)} Punkte</p>
                        </div>
                    </div>
                )}
            </div>

            {unassignedTests.length > 0 && (
                <div>
                    <h4 className="text-lg font-semibold text-orange-700 mb-3">Nicht zugeordnete Tests</h4>
                    <div className="space-y-3">
                        {unassignedTests.map(test => (
                            <div key={test.testInstanceId} className="bg-orange-50 p-3 rounded-md border border-orange-200">
                                <div className="flex justify-between items-start">
                                    <div>
                                        <p className="font-semibold text-gray-800">{test.name}</p>
                                        <p className="text-sm text-gray-500">Durchgeführt am: {new Date(test.date).toLocaleDateString('de-DE')}</p>
                                    </div>
                                    <div className="w-1/2">
                                        <select
                                            onChange={(e) => onAssignTestToPeriod(test.testInstanceId, test.testType, e.target.value)}
                                            className="block w-full text-sm border-gray-300 rounded-md shadow-sm"
                                            defaultValue=""
                                        >
                                            <option value="" disabled>Abrechnungszeitraum zuweisen...</option>
                                            <option value="frei">Abrechnungsfrei belassen</option>
                                            {(patient.billingPeriods || []).map(p => (
                                                <option key={p.id} value={p.id}>{p.name}</option>
                                            ))}
                                        </select>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            )}

            <div>
                <h4 className="text-lg font-semibold text-gray-700 mb-3">Durchgeführte Testdiagnostik (Alle)</h4>
                {conductedTests.length > 0 ? (
                    <div className="space-y-3">
                        {conductedTests.map(test => (
                            <div key={test.id} className="bg-white p-3 rounded-md border shadow-sm flex justify-between items-center">
                                <div>
                                    <p className="font-semibold text-gray-800">{test.name}</p>
                                    <p className="text-sm text-gray-500">{test.count} mal durchgeführt</p>
                                </div>
                                <button onClick={() => onShowTestDetails(test.id)} className="text-teal-600 hover:text-teal-800 font-semibold text-sm flex items-center">Details <ChevronRight /></button>
                            </div>
                        ))}
                    </div>
                ) : <p className="text-sm text-gray-500 p-3 bg-gray-50 rounded-md">Noch keine Tests für diesen Patienten durchgeführt.</p>}
            </div>

            <div>
                <h4 className="text-lg font-semibold text-gray-700 mb-3">Neuen Test hinzufügen</h4>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                    {availableTests.map(test => (
                        <div key={test.id} className="bg-white p-3 rounded-md border flex justify-between items-center">
                            <div>
                                <p className="font-semibold text-gray-800">{test.name}</p>
                                <p className="text-xs text-gray-500">EBM: {test.ebm}</p>
                            </div>
                            <button onClick={() => onAddTest(test)} className="text-teal-600 hover:text-teal-800 p-2 rounded-full"><PlusCircle /></button>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default DiagnostikUebersicht;
