import React, { useState } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, ReferenceArea } from 'recharts';

// Icons (können aus App.js kopiert oder aus einer zentralen Icon-Datei importiert werden)
const PlusCircleIcon = ({ className = "w-6 h-6" }) => ( <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><circle cx="12" cy="12" r="10" /><line x1="12" y1="8" x2="12" y2="16" /><line x1="8" y1="12" x2="16" y2="12" /></svg>);
const Trash2 = ({ className = "w-6 h-6" }) => ( <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><polyline points="3 6 5 6 21 6" /><path d="M19 6v14a2 2 0 0 1-2 2H7a2 2 0 0 1-2-2V6m3 0V4a2 2 0 0 1 2-2h4a2 2 0 0 1 2 2v2" /><line x1="10" y1="11" x2="10" y2="17" /><line x1="14" y1="11" x2="14" y2="17" /></svg>);


const BDI2Component = ({ patient, onAddResult, onDeleteResult, bdiCutoffs, bdiAxisTicks, CustomBdiLabel, setAppError }) => {
    const [newBdiDate, setNewBdiDate] = useState(new Date().toISOString().split('T')[0]);
    const [newBdiScore, setNewBdiScore] = useState('');

    const handleAdd = () => {
        const score = parseInt(newBdiScore, 10);
        if (!newBdiDate || isNaN(score) || score < 0 || score > 63) {
            setAppError("Bitte geben Sie ein gültiges Datum und einen BDI-II Wert zwischen 0 und 63 ein.");
            return;
        }
        onAddResult({ date: newBdiDate, score: score });
        setNewBdiScore('');
        setAppError(null);
    };

    // Hilfsvariablen für Styling
    const inputStd = "mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm";
    const btnPrimary = "bg-indigo-600 hover:bg-indigo-700 text-white font-semibold py-2 px-4 rounded-md shadow-sm flex items-center justify-center h-10";
    const btnDanger = "bg-red-500 hover:bg-red-600 text-white font-semibold py-2 px-3 rounded-md flex items-center";


    return (
        <div>
            <h4 className="text-xl font-semibold text-gray-700 mb-4">BDI-II Ergebnisse</h4>
            <div className="bg-gray-50 p-4 rounded-lg mb-6 shadow">
                <h5 className="text-md font-semibold text-gray-600 mb-3">Neues BDI-II Ergebnis</h5>
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-3 items-end">
                    <div>
                        <label htmlFor="bdiDate" className="block text-sm font-medium text-gray-700">Datum</label>
                        <input type="date" id="bdiDate" value={newBdiDate} onChange={(e) => setNewBdiDate(e.target.value)} className={inputStd} />
                    </div>
                    <div>
                        <label htmlFor="bdiScore" className="block text-sm font-medium text-gray-700">BDI-II Wert (0-63)</label>
                        <input type="number" id="bdiScore" value={newBdiScore} onChange={(e) => setNewBdiScore(e.target.value)} min="0" max="63" className={inputStd} />
                    </div>
                    <button onClick={handleAdd} className={btnPrimary}>
                        <PlusCircleIcon className="w-5 h-5 mr-2" /> Speichern
                    </button>
                </div>
            </div>

            <div className="mb-6">
                <h5 className="text-md font-semibold text-gray-600 mb-2">Protokollierte BDI-II Ergebnisse:</h5>
                <ul className="space-y-2">
                    {(patient.bdi2Results || []).map((result, index) => (
                        <li key={index} className="flex justify-between items-center bg-white border p-3 rounded-md shadow-sm">
                            <div>
                                <span className="font-medium">{new Date(result.date).toLocaleDateString('de-DE')}</span>: 
                                <span className="text-gray-700"> Score {result.score}</span>
                            </div>
                            <button onClick={() => onDeleteResult(result.date)} className={`${btnDanger} p-1 text-xs`}><Trash2 className="w-4 h-4" /></button>
                        </li>
                    ))}
                    {(patient.bdi2Results || []).length === 0 && <p className="text-sm text-gray-500">Keine BDI-II Ergebnisse erfasst.</p>}
                </ul>
            </div>

            {(patient.bdi2Results || []).length > 0 && (
                <div>
                    <h5 className="text-md font-semibold text-gray-600 mb-3">Verlaufsgraph BDI-II</h5>
                    <div className="bg-white p-4 rounded-lg shadow" style={{ height: '400px' }}>
                        <ResponsiveContainer width="100%" height="100%">
                            <LineChart data={(patient.bdi2Results || []).map(r => ({...r, date: new Date(r.date).toLocaleDateString('de-DE')}))} margin={{ top: 5, right: 30, left: 0, bottom: 20 }}>
                                <CartesianGrid strokeDasharray="3 3" />
                                <XAxis dataKey="date" />
                                <YAxis domain={[0, 63]} ticks={bdiAxisTicks} allowDataOverflow={true}/>
                                <Tooltip />
                                <Legend verticalAlign="top" height={36}/>
                                {bdiCutoffs.map(cutoff => (
                                    <ReferenceArea key={cutoff.label} y1={cutoff.y1} y2={cutoff.y2} fill={cutoff.color} strokeOpacity={0.3} label={<CustomBdiLabel text={cutoff.label} y={cutoff.y2 - (cutoff.y2-cutoff.y1)/2} />}/>
                                ))}
                                <Line type="monotone" dataKey="score" name="BDI-II Score" stroke="#3b82f6" strokeWidth={2} activeDot={{ r: 8 }} />
                            </LineChart>
                        </ResponsiveContainer>
                    </div>
                </div>
            )}
        </div>
    );
};

export default BDI2Component;
