import React, { useState, useEffect } from 'react';

// Icons
const SaveIcon = ({ className = "w-4 h-4" }) => <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="M19 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h11l5 5v11a2 2 0 0 1-2 2z" /><polyline points="17 21 17 13 7 13 7 21" /><polyline points="7 3 7 8 15 8" /></svg>;
const PlusIcon = ({ className = "w-4 h-4" }) => <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><line x1="12" y1="5" x2="12" y2="19"></line><line x1="5" y1="12" x2="19" y2="12"></line></svg>;
const TrashIcon = ({ className = "w-4 h-4" }) => <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><polyline points="3 6 5 6 21 6"></polyline><path d="M19 6v14a2 2 0 0 1-2 2H7a2 2 0 0 1-2-2V6m3 0V4a2 2 0 0 1 2-2h4a2 2 0 0 1 2 2v2"></path></svg>;

const BillingModal = ({ event, ebmData, onSave, onClose }) => {
    const [step, setStep] = useState(1);
    const [ebmEntries, setEbmEntries] = useState([]);
    const [newEbmCode, setNewEbmCode] = useState('');
    const [multiplier, setMultiplier] = useState(1);

    const [notes, setNotes] = useState({
        thema: '',
        patientenbericht: '',
        therapeutischeInterventionen: '',
        gesamteindruck: '',
        risikoabschaetzung: 'Auf Nachfrage wurden suizidale Gedanken, Impulse sowie Handlungsabsichten glaubhaft und eindeutig verneint. Die Absprachefähigkeit des Patienten ist gegeben. Es besteht kein Anhalt für eine akute Eigen- oder Fremdgefährdung.',
        hausaufgaben: ''
    });
    const [isSuicidal, setIsSuicidal] = useState(false);

    const riskTextStandard = 'Auf Nachfrage wurden suizidale Gedanken, Impulse sowie Handlungsabsichten glaubhaft und eindeutig verneint. Die Absprachefähigkeit des Patienten ist gegeben. Es besteht kein Anhalt für eine akute Eigen- oder Fremdgefährdung.';

    useEffect(() => {
        if(isSuicidal) {
            if (notes.risikoabschaetzung === riskTextStandard) {
                 setNotes(prev => ({ ...prev, risikoabschaetzung: '' }));
            }
        } else {
             setNotes(prev => ({ ...prev, risikoabschaetzung: riskTextStandard }));
        }
    }, [isSuicidal]);


    const handleNoteChange = (e) => {
        const { name, value } = e.target;
        setNotes(prev => ({ ...prev, [name]: value }));
    };

    const handleAddEbmEntry = () => {
        const selectedEbmItem = ebmData.find(item => item.Nummer === newEbmCode);
        if (selectedEbmItem) {
            setEbmEntries(prev => [...prev, { ...selectedEbmItem, anzahl: multiplier }]);
            setNewEbmCode('');
            setMultiplier(1);
        }
    };

    const handleDeleteEbmEntry = (index) => {
        setEbmEntries(prev => prev.filter((_, i) => i !== index));
    };

    const totalPoints = ebmEntries.reduce((sum, entry) => sum + (parseInt(entry.Punkte, 10) * entry.anzahl), 0);

    const handleSave = () => {
        const chiffreMatch = event.summary.match(/[A-Z][0-9]{6}/);
        const billingData = {
            eventId: event.id,
            eventSummary: event.summary,
            eventStart: event.start.dateTime || event.start.date,
            chiffre: chiffreMatch ? chiffreMatch[0] : null,
            ebmEntries: ebmEntries.map(e => ({ nummer: e.Nummer, anzahl: e.anzahl })),
            notes,
        };
        onSave(billingData);
    };

    const inputStd = "mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm";
    const textareaStd = `${inputStd} min-h-[100px]`;

    return (
        <div className="space-y-6">
            <div className="flex justify-center items-center space-x-4">
                <div className={`flex items-center ${step >= 1 ? 'text-indigo-600' : 'text-gray-400'}`}>
                    <div className={`w-8 h-8 rounded-full flex items-center justify-center font-bold ${step >= 1 ? 'bg-indigo-600 text-white' : 'bg-gray-200'}`}>1</div>
                    <span className="ml-2">EBM-Ziffern</span>
                </div>
                <div className={`h-1 w-16 ${step >= 2 ? 'bg-indigo-600' : 'bg-gray-200'}`}></div>
                <div className={`flex items-center ${step >= 2 ? 'text-indigo-600' : 'text-gray-400'}`}>
                    <div className={`w-8 h-8 rounded-full flex items-center justify-center font-bold ${step >= 2 ? 'bg-indigo-600 text-white' : 'bg-gray-200'}`}>2</div>
                    <span className="ml-2">Sitzungsnotiz</span>
                </div>
            </div>

            {step === 1 && (
                <div className="animate-fade-in space-y-4">
                    <h3 className="font-semibold text-lg">EBM-Ziffern hinzufügen</h3>
                    <div className="grid grid-cols-1 md:grid-cols-[1fr,auto,auto] gap-3 items-end">
                        <select value={newEbmCode} onChange={e => setNewEbmCode(e.target.value)} className={inputStd}>
                            <option value="">EBM-Ziffer auswählen...</option>
                            {ebmData.map(item => <option key={item.Nummer} value={item.Nummer}>{item.Nummer} - {item.Titel}</option>)}
                        </select>
                        <input type="number" value={multiplier} onChange={e => setMultiplier(parseInt(e.target.value, 10) || 1)} min="1" className={`${inputStd} w-20 text-center`} />
                        <button onClick={handleAddEbmEntry} className="bg-blue-500 text-white p-2 rounded-md hover:bg-blue-600"><PlusIcon /></button>
                    </div>
                    <div>
                        {ebmEntries.map((entry, index) => (
                            <div key={index} className="flex justify-between items-center bg-gray-50 p-2 rounded-md mb-2">
                                <span>{entry.anzahl} x {entry.Nummer} ({entry.Titel})</span>
                                <div className="flex items-center">
                                    <span className="mr-4 font-mono">{(parseInt(entry.Punkte, 10) * entry.anzahl)} Pkt.</span>
                                    <button onClick={() => handleDeleteEbmEntry(index)} className="text-red-500 hover:text-red-700"><TrashIcon /></button>
                                </div>
                            </div>
                        ))}
                    </div>
                    <div className="text-right font-bold text-lg">Gesamtpunkte: {totalPoints}</div>
                </div>
            )}

            {step === 2 && (
                <div className="animate-fade-in space-y-4">
                     <h3 className="font-semibold text-lg">Sitzungsdokumentation</h3>
                    <div><label className="text-sm font-medium">Thema der Sitzung</label><input name="thema" value={notes.thema} onChange={handleNoteChange} className={inputStd} /></div>
                    <div><label className="text-sm font-medium">Patientenbericht</label><textarea name="patientenbericht" value={notes.patientenbericht} onChange={handleNoteChange} className={textareaStd} /></div>
                    <div><label className="text-sm font-medium">Therapeutische Interventionen</label><textarea name="therapeutischeInterventionen" value={notes.therapeutischeInterventionen} onChange={handleNoteChange} className={textareaStd} /></div>
                    <div><label className="text-sm font-medium">Gesamteindruck</label><textarea name="gesamteindruck" value={notes.gesamteindruck} onChange={handleNoteChange} className={textareaStd} /></div>
                    <div>
                        <label className="text-sm font-medium">Risikoabschätzung (Suizidalität)</label>
                        <div className="flex items-center space-x-4 mt-2">
                             <label><input type="radio" checked={!isSuicidal} onChange={() => setIsSuicidal(false)} className="mr-1"/> Nein</label>
                             <label><input type="radio" checked={isSuicidal} onChange={() => setIsSuicidal(true)} className="mr-1"/> Ja, Interventionsbedarf</label>
                        </div>
                        <textarea name="risikoabschaetzung" value={notes.risikoabschaetzung} onChange={handleNoteChange} disabled={!isSuicidal} className={`${textareaStd} mt-2 ${!isSuicidal ? 'bg-gray-100' : ''}`} />
                    </div>
                    <div><label className="text-sm font-medium">Hausaufgaben</label><input name="hausaufgaben" value={notes.hausaufgaben} onChange={handleNoteChange} className={inputStd} /></div>
                </div>
            )}

            <div className="flex justify-between pt-4 border-t">
                <button onClick={() => step > 1 ? setStep(step - 1) : onClose()} className="px-4 py-2 text-sm font-medium text-gray-700 bg-gray-200 rounded-md hover:bg-gray-300">
                    {step === 1 ? 'Abbrechen' : 'Zurück'}
                </button>
                {step === 1 && <button onClick={() => setStep(2)} className="px-4 py-2 text-sm font-medium text-white bg-indigo-600 rounded-md hover:bg-indigo-700">Weiter</button>}
                {step === 2 && <button onClick={handleSave} className="px-4 py-2 text-sm font-medium text-white bg-green-600 rounded-md hover:bg-green-700 flex items-center"><SaveIcon className="mr-2"/> Abrechnung speichern</button>}
            </div>
        </div>
    );
};

// WICHTIG: Stellen Sie sicher, dass dies die einzige Export-Anweisung in der Datei ist.
export default BillingModal;
