import React, { useState, useEffect } from 'react';

// Import the official, specialized entry forms using the correct path
import { Bdi2EntryForm } from '../psychodiagnostic/BDI2.js';
import { FEP2EntryForm } from '../psychodiagnostic/FEP2.js';
import { HSCL11EntryForm } from '../psychodiagnostic/HSCL11.js';
import { SCL90REntryForm } from '../psychodiagnostic/SCL90R.js';

// Icons
const SaveIcon = ({ className = "w-4 h-4" }) => <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="M19 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h11l5 5v11a2 2 0 0 1-2 2z" /><polyline points="17 21 17 13 7 13 7 21" /><polyline points="7 3 7 8 15 8" /></svg>;
const XCircle = ({ className = "w-6 h-6" }) => (<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><circle cx="12" cy="12" r="10" /><line x1="15" y1="9" x2="9" y2="15" /><line x1="9" y1="9" x2="15" y2="15" /></svg>);

const ZentraleTestErfassungModal = ({ isOpen, onClose, onSave, testInfo, patients, patient }) => {
    const [selectedPatientId, setSelectedPatientId] = useState('');
    const [testDate, setTestDate] = useState(new Date().toISOString().split('T')[0]);
    // This state will now be populated by the official EntryForm's onSave callback
    const [stagedResults, setStagedResults] = useState(null);

    useEffect(() => {
        if (isOpen) {
            setSelectedPatientId('');
            setTestDate(new Date().toISOString().split('T')[0]);
            setStagedResults(null);
        }
    }, [isOpen]);

    if (!isOpen || !testInfo) return null;

    const handleSave = () => {
        if (!selectedPatientId) {
            alert("Bitte wählen Sie einen Patienten aus.");
            return;
        }
        if (!stagedResults) {
            alert("Bitte geben Sie die Testergebnisse ein und übernehmen Sie diese, bevor Sie speichern.");
            return;
        }

        const testData = {
            testId: testInfo.id,
            results: stagedResults,
            date: testDate
        };

        onSave(selectedPatientId, testData);
        onClose();
    };

    // This function renders the correct, specialized form for the selected test
    const renderTestEntryForm = () => {
        // The `onSave` prop of the specialized forms will be used to stage the results.
        const handleResultsStaged = (results) => {
            setStagedResults(results);
            alert("Ergebnisse wurden zur Speicherung übernommen.\nBitte klicken Sie unten auf 'Testergebnis speichern', um den Vorgang abzuschließen.");
        };

        const patientAge = selectedPatientId ? (patients.find(p=>p.id === selectedPatientId)?.geburtsdatum ? (new Date().getFullYear() - new Date(patients.find(p=>p.id === selectedPatientId).geburtsdatum).getFullYear()) : '') : '';
        const patientGender = selectedPatientId ? (patients.find(p=>p.id === selectedPatientId)?.gender || '1') : '1';

        switch (testInfo.id) {
            case 'hscl11':
                return <HSCL11EntryForm onSave={handleResultsStaged} />;
            case 'bdi2':
                return <Bdi2EntryForm onSave={handleResultsStaged} />;
            case 'fep2':
                return <FEP2EntryForm onSave={handleResultsStaged} />;
            case 'scl90r':
                return <SCL90REntryForm onSave={handleResultsStaged} patientAge={patientAge} patientGender={patientGender} />;
            default:
                return (
                    <div className="text-center p-4 bg-gray-50 rounded-md">
                        <p className="text-sm text-gray-600">Für diesen Test ist kein spezifisches Eingabeformular vorhanden.</p>
                        <button
                            onClick={() => handleResultsStaged({ placeholder: true, score: 0 })}
                            className="mt-2 px-3 py-1 text-sm text-white bg-blue-600 rounded-md hover:bg-blue-700"
                        >
                            Als durchgeführt markieren
                        </button>
                    </div>
                );
        }
    };


    const inputStyle = "block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-teal-500 sm:text-sm";
    const btnPrimary = "bg-teal-600 hover:bg-teal-700 text-white font-semibold py-2 px-4 rounded-md shadow-sm flex items-center justify-center transition-colors disabled:bg-gray-400";

    return (
        <div className="fixed inset-0 bg-black bg-opacity-60 backdrop-blur-sm flex justify-center items-center z-[99] p-4">
            <div className="bg-white p-6 rounded-lg shadow-xl w-full max-w-4xl">
                <div className="flex justify-between items-center mb-4">
                    <h2 className="text-2xl font-semibold text-gray-800">Test erfassen: {testInfo.name}</h2>
                    <button onClick={onClose} className="text-gray-500 hover:text-gray-700">
                        <XCircle className="w-7 h-7" />
                    </button>
                </div>
                <div className="space-y-6">
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                        <div>
                            <label htmlFor="patient-select" className="block text-sm font-medium text-gray-700">Patient*in</label>
                            <select
                                id="patient-select"
                                value={selectedPatientId}
                                onChange={(e) => setSelectedPatientId(e.target.value)}
                                className={inputStyle}
                            >
                                <option value="" disabled>Bitte wählen...</option>
                                {patients.map(p => (
                                    <option key={p.id} value={p.id}>{p.name}, {p.vorname} ({p.chiffre})</option>
                                ))}
                            </select>
                        </div>
                        <div>
                            <label htmlFor="test-date" className="block text-sm font-medium text-gray-700">Testdatum</label>
                            <input
                                type="date"
                                id="test-date"
                                value={testDate}
                                onChange={(e) => setTestDate(e.target.value)}
                                className={inputStyle}
                            />
                        </div>
                    </div>
                    <div className="border-t pt-4">
                         <label className="block text-sm font-medium text-gray-700 mb-2">Ergebnisse</label>
                         {selectedPatientId ? (
                            renderTestEntryForm()
                         ) : (
                            <p className="text-sm text-center text-gray-500 p-4">Bitte wählen Sie zuerst einen Patienten aus, um die Ergebnisse einzugeben.</p>
                         )}
                    </div>
                </div>
                <div className="flex justify-end space-x-3 pt-6 mt-6 border-t">
                    <button type="button" onClick={onClose} className="px-4 py-2 text-sm font-medium text-gray-700 bg-gray-100 rounded-md hover:bg-gray-200">Abbrechen</button>
                    <button
                        onClick={handleSave}
                        disabled={!selectedPatientId || !stagedResults}
                        className={btnPrimary}
                        title={!stagedResults ? "Bitte zuerst Ergebnisse eingeben und übernehmen" : "Testergebnis für den ausgewählten Patienten speichern"}
                    >
                        <SaveIcon className="w-4 h-4 mr-2" /> Testergebnis speichern
                    </button>
                </div>
            </div>
        </div>
    );
};

export default ZentraleTestErfassungModal;
