import React, { useState, useMemo } from 'react';

// --- Icons (können bei Bedarf aus einer zentralen Datei importiert werden) ---
const FileTextIcon = ({ className = "w-5 h-5" }) => (<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="M14.5 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V7.5L14.5 2z" /><polyline points="14 2 14 8 20 8" /><line x1="16" y1="13" x2="8" y2="13" /><line x1="16" y1="17" x2="8" y2="17" /><line x1="10" y1="9" x2="8" y2="9" /></svg>);
const XCircle = ({ className = "w-6 h-6" }) => (<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><circle cx="12" cy="12" r="10" /><line x1="15" y1="9" x2="9" y2="15" /><line x1="9" y1="9" x2="15" y2="15" /></svg>);

// --- PTV 11 Formular-Komponente ---
const Ptv11Form = ({ patient, onGenerate }) => {
    // In einer echten Anwendung würden diese Werte aus einem State oder Props kommen
    const [formData, setFormData] = useState({
        symptomatik: '',
        diagnostikErgebnisse: '',
        behandlungsplan: '',
        prognose: '',
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    // Automatisch generierter Text aus Diagnosen und Tests
    const autoDiagnostics = useMemo(() => {
        if (!patient) return '';
        const diagnoses = (patient.diagnoses || []).join('\n');
        const tests = (patient.bdi2Results || [])
            .map(r => `BDI-II (${new Date(r.date).toLocaleDateString()}): Score ${r.results?.score}`)
            .join('\n');
        return `Diagnosen:\n${diagnoses}\n\nRelevante Testergebnisse:\n${tests}`;
    }, [patient]);


    if (!patient) {
        return <div className="text-center text-gray-500 p-8">Bitte wählen Sie oben einen Patienten aus.</div>;
    }

    const inputStyle = "block w-full px-3 py-2 bg-gray-50 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-teal-500 sm:text-sm";
    const textareaStyle = `${inputStyle} min-h-[100px]`;

    return (
        <div className="p-4 border-t">
            <h3 className="text-lg font-semibold text-gray-800 mb-4">Angaben zur Indikation und Behandlungsplanung (PTV 11)</h3>

            {/* Patientendaten (automatisch gefüllt) */}
            <div className="grid grid-cols-2 gap-4 p-4 mb-4 bg-blue-50 border border-blue-200 rounded-lg">
                <div><p className="text-sm font-medium text-gray-600">Patient/in</p><p className="font-semibold">{patient.vorname} {patient.name}</p></div>
                <div><p className="text-sm font-medium text-gray-600">Geburtsdatum</p><p className="font-semibold">{new Date(patient.geburtsdatum).toLocaleDateString('de-DE')}</p></div>
                <div><p className="text-sm font-medium text-gray-600">Krankenkasse</p><p className="font-semibold">{patient.krankenkasse}</p></div>
                <div><p className="text-sm font-medium text-gray-600">Vers.-Nr.</p><p className="font-semibold">{patient.versichertennummer}</p></div>
            </div>

            {/* Formularfelder zum Ausfüllen */}
            <div className="space-y-4">
                <div>
                    <label htmlFor="symptomatik" className="block text-sm font-medium text-gray-700">Symptomatik, psychodynamische/verhaltensanalytische Problembeschreibung</label>
                    <textarea id="symptomatik" name="symptomatik" value={formData.symptomatik} onChange={handleChange} className={textareaStyle} />
                </div>
                <div>
                    <label htmlFor="diagnostikErgebnisse" className="block text-sm font-medium text-gray-700">Diagnosen und Ergebnisse der probatorischen Sitzungen</label>
                    <textarea id="diagnostikErgebnisse" name="diagnostikErgebnisse" value={autoDiagnostics} readOnly className={`${textareaStyle} bg-gray-100`} />
                </div>
                 <div>
                    <label htmlFor="behandlungsplan" className="block text-sm font-medium text-gray-700">Behandlungsplan mit Zielsetzung</label>
                    <textarea id="behandlungsplan" name="behandlungsplan" value={formData.behandlungsplan} onChange={handleChange} className={textareaStyle} />
                </div>
                 <div>
                    <label htmlFor="prognose" className="block text-sm font-medium text-gray-700">Prognose</label>
                    <textarea id="prognose" name="prognose" value={formData.prognose} onChange={handleChange} className={textareaStyle} />
                </div>
            </div>
            <div className="flex justify-end mt-6">
                <button onClick={() => onGenerate({ ...formData, patientDaten: patient })} className="px-6 py-2 text-white bg-green-600 rounded-md hover:bg-green-700 font-semibold">
                    Formular generieren
                </button>
            </div>
        </div>
    );
};


// --- Hauptkomponente für die Formular-Seite ---
const PtvFormGenerator = ({ patients }) => {
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [selectedPatientId, setSelectedPatientId] = useState('');

    const selectedPatient = useMemo(() => 
        patients.find(p => p.id === selectedPatientId),
        [selectedPatientId, patients]
    );

    const handleOpenModal = () => {
        setIsModalOpen(true);
    };

    const handleCloseModal = () => {
        setIsModalOpen(false);
        setSelectedPatientId('');
    };

    const handleGenerateForm = (formData) => {
        console.log("Formulardaten zum Generieren:", formData);
        alert(`PTV 11 für ${formData.patientDaten.name} wurde generiert. Siehe Konsole für Details.`);
        handleCloseModal();
    };

    const formTemplates = [
        { id: 'ptv11', name: 'PTV 11 - Angaben des Therapeuten', description: 'Standardformular für den Therapieantrag.' },
        // Hier könnten weitere Vorlagen hinzugefügt werden
    ];

    return (
        <div className="w-full max-w-4xl mx-auto">
            <h3 className="text-2xl font-semibold text-gray-800 mb-4">Formular-Vorlagen</h3>
            <div className="bg-white p-6 border border-gray-200 rounded-lg shadow-sm">
                <div className="space-y-3">
                    {formTemplates.map(template => (
                        <div key={template.id} className="p-4 border rounded-lg flex items-center justify-between hover:bg-gray-50">
                            <div className="flex items-center">
                                <FileTextIcon className="text-teal-500 mr-4" />
                                <div>
                                    <p className="font-semibold text-gray-800">{template.name}</p>
                                    <p className="text-sm text-gray-500">{template.description}</p>
                                </div>
                            </div>
                            <button onClick={handleOpenModal} className="px-4 py-2 text-sm font-medium text-white bg-teal-600 rounded-md hover:bg-teal-700">
                                Ausfüllen
                            </button>
                        </div>
                    ))}
                </div>
            </div>

            {/* Modal zum Ausfüllen des Formulars */}
            {isModalOpen && (
                 <div className="fixed inset-0 bg-black bg-opacity-50 backdrop-blur-sm flex justify-center items-center z-50 p-4">
                    <div className="bg-white rounded-lg shadow-xl w-full max-w-4xl max-h-[90vh] overflow-y-auto">
                        <div className="flex justify-between items-center p-4 border-b">
                            <h2 className="text-xl font-semibold text-gray-800">PTV 11 Generator</h2>
                            <button onClick={handleCloseModal} className="text-gray-400 hover:text-gray-600"><XCircle /></button>
                        </div>
                        <div className="p-4">
                             <label htmlFor="patient-select" className="block text-sm font-medium text-gray-700">1. Patient auswählen</label>
                             <select
                                id="patient-select"
                                value={selectedPatientId}
                                onChange={(e) => setSelectedPatientId(e.target.value)}
                                className="mt-1 block w-full pl-3 pr-10 py-2 text-base border-gray-300 focus:outline-none focus:ring-teal-500 focus:border-teal-500 sm:text-sm rounded-md"
                             >
                                <option value="">-- Bitte wählen --</option>
                                {patients.map(p => (
                                    <option key={p.id} value={p.id}>{p.name}, {p.vorname} ({p.chiffre})</option>
                                ))}
                             </select>
                        </div>
                        <Ptv11Form patient={selectedPatient} onGenerate={handleGenerateForm} />
                    </div>
                </div>
            )}
        </div>
    );
};

export default PtvFormGenerator;
