import { motion, AnimatePresence } from 'framer-motion';

// --- ICONS ---
const PlusCircle = ({ className = "w-5 h-5" }) => <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><circle cx="12" cy="12" r="10"></circle><line x1="12" y1="8" x2="12" y2="16"></line><line x1="8" y1="12" x2="16" y2="12"></line></svg>;
const ChevronRight = ({ className = "w-5 h-5" }) => <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><polyline points="9 18 15 12 9 6"></polyline></svg>;
const CheckCircle = ({ className = "w-5 h-5" }) => <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="M22 11.08V12a10 10 0 1 1-5.93-9.14"></path><polyline points="22 4 12 14.01 9 11.01"></polyline></svg>;
const GripVertical = ({ className = "w-5 h-5" }) => <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><circle cx="9" cy="12" r="1"></circle><circle cx="9" cy="5" r="1"></circle><circle cx="9" cy="19" r="1"></circle><circle cx="15" cy="12" r="1"></circle><circle cx="15" cy="5" r="1"></circle><circle cx="15" cy="19" r="1"></circle></svg>;


// --- DRAG-AND-DROP COMPONENTS ---

const ItemTypes = {
    TEST: 'test',
};

const DraggableTest = ({ test }) => {
    const [{ isDragging }, drag] = useDrag(() => ({
        type: ItemTypes.TEST,
        item: test,
        collect: (monitor) => ({
            isDragging: !!monitor.isDragging(),
        }),
    }));

    return (
        <div
            ref={drag}
            style={{ opacity: isDragging ? 0.5 : 1 }}
            className="p-2.5 bg-white rounded-lg border border-gray-200 shadow-sm cursor-grab active:cursor-grabbing flex items-center gap-2"
        >
            <GripVertical className="text-gray-400" />
            <div>
                <p className="font-semibold text-sm text-gray-800">{test.name}</p>
                <p className="text-xs text-gray-500">
                    Vom: {new Date(test.date).toLocaleDateString('de-DE')}
                </p>
            </div>
        </div>
    );
};

const DropZone = ({ period, onDropTest, assignedTests, diagnostikAnalysis, children, className }) => {
    const [{ isOver, canDrop }, drop] = useDrop(() => ({
        accept: ItemTypes.TEST,
        drop: (item) => onDropTest(item, period.id),
        collect: (monitor) => ({
            isOver: !!monitor.isOver(),
            canDrop: !!monitor.canDrop(),
        }),
    }));

    const analysis = period.id !== 'unassigned' && period.id !== 'frei' 
        ? (diagnostikAnalysis[period.id] || { pointsUsed: 0, pointsLimit: 0 })
        : null;

    const isActive = isOver && canDrop;

    return (
        <div
            ref={drop}
            className={`${className} rounded-xl border-2 transition-all duration-200 ${
                isActive ? 'border-teal-500 bg-teal-50' : 'border-dashed border-gray-300'
            }`}
        >
            <div className="p-4">
                 <h4 className="font-bold text-gray-700">{period.name}</h4>
                 {analysis && (
                     <>
                        <p className="text-xs text-gray-500 mb-2">Datum: {new Date(period.date).toLocaleDateString('de-DE')}</p>
                        <div className="w-full bg-gray-200 rounded-full h-2.5 mt-1">
                            <div className="bg-teal-500 h-2.5 rounded-full" style={{ width: `${Math.min((analysis.pointsUsed / analysis.pointsLimit) * 100, 100)}%` }}></div>
                        </div>
                        <p className="text-xs text-gray-600 mt-1 text-right font-mono">{analysis.pointsUsed} / {analysis.pointsLimit} Punkte</p>
                     </>
                 )}
                 {period.id === 'frei' && <p className="text-xs text-gray-500">Tests hier ablegen, die nicht abgerechnet werden sollen.</p>}
            </div>
            <div className="p-2 space-y-2 min-h-[80px]">
                {assignedTests.map(test => (
                    <DraggableTest key={test.testInstanceId} test={test} />
                ))}
                {children}
            </div>
        </div>
    );
};

// --- MAIN COMPONENT ---

const DiagnostikZuordnung = ({ patient, availableTests, onAddTest, onShowTestDetails, onAssignTestToPeriod, diagnostikAnalysis }) => {
    const [showSuccess, setShowSuccess] = useState(null);

    // Leite alle Testdaten direkt aus den Props ab. Dies ist die "Single Source of Truth".
    const allTests = useMemo(() => {
        const tests = [];
        availableTests.forEach(testDef => {
            const results = patient[`${testDef.id}Results`] || [];
            results.forEach(result => {
                tests.push({
                    ...testDef,
                    ...result,
                    testType: testDef.id,
                    billingPeriodId: result.billingPeriodId === undefined ? null : result.billingPeriodId,
                });
            });
        });
        return tests.sort((a, b) => new Date(b.date) - new Date(a.date));
    }, [patient, availableTests]);

    const handleDropTest = (test, targetPeriodId) => {
        // Rufe direkt die Funktion in App.js auf, um die Änderung in Firestore zu speichern.
        // Die UI wird sich automatisch aktualisieren, wenn der 'patient'-Prop aktualisiert wird.
        onAssignTestToPeriod(test.testInstanceId, test.testType, targetPeriodId);

        // Visuelles Feedback für den Benutzer anzeigen.
        setShowSuccess(`Test "${test.name}" wurde zugeordnet.`);
        setTimeout(() => setShowSuccess(null), 2500);
    };

    const conductedTests = useMemo(() => {
        return availableTests.map(test => {
            const results = patient[`${test.id}Results`] || [];
            return { ...test, count: results.length, results };
        }).filter(test => test.count > 0);
    }, [patient, availableTests]);

    // Leite die Tests für die verschiedenen Zonen direkt von `allTests` ab.
    const unassignedTests = useMemo(() => allTests.filter(t => t.billingPeriodId === null), [allTests]);
    const freiTests = useMemo(() => allTests.filter(t => t.billingPeriodId === 'frei'), [allTests]);

    const billingPeriodsWithTests = useMemo(() => {
        const periods = (patient?.billingPeriods || []).map(p => ({
            ...p,
            assignedTests: allTests.filter(t => t.billingPeriodId === p.id)
        }));
        return periods;
    }, [patient, allTests]);

    const dropZones = [
        ...billingPeriodsWithTests,
        { id: 'frei', name: 'Abrechnungsfrei', assignedTests: freiTests }
    ];

    return (
        <DndProvider backend={HTML5Backend}>
            <div className="space-y-8">
                {/* 1. Testdiagnostik Zuordnung */}
                <div>
                    <h3 className="text-xl font-bold text-gray-800 mb-4">Testdiagnostik Zuordnung</h3>
                    <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 p-4 bg-white rounded-2xl shadow-sm border">
                        {/* Linke Spalte: Abrechnungszeiträume */}
                        <div className="lg:col-span-2 grid grid-cols-1 md:grid-cols-2 gap-4">
                            {dropZones.map(period => (
                                <DropZone
                                    key={period.id}
                                    period={period}
                                    onDropTest={handleDropTest}
                                    assignedTests={period.assignedTests}
                                    diagnostikAnalysis={diagnostikAnalysis}
                                    className="bg-gray-50"
                                />
                            ))}
                        </div>

                        {/* Rechte Spalte: Nicht zugeordnete Tests */}
                        <div
                            className="bg-slate-50 p-4 rounded-xl border min-h-[200px]"
                        >
                            <DropZone
                                period={{ id: null, name: 'Nicht zugeordnete Tests' }}
                                onDropTest={handleDropTest}
                                assignedTests={[]} // This zone receives, doesn't hold
                                diagnostikAnalysis={diagnostikAnalysis}
                                className="bg-slate-100 border-slate-300"
                            >
                                {unassignedTests.length > 0 ? (
                                    <div className="space-y-2">
                                        {unassignedTests.map(test => (
                                            <DraggableTest key={test.testInstanceId} test={test} />
                                        ))}
                                    </div>
                                ) : (
                                    <div className="flex items-center justify-center h-full min-h-[100px]">
                                        <p className="text-sm text-gray-500 text-center p-4">Alle Tests sind zugeordnet. Sehr gut!</p>
                                    </div>
                                )}
                            </DropZone>
                        </div>
                    </div>
                    {/* Success Message */}
                    <AnimatePresence>
                        {showSuccess && (
                            <motion.div
                                initial={{ opacity: 0, y: 50, scale: 0.3 }}
                                animate={{ opacity: 1, y: 0, scale: 1 }}
                                exit={{ opacity: 0, scale: 0.5, transition: { duration: 0.2 } }}
                                className="fixed bottom-4 right-4 flex items-center p-4 bg-green-600 text-white rounded-lg shadow-2xl z-50"
                            >
                                <CheckCircle className="mr-3" /> {showSuccess}
                            </motion.div>
                        )}
                    </AnimatePresence>
                </div>

                {/* 2. Durchgeführte Diagnostik */}
                <div>
                    <h3 className="text-xl font-bold text-gray-800 mb-4">Durchgeführte Testdiagnostik (Alle)</h3>
                    {conductedTests.length > 0 ? (
                        <div className="space-y-3">
                            {conductedTests.map(test => (
                                <div key={test.id} className="bg-white p-4 rounded-lg border shadow-sm flex justify-between items-center">
                                    <div>
                                        <p className="font-semibold text-gray-800">{test.name}</p>
                                        <p className="text-sm text-gray-500">{test.count} mal durchgeführt</p>
                                    </div>
                                    <button onClick={() => onShowTestDetails(test.id)} className="text-teal-600 hover:text-teal-800 font-semibold text-sm flex items-center">
                                        Details <ChevronRight />
                                    </button>
                                </div>
                            ))}
                        </div>
                    ) : <p className="text-sm text-gray-500 p-4 bg-gray-50 rounded-lg">Noch keine Tests für diesen Patienten durchgeführt.</p>}
                </div>

                {/* 3. Neuen Test hinzufügen */}
                <div>
                    <h3 className="text-xl font-bold text-gray-800 mb-4">Neuen Test hinzufügen</h3>
                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                        {availableTests.map(test => (
                            <div key={test.id} className="bg-white p-4 rounded-lg border flex justify-between items-center">
                                <div>
                                    <p className="font-semibold text-gray-800">{test.name}</p>
                                    <p className="text-xs text-gray-500">EBM: {test.ebm}</p>
                                </div>
                                <button onClick={() => onAddTest(test)} className="text-teal-600 hover:bg-teal-100 p-2 rounded-full transition-colors">
                                    <PlusCircle />
                                </button>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </DndProvider>
    );
};

export default DiagnostikZuordnung;
