import React, { useState, useMemo } from 'react';

// Icons
const EditIcon = ({ className = "w-4 h-4" }) => <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="M11 4H4a2 2 0 0 0-2 2v14a2 2 0 0 0 2 2h14a2 2 0 0 0 2-2v-7"></path><path d="M18.5 2.5a2.121 2.121 0 0 1 3 3L12 15l-4 1 1-4 9.5-9.5z"></path></svg>;
const TrashIcon = ({ className = "w-4 h-4" }) => <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><polyline points="3 6 5 6 21 6"></polyline><path d="M19 6v14a2 2 0 0 1-2 2H7a2 2 0 0 1-2-2V6m3 0V4a2 2 0 0 1 2-2h4a2 2 0 0 1 2 2v2"></path></svg>;

const EBM_PUNKTWERT_EURO = 0.123934;

const AbrechnungsUebersicht = ({ patient, ebmData, onEditSession, onDeleteSession }) => {
    const [selectedSession, setSelectedSession] = useState(null);

    const calculateSessionRevenue = (session) => {
        if (!session.ebmEntries || ebmData.length === 0) return { totalEuro: 0 };
        let totalEuro = 0;
        session.ebmEntries.forEach(entry => {
            const ebmInfo = ebmData.find(e => e.Nummer === entry.nummer);
            if (ebmInfo) {
                const punkte = parseFloat(ebmInfo.Punkte) || 0;
                const euroValue = punkte * EBM_PUNKTWERT_EURO;
                totalEuro += euroValue * (entry.anzahl || 1) * (entry.faktor || 1);
            }
        });
        return { totalEuro };
    };

    const { therapySessions, testSessions } = useMemo(() => {
        const allSessions = (patient.sessions || []).sort((a, b) => new Date(b.date) - new Date(a.date));
        const therapySessions = allSessions.filter(s => s.structuredNotes && s.structuredNotes.sitzungsnummer);
        const testSessions = allSessions.filter(s => s.structuredNotes && s.structuredNotes.thema?.startsWith('Testdiagnostik'));
        return { therapySessions, testSessions };
    }, [patient.sessions]);

    const sessionDetails = selectedSession ? calculateSessionRevenue(selectedSession) : { totalEuro: 0 };

    const renderSessionList = (sessions, title, type) => (
        <div>
            <h4 className="text-lg font-bold text-gray-800 mb-2">{title}</h4>
            <div className="space-y-1 max-h-[40vh] overflow-y-auto pr-2">
                {sessions.length > 0 ? sessions.map((session) => (
                    <div 
                        key={session.id} 
                        onClick={() => setSelectedSession(session)} 
                        className={`p-2 rounded-md cursor-pointer text-xs transition-colors flex justify-between items-center ${selectedSession?.id === session.id ? 'bg-teal-600 text-white' : 'bg-gray-100 hover:bg-teal-50 text-gray-800'}`}
                    >
                        <div>
                            <p className="font-semibold">
                                {type === 'therapy' ? `Sitzung ${session.structuredNotes.sitzungsnummer}` : 'Testung'} - {new Date(session.date).toLocaleDateString('de-DE')}
                            </p>
                            <p className={selectedSession?.id === session.id ? 'text-teal-200' : 'text-gray-500'}>{session.structuredNotes?.thema || 'Sitzung'}</p>
                        </div>
                        <div className="flex items-center flex-shrink-0">
                             <button onClick={(e) => { e.stopPropagation(); onEditSession(session); }} className="p-1 hover:bg-white/20 rounded-full"><EditIcon className="w-4 h-4"/></button>
                             <button onClick={(e) => { e.stopPropagation(); onDeleteSession(session.id); }} className="p-1 hover:bg-white/20 rounded-full"><TrashIcon className="w-4 h-4"/></button>
                        </div>
                    </div>
                )) : <p className="text-xs text-gray-500 p-3 bg-gray-50 rounded-md">Keine {title.toLowerCase()} erfasst.</p>}
            </div>
        </div>
    );

    return (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div className="md:col-span-1 space-y-6">
                {renderSessionList(testSessions, "Abgerechnete Testdiagnostik", "test")}
                {renderSessionList(therapySessions, "Abgerechnete Therapiesitzungen", "therapy")}
            </div>

            <div className="md:col-span-2">
                {selectedSession ? (
                    <div className="bg-white p-4 rounded-lg shadow-sm border space-y-4">
                        <div className="flex justify-between items-center">
                            <h4 className="text-lg font-semibold text-gray-700">Details für Termin vom {new Date(selectedSession.date).toLocaleDateString('de-DE')}</h4>
                        </div>

                        <div className="bg-green-50 border border-green-200 rounded-lg p-3">
                            <h5 className="font-semibold text-green-800 mb-2">Abgerechnete Leistungen</h5>
                            <table className="w-full text-xs">
                                <tbody>
                                    {(selectedSession.ebmEntries || []).map((entry, i) => (
                                        <tr key={i} className="border-b last:border-b-0 border-green-100">
                                            <td className="py-1 font-mono">{entry.nummer}</td>
                                            <td className="py-1 text-gray-600">{(ebmData.find(e => e.Nummer === entry.nummer) || {}).Titel}</td>
                                            <td className="py-1 text-right">{entry.anzahl > 1 ? `x${entry.anzahl}` : ''}</td>
                                        </tr>
                                    ))}
                                </tbody>
                                <tfoot>
                                    <tr className="font-bold">
                                        <td colSpan="2" className="pt-2 text-right">Gesamt:</td>
                                        <td className="pt-2 text-right">{sessionDetails.totalEuro.toFixed(2)} €</td>
                                    </tr>
                                </tfoot>
                            </table>
                        </div>

                        {selectedSession.structuredNotes && 
                        <div className="space-y-3">
                            {Object.entries(selectedSession.structuredNotes).map(([key, value]) => (
                                <div key={key}>
                                    <h6 className="font-semibold text-sm text-gray-600 capitalize">{key.replace(/([A-Z])/g, ' $1').trim()}</h6>
                                    {Array.isArray(value) ? (
                                        <ul className="list-disc list-inside pl-4 text-sm text-gray-800 bg-gray-50 p-2 rounded-md mt-1">
                                            {value.map((item, i) => item && <li key={i}>{item}</li>)}
                                        </ul>
                                    ) : (
                                        <p className="text-sm text-gray-800 bg-gray-50 p-2 rounded-md mt-1 whitespace-pre-wrap">{String(value)}</p>
                                    )}
                                </div>
                            ))}
                        </div>}
                    </div>
                ) : (
                    <div className="flex items-center justify-center h-full bg-gray-50 rounded-lg border">
                        <p className="text-gray-500">Wählen Sie links einen Termin aus, um die Details anzuzeigen.</p>
                    </div>
                )}
            </div>
        </div>
    );
};

export default AbrechnungsUebersicht;
