import React, { useMemo, useState, useEffect } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, AreaChart, Area } from 'recharts';

// --- ICONS (Behalte deine Icons oder füge diese hinzu) ---
const DollarSignIcon = ({ className = "w-6 h-6" }) => (<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><line x1="12" y1="1" x2="12" y2="23"></line><path d="M17 5H9.5a3.5 3.5 0 0 0 0 7h5a3.5 3.5 0 0 1 0 7H6"></path></svg>);
const UsersIcon = ({ className = "w-6 h-6" }) => (<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="M16 21v-2a4 4 0 0 0-4-4H6a4 4 0 0 0-4 4v2"></path><circle cx="9" cy="7" r="4"></circle><path d="M22 21v-2a4 4 0 0 0-3-3.87"></path><path d="M16 3.13a4 4 0 0 1 0 7.75"></path></svg>);
const ClockIcon = ({ className = "w-6 h-6" }) => (<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><circle cx="12" cy="12" r="10"></circle><polyline points="12 6 12 12 16 14"></polyline></svg>);
const CalendarIcon = ({ className = "w-6 h-6" }) => (<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><rect x="3" y="4" width="18" height="18" rx="2" ry="2"></rect><line x1="16" y1="2" x2="16" y2="6"></line><line x1="8" y1="2" x2="8" y2="6"></line><line x1="3" y1="10" x2="21" y2="10"></line></svg>);

// --- Hilfsfunktionen ---
const getStartOfWeek = (date) => {
    const d = new Date(date);
    const day = d.getDay();
    const diff = d.getDate() - day + (day === 0 ? -6 : 1);
    return new Date(d.setDate(diff));
};

// --- Komponenten ---
const StatCard = ({ title, value, icon, colorClass }) => (
    <div className="bg-white p-6 rounded-lg shadow-sm flex items-center space-x-4">
        <div className={`p-3 rounded-full ${colorClass}`}>
            {icon}
        </div>
        <div>
            <p className="text-sm font-medium text-gray-500">{title}</p>
            <p className="text-2xl font-bold text-gray-800">{value}</p>
        </div>
    </div>
);

const Dashboard = ({ patients, profile }) => {
    const [upcomingAppointments, setUpcomingAppointments] = useState([]);

    // Annahme: Dein Backend kann zukünftige Termine liefern
    useEffect(() => {
        const fetchAppointments = async () => {
            try {
                const response = await fetch('http://localhost:3001/api/calendar/upcoming?days=7'); // Holt Termine für die nächsten 7 Tage
                if (!response.ok) throw new Error('Could not fetch appointments');
                const events = await response.json();
                setUpcomingAppointments(events);
            } catch (error) {
                console.error("Error fetching upcoming appointments:", error);
            }
        };
        fetchAppointments();
    }, []);

    const dashboardData = useMemo(() => {
        const data = {
            revenueToday: 0,
            revenueWeek: 0,
            totalPatients: 0,
            totalConductedSessions: 0,
            monthlySessions: Array(12).fill(0).map((_, i) => ({ name: new Date(0, i).toLocaleString('de-DE', { month: 'short' }), "Sitzungen": 0 })),
        };

        const now = new Date();
        const today = now.toDateString();
        const startOfWeek = getStartOfWeek(now);
        const punktwert = profile.punktwert || 0.116; 

        data.totalPatients = patients.length;

        patients.forEach(patient => {
            if (patient.sessions && patient.sessions.length > 0) {
                 const conductedSessionsCount = Math.max(0, ...patient.sessions.map(s => s.structuredNotes?.sitzungsnummer || 0));
                 data.totalConductedSessions += conductedSessionsCount;

                patient.sessions.forEach(session => {
                    const sessionDate = new Date(session.date);
                    const sessionRevenue = (session.ebmEntries || [])
                        .reduce((sum, entry) => sum + (entry.punkte || 0), 0) * punktwert;

                    if (sessionDate.toDateString() === today) {
                        data.revenueToday += sessionRevenue;
                    }
                    if (sessionDate >= startOfWeek) {
                        data.revenueWeek += sessionRevenue;
                    }
                    if (sessionDate.getFullYear() === now.getFullYear()) {
                        const monthIndex = sessionDate.getMonth();
                        data.monthlySessions[monthIndex]["Sitzungen"]++;
                    }
                });
            }
        });

        return data;
    }, [patients, profile]);

    // Begrüßung basierend auf der Tageszeit
    const getGreeting = () => {
        const hour = new Date().getHours();
        if (hour < 12) return "Guten Morgen";
        if (hour < 18) return "Guten Tag";
        return "Guten Abend";
    };

    return (
        <div className="p-2 space-y-6 bg-slate-50 min-h-full">
            {/* Header */}
            <div className="flex justify-between items-center">
                <div>
                    <h1 className="text-3xl font-bold text-gray-800">{getGreeting()}, {profile.vorname || 'Therapeut/in'}!</h1>
                    <p className="text-gray-500">Hier ist dein Überblick für heute.</p>
                </div>
            </div>

            {/* Stat Cards */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                <StatCard title="Umsatz Heute" value={`€ ${dashboardData.revenueToday.toFixed(2)}`} icon={<DollarSignIcon className="h-6 w-6 text-white"/>} colorClass="bg-green-500" />
                <StatCard title="Umsatz Diese Woche" value={`€ ${dashboardData.revenueWeek.toFixed(2)}`} icon={<DollarSignIcon className="h-6 w-6 text-white"/>} colorClass="bg-blue-500" />
                <StatCard title="Aktive Patienten" value={dashboardData.totalPatients} icon={<UsersIcon className="h-6 w-6 text-white"/>} colorClass="bg-indigo-500" />
                <StatCard title="Geleistete Sitzungen" value={dashboardData.totalConductedSessions} icon={<ClockIcon className="h-6 w-6 text-white"/>} colorClass="bg-purple-500" />
            </div>

            {/* Main Content Area */}
            <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
                {/* Session Chart */}
                <div className="lg:col-span-2 bg-white p-6 rounded-lg shadow-sm">
                    <h2 className="text-lg font-semibold text-gray-700 mb-4">Sitzungsübersicht (Dieses Jahr)</h2>
                    <ResponsiveContainer width="100%" height={300}>
                        <AreaChart data={dashboardData.monthlySessions} margin={{ top: 10, right: 30, left: 0, bottom: 0 }}>
                            <CartesianGrid strokeDasharray="3 3" />
                            <XAxis dataKey="name" />
                            <YAxis />
                            <Tooltip />
                            <Area type="monotone" dataKey="Sitzungen" stroke="#4f46e5" fill="#c7d2fe" />
                        </AreaChart>
                    </ResponsiveContainer>
                </div>

                {/* Upcoming Appointments */}
                <div className="bg-white p-6 rounded-lg shadow-sm">
                    <h2 className="text-lg font-semibold text-gray-700 mb-4 flex items-center">
                        <CalendarIcon className="mr-2 text-red-500"/>Nächste Termine
                    </h2>
                    <div className="space-y-4 max-h-[280px] overflow-y-auto pr-2">
                        {upcomingAppointments.length > 0 ? upcomingAppointments.map(event => (
                             <div key={event.id} className="flex items-center space-x-3">
                                <div className="p-2 bg-red-100 rounded-lg">
                                    <CalendarIcon className="h-5 w-5 text-red-600"/>
                                </div>
                                <div className="flex-1">
                                    <p className="font-semibold text-gray-800">{event.summary}</p>
                                    <p className="text-sm text-gray-500">
                                       {new Date(event.start.dateTime).toLocaleDateString('de-DE')} - {new Date(event.start.dateTime).toLocaleTimeString('de-DE', { hour: '2-digit', minute: '2-digit' })} Uhr
                                    </p>
                                </div>
                             </div>
                         )) : <p className="text-sm text-gray-500">Keine bevorstehenden Termine in den nächsten 7 Tagen gefunden.</p>}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
