// src/components/Dashboard.js

import React, { useMemo } from 'react';
import { Bar, Doughnut } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from 'chart.js';

// Chart.js-Module registrieren
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

// Wiederverwendbare Icons (könnten aus einer zentralen Icon-Datei importiert werden)
const DollarSignIcon = ({ className = "w-8 h-8" }) => (<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><line x1="12" y1="1" x2="12" y2="23"></line><path d="M17 5H9.5a3.5 3.5 0 0 0 0 7h5a3.5 3.5 0 0 1 0 7H6"></path></svg>);
const UsersIcon = ({ className = "w-8 h-8" }) => (<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="M16 21v-2a4 4 0 0 0-4-4H6a4 4 0 0 0-4 4v2"></path><circle cx="9" cy="7" r="4"></circle><path d="M22 21v-2a4 4 0 0 0-3-3.87"></path><path d="M16 3.13a4 4 0 0 1 0 7.75"></path></svg>);
const ListChecksIcon = ({ className = "w-8 h-8" }) => (<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="m3 17 2 2 4-4"/><path d="m3 7 2 2 4-4"/><path d="M13 6h8"/><path d="M13 12h8"/><path d="M13 18h8"/></svg>);

// Eine einzelne Statistik-Kachel
const StatCard = ({ title, value, icon, colorClass }) => (
    <div className={`bg-white p-6 rounded-xl shadow-md flex items-center space-x-4 border-l-4 ${colorClass}`}>
        <div className="flex-shrink-0">{icon}</div>
        <div>
            <p className="text-sm font-medium text-gray-500">{title}</p>
            <p className="text-2xl font-bold text-gray-900">{value}</p>
        </div>
    </div>
);


const Dashboard = ({ patients, profile, setMainView }) => {

    // useMemo wird verwendet, um die Berechnungen nur dann erneut auszuführen,
    // wenn sich die Patientendaten oder das Profil ändern.
    const dashboardData = useMemo(() => {
        const data = {
            totalRevenueMonth: 0,
            totalRevenueYear: 0,
            totalSessionsMonth: 0,
            activePatientCount: patients.length,
            sessionsByMonth: Array(12).fill(0),
            revenueByMonth: Array(12).fill(0),
            patientProgress: []
        };

        const now = new Date();
        const currentYear = now.getFullYear();
        const currentMonth = now.getMonth();
        const punktwert = profile.punktwert || 0.11; // Fallback-Punktwert

        patients.forEach(patient => {
            let conductedSessions = 0;
            if (patient.sessions && patient.sessions.length > 0) {
                 conductedSessions = Math.max(...patient.sessions.map(s => s.structuredNotes?.sitzungsnummer || 0));

                patient.sessions.forEach(session => {
                    const sessionDate = new Date(session.date);
                    // EBM-Ziffern-basierte Berechnung (angenommen)
                    const sessionRevenue = (session.ebmEntries || [])
                        .reduce((sum, entry) => sum + (entry.punkte || 0), 0) * punktwert;

                    if (sessionDate.getFullYear() === currentYear) {
                        data.totalRevenueYear += sessionRevenue;
                        data.revenueByMonth[sessionDate.getMonth()] += sessionRevenue;

                        if (sessionDate.getMonth() === currentMonth) {
                            data.totalRevenueMonth += sessionRevenue;
                            data.totalSessionsMonth++;
                        }
                    }
                });
            }
             data.patientProgress.push({
                id: patient.id,
                chiffre: patient.chiffre,
                conducted: conductedSessions,
                approved: patient.approvedHours || 0,
            });
        });

        return data;
    }, [patients, profile]);

    const totalConductedSessions = dashboardData.patientProgress.reduce((sum, p) => sum + p.conducted, 0);

    const revenueChartData = {
        labels: ['Jan', 'Feb', 'Mär', 'Apr', 'Mai', 'Jun', 'Jul', 'Aug', 'Sep', 'Okt', 'Nov', 'Dez'],
        datasets: [{
            label: 'Umsatz pro Monat (€)',
            data: dashboardData.revenueByMonth,
            backgroundColor: 'rgba(20, 184, 166, 0.6)',
            borderColor: 'rgba(13, 148, 136, 1)',
            borderWidth: 1,
        }],
    };

     const goalChartData = {
        labels: ['Erreicht', 'Offen'],
        datasets: [{
            data: [totalConductedSessions, Math.max(0, 600 - totalConductedSessions)],
            backgroundColor: ['#10b981', '#e5e7eb'],
            hoverBackgroundColor: ['#059669', '#d1d5db'],
            borderWidth: 0,
        }]
    };

    return (
        <div className="p-6 space-y-8 bg-gray-50 min-h-full">
            {/* Header */}
            <h1 className="text-3xl font-bold text-gray-800">Dashboard</h1>

            {/* Obere Statistik-Kacheln */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                <StatCard title="Umsatz (Dieser Monat)" value={`€ ${dashboardData.totalRevenueMonth.toFixed(2)}`} icon={<DollarSignIcon className="text-green-500 w-8 h-8" />} colorClass="border-green-500" />
                <StatCard title="Aktive Patienten" value={dashboardData.activePatientCount} icon={<UsersIcon className="text-blue-500 w-8 h-8" />} colorClass="border-blue-500" />
                <StatCard title="Sitzungen (Dieser Monat)" value={dashboardData.totalSessionsMonth} icon={<ListChecksIcon className="text-indigo-500 w-8 h-8" />} colorClass="border-indigo-500" />
            </div>

            {/* Hauptbereich mit Charts und Listen */}
            <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
                {/* Umsatz-Chart */}
                <div className="lg:col-span-2 bg-white p-6 rounded-xl shadow-md">
                    <h2 className="text-lg font-semibold text-gray-700 mb-4">Jahresumsatz nach Monaten</h2>
                    <Bar data={revenueChartData} options={{ responsive: true, maintainAspectRatio: true }} />
                </div>

                {/* Rechte Spalte */}
                <div className="space-y-8">
                   {/* Jahresziel */}
                    <div className="bg-white p-6 rounded-xl shadow-md text-center">
                         <h2 className="text-lg font-semibold text-gray-700 mb-4">Jahresziel (Sitzungen)</h2>
                         <div className="relative w-48 h-48 mx-auto">
                            <Doughnut data={goalChartData} options={{ responsive: true, maintainAspectRatio: false, cutout: '70%' }} />
                            <div className="absolute inset-0 flex flex-col items-center justify-center">
                                <span className="text-3xl font-bold text-gray-800">{totalConductedSessions}</span>
                                <span className="text-sm text-gray-500">/ 600</span>
                            </div>
                         </div>
                    </div>

                    {/* Patientenübersicht */}
                    <div className="bg-white p-6 rounded-xl shadow-md">
                         <h2 className="text-lg font-semibold text-gray-700 mb-4">Patienten-Auslastung</h2>
                         <div className="space-y-4 max-h-64 overflow-y-auto pr-2">
                             {dashboardData.patientProgress.map(p => (
                                 <div key={p.id}>
                                     <div className="flex justify-between mb-1 text-sm">
                                         <span className="font-medium text-gray-700">{p.chiffre}</span>
                                         <span className="text-gray-500">{p.conducted} / {p.approved}</span>
                                     </div>
                                     <div className="w-full bg-gray-200 rounded-full h-2.5">
                                         <div className="bg-teal-600 h-2.5 rounded-full" style={{ width: `${(p.conducted / p.approved) * 100}%` }}></div>
                                     </div>
                                 </div>
                             ))}
                         </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
