const TrashIcon = ({ className = "w-4 h-4" }) => <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><polyline points="3 6 5 6 21 6"></polyline><path d="M19 6v14a2 2 0 0 1-2 2H7a2 2 0 0 1-2-2V6m3 0V4a2 2 0 0 1 2-2h4a2 2 0 0 1 2 2v2"></path></svg>;
const ChevronDownIcon = ({ className = "w-5 h-5" }) => <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><polyline points="6 9 12 15 18 9"></polyline></svg>;

// NEU: Konstante für den EBM-Punktwert
const EBM_PUNKTWERT_EURO = 0.123934;

const EbmAnalysis = ({ sessions, ebmData }) => {
    const analysis = useMemo(() => {
        const counts = {};
        sessions.forEach(session => {
            (session.ebmEntries || []).forEach(entry => {
                counts[entry.nummer] = (counts[entry.nummer] || 0) + (entry.anzahl || 1);
            });
        });
        return Object.entries(counts).map(([nummer, anzahl]) => ({
            nummer, anzahl, titel: (ebmData.find(e => e.Nummer === nummer) || {}).Titel || 'Unbekannte Ziffer'
        })).sort((a, b) => b.anzahl - a.anzahl);
    }, [sessions, ebmData]);
    if (analysis.length === 0) return null;
    return (
        <div className="max-h-48 overflow-y-auto">
            <table className="w-full text-sm">
                <thead className="bg-gray-50">
                    <tr>
                        <th className="text-left font-semibold p-2">EBM-Ziffer</th>
                        <th className="text-left font-semibold p-2">Titel</th>
                        <th className="text-right font-semibold p-2">Anzahl</th>
                    </tr>
                </thead>
                <tbody>
                    {analysis.map(({ nummer, anzahl, titel }) => (
                        <tr key={nummer} className="border-b last:border-0">
                            <td className="p-2 font-mono text-indigo-700">{nummer}</td>
                            <td className="p-2 text-gray-600">{titel}</td>
                            <td className="p-2 text-right font-semibold">{anzahl}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

const AbrechnungsUebersicht = ({ patient, ebmData, unbilledAppointments, onStartBilling, onDeleteEbmEntry }) => {
    const [selectedBilledSession, setSelectedBilledSession] = useState(null);
    const [isAnalysisVisible, setIsAnalysisVisible] = useState(false);
    const [isEditing, setIsEditing] = useState(false);

    // GEÄNDERT: Diese Funktion berechnet nun den Euro-Wert korrekt aus den Punkten
    const calculateSessionRevenue = (session) => {
        if (!session.ebmEntries || ebmData.length === 0) return { totalEuro: 0 };
        let totalEuro = 0;
        session.ebmEntries.forEach(entry => {
            const ebmInfo = ebmData.find(e => e.Nummer === entry.nummer);
            if (ebmInfo) {
                const punkte = parseFloat(ebmInfo.Punkte) || 0;
                const euroValue = punkte * EBM_PUNKTWERT_EURO;
                totalEuro += euroValue * (entry.anzahl || 1) * (entry.faktor || 1);
            }
        });
        return { totalEuro };
    };

    const billedSessions = useMemo(() => {
        return (patient.sessions || [])
            .filter(s =>
                s.ebmEntries &&
                s.ebmEntries.length > 0 &&
                !s.structuredNotes?.thema?.startsWith('Testdiagnostik:')
            )
            .sort((a, b) => new Date(b.date) - new Date(a.date));
    }, [patient.sessions]);

    const patientUnbilled = unbilledAppointments.filter(event => event.summary?.includes(patient.chiffre));
    const sessionDetails = selectedBilledSession ? calculateSessionRevenue(selectedBilledSession) : { totalEuro: 0 };

    return (
        <div className="space-y-6">
            <div className="p-4 bg-white rounded-lg shadow-md border">
                <button
                    onClick={() => setIsAnalysisVisible(!isAnalysisVisible)}
                    className="w-full flex justify-between items-center text-left"
                >
                    <h4 className="text-lg font-bold text-gray-800">Analyse der abgerechneten Ziffern</h4>
                    <ChevronDownIcon className={`w-5 h-5 text-gray-500 transition-transform duration-200 ${isAnalysisVisible ? 'rotate-180' : ''}`} />
                </button>
                {isAnalysisVisible && (
                    <div className="mt-4">
                        <EbmAnalysis sessions={patient.sessions || []} ebmData={ebmData} />
                    </div>
                )}
            </div>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                <div className="md:col-span-1 space-y-6">
                    <div>
                        <h4 className="text-lg font-bold text-gray-800 mb-2">Offene Termine</h4>
                        <div className="space-y-2">
                            {patientUnbilled.length === 0 ? 
                                <p className="text-sm text-gray-500 p-3 bg-gray-50 rounded-md">Keine offenen Termine.</p> :
                                patientUnbilled.map(event => (
                                    <button key={event.id} onClick={() => onStartBilling(event)} className="w-full text-left p-3 bg-yellow-100 hover:bg-yellow-200 rounded-md text-sm text-yellow-800 font-medium transition-colors">
                                        {new Date(event.start.dateTime).toLocaleDateString('de-DE')} - {event.summary.replace(`(${patient.chiffre})`, '').trim()}
                                    </button>
                                ))
                            }
                        </div>
                    </div>
                    <div>
                        <h4 className="text-lg font-bold text-gray-800 mb-2">Abgerechnete Sitzungen</h4>
                        <div className="space-y-1 max-h-[60vh] overflow-y-auto pr-2">
                            {billedSessions.length > 0 ? billedSessions.map((session, index) => (
                                <div 
                                    key={session.eventId || index} 
                                    onClick={() => setSelectedBilledSession(session)} 
                                    className={`p-2 rounded-md cursor-pointer text-xs transition-colors ${selectedBilledSession?.date === session.date ? 'bg-indigo-600 text-white' : 'bg-gray-100 hover:bg-indigo-100 text-gray-800'}`}
                                >
                                    <p className="font-semibold">{new Date(session.date).toLocaleDateString('de-DE', { year: 'numeric', month: '2-digit', day: '2-digit' })}</p>
                                    <p className={selectedBilledSession?.date === session.date ? 'text-indigo-200' : 'text-gray-500'}>{session.structuredNotes?.thema || 'Sitzung'}</p>
                                </div>
                            )) : <p className="text-xs text-gray-500 p-3 bg-gray-50 rounded-md">Noch keine Therapiesitzungen abgerechnet.</p>}
                        </div>
                    </div>
                </div>

                <div className="md:col-span-2">
                    {selectedBilledSession ? (
                        <div className="bg-white p-4 rounded-lg shadow-md space-y-4">
                            <div className="flex justify-between items-center">
                                <h4 className="text-lg font-semibold text-gray-700">Details für Sitzung vom {new Date(selectedBilledSession.date).toLocaleDateString('de-DE')}</h4>
                                <button
                                    onClick={() => setIsEditing(!isEditing)}
                                    className={`px-3 py-1 text-xs font-semibold rounded-full flex items-center gap-1 ${isEditing ? 'bg-red-100 text-red-700' : 'bg-gray-200 text-gray-700'}`}
                                >
                                    {isEditing ? 'Fertig' : <><EditIcon /> Bearbeiten</>}
                                </button>
                            </div>

                            <div className="bg-green-50 border border-green-200 rounded-lg p-3">
                                <h5 className="font-semibold text-green-800 mb-2">Abgerechnete Leistungen</h5>
                                <table className="w-full text-xs">
                                    <tbody>
                                        {(selectedBilledSession.ebmEntries || []).map((entry, i) => (
                                            <tr key={i} className="border-b last:border-b-0 border-green-100">
                                                <td className="py-1 font-mono">{entry.nummer}</td>
                                                <td className="py-1 text-gray-600">{(ebmData.find(e => e.Nummer === entry.nummer) || {}).Titel}</td>
                                                <td className="py-1 text-right">{entry.anzahl > 1 ? `x${entry.anzahl}` : ''}</td>
                                                {isEditing && (
                                                    <td className="py-1 text-right w-6">
                                                        <button 
                                                            onClick={() => onDeleteEbmEntry(selectedBilledSession.date, i)}
                                                            className="text-red-500 hover:text-red-700 p-1"
                                                            title="Diesen Eintrag löschen"
                                                        >
                                                            <TrashIcon />
                                                        </button>
                                                    </td>
                                                )}
                                            </tr>
                                        ))}
                                    </tbody>
                                    <tfoot>
                                        <tr className="font-bold">
                                            <td colSpan="2" className="pt-2 text-right">Gesamt:</td>
                                            <td className="pt-2 text-right">{sessionDetails.totalEuro.toFixed(2)} €</td>
                                        </tr>
                                    </tfoot>
                                </table>
                            </div>

                            {selectedBilledSession.structuredNotes && 
                            <div className="space-y-3">
                                {Object.entries(selectedBilledSession.structuredNotes).map(([key, value]) => (
                                    <div key={key}>
                                        <h6 className="font-semibold text-sm text-gray-600 capitalize">{key.replace(/([A-Z])/g, ' $1').trim()}</h6>
                                        {Array.isArray(value) ? (
                                            <ul className="list-disc list-inside pl-4 text-sm text-gray-800 bg-gray-50 p-2 rounded-md mt-1">
                                                {value.map((item, i) => item && <li key={i}>{item}</li>)}
                                            </ul>
                                        ) : (
                                            <p className="text-sm text-gray-800 bg-gray-50 p-2 rounded-md mt-1 whitespace-pre-wrap">{String(value)}</p>
                                        )}
                                    </div>
                                ))}
                            </div>}
                        </div>
                    ) : (
                        <div className="flex items-center justify-center h-full bg-gray-50 rounded-lg">
                            <p className="text-gray-500">Wählen Sie links eine Sitzung aus, um die Details anzuzeigen.</p>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default AbrechnungsUebersicht;
