import React, { useState, useMemo } from 'react';

const AbrechnungsUebersicht = ({ patient, ebmData, unbilledAppointments, onStartBilling }) => {
    const [activeBillingSubTab, setActiveBillingSubTab] = useState('gesamt');
    const [selectedBilledSession, setSelectedBilledSession] = useState(null);

    // Memoized-Berechnung für die aggregierte Gesamtübersicht des ausgewählten Patienten
    const aggregatedEbm = useMemo(() => {
        if (!patient || !patient.sessions) {
            return { ebmCounts: {}, totalEuro: 0 };
        }

        const allEntries = patient.sessions.flatMap(session => session.ebmEntries || []);

        const ebmCounts = allEntries.reduce((acc, entry) => {
            if (!acc[entry.nummer]) {
                const ebmInfo = ebmData.find(e => e.Nummer === entry.nummer);
                acc[entry.nummer] = { 
                    titel: ebmInfo?.Titel || 'Unbekannte Leistung',
                    anzahl: 0,
                    euro: parseFloat(String(ebmInfo?.Euro).replace(',', '.')) || 0
                };
            }
            acc[entry.nummer].anzahl += (entry.anzahl || 1) * (entry.faktor || 1);
            return acc;
        }, {});

        const totalEuro = Object.values(ebmCounts).reduce((sum, item) => {
            return sum + (item.anzahl * item.euro);
        }, 0);

        return { ebmCounts, totalEuro };
    }, [patient, ebmData]);

    const calculateSessionRevenue = (session) => {
        if (!session.ebmEntries || ebmData.length === 0) return { totalEuro: 0 };
        let totalEuro = 0;
        session.ebmEntries.forEach(entry => {
            const ebmInfo = ebmData.find(e => e.Nummer === entry.nummer);
            if (ebmInfo) {
                const euroValue = parseFloat(String(ebmInfo.Euro).replace(',', '.')) || 0;
                totalEuro += euroValue * (entry.anzahl || 1) * (entry.faktor || 1);
            }
        });
        return { totalEuro };
    };

    const billedSessions = (patient.sessions || []).filter(s => s.ebmEntries && s.ebmEntries.length > 0).sort((a, b) => new Date(b.date) - new Date(a.date));
    const patientUnbilled = unbilledAppointments.filter(event => event.summary?.includes(patient.chiffre));
    const sessionDetails = selectedBilledSession ? calculateSessionRevenue(selectedBilledSession) : { totalEuro: 0 };

    return (
        <div>
            <div className="border-b border-gray-200 mb-4">
                <nav className="-mb-px flex space-x-6" aria-label="Abrechnungs-Tabs">
                    {['Offen', 'Abgeschlossen', 'Gesamt'].map(tab => (
                        <button 
                            key={tab} 
                            onClick={() => setActiveBillingSubTab(tab.toLowerCase())}
                            className={`whitespace-nowrap pb-2 px-1 border-b-2 font-medium text-sm ${activeBillingSubTab === tab.toLowerCase() ? 'border-green-500 text-green-600' : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'}`}
                        >
                            {tab}
                        </button>
                    ))}
                </nav>
            </div>

            {activeBillingSubTab === 'offen' && (
                 <div className="space-y-2">
                    {patientUnbilled.length === 0 ? <p className="text-sm text-gray-500 p-2 bg-gray-50 rounded-md">Keine offenen Termine für diesen Patienten.</p> :
                        patientUnbilled.map(event => (
                            <button key={event.id} onClick={() => onStartBilling(event)} className="w-full text-left p-3 bg-yellow-100 hover:bg-yellow-200 rounded-md text-sm text-yellow-800 font-medium transition-colors">
                                {new Date(event.start.dateTime).toLocaleDateString('de-DE')} - {event.summary.replace(`(${patient.chiffre})`, '').trim()}
                            </button>
                        ))}
                </div>
            )}

            {activeBillingSubTab === 'abgeschlossen' && (
                <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                    <div className="md:col-span-1">
                        <h5 className="text-sm font-semibold text-gray-600 mb-2">Abgerechnete Sitzungen</h5>
                        <div className="space-y-1 max-h-[60vh] overflow-y-auto pr-2">
                            {billedSessions.length > 0 ? billedSessions.map((session, index) => (
                                <div key={session.eventId || index} onClick={() => setSelectedBilledSession(session)} className={`p-2 rounded-md cursor-pointer text-xs ${selectedBilledSession?.date === session.date ? 'bg-indigo-600 text-white' : 'bg-gray-100 hover:bg-indigo-100 text-gray-800'}`}>
                                    <p className="font-semibold">{new Date(session.date).toLocaleDateString('de-DE', { year: 'numeric', month: '2-digit', day: '2-digit' })}</p>
                                    <p className="text-gray-500 truncate">{session.structuredNotes?.thema || 'Testdiagnostik'}</p>
                                </div>
                            )) : <p className="text-xs text-gray-500 p-2 bg-gray-50 rounded-md">Noch keine Sitzungen abgerechnet.</p>}
                        </div>
                    </div>
                    <div className="md:col-span-2">
                        {selectedBilledSession ? (
                             <div className="space-y-4">
                                <h4 className="text-lg font-semibold text-gray-700 mb-3">Details für Sitzung vom {new Date(selectedBilledSession.date).toLocaleDateString('de-DE')}</h4>
                                <div className="bg-green-50 border border-green-200 rounded-lg p-3">
                                    <h5 className="font-semibold text-green-800 mb-2">Abgerechnete Leistungen</h5>
                                    <table className="w-full text-xs">
                                        <tbody>
                                            {(selectedBilledSession.ebmEntries || []).map((entry, i) => (
                                                <tr key={i} className="border-b last:border-b-0 border-green-100">
                                                    <td className="py-1 font-mono">{entry.nummer}</td>
                                                    <td className="py-1 text-gray-600">{(ebmData.find(e => e.Nummer === entry.nummer) || {}).Titel}</td>
                                                    <td className="py-1 text-right">{entry.anzahl > 1 ? `x${entry.anzahl}` : ''}</td>
                                                </tr>
                                            ))}
                                        </tbody>
                                        <tfoot>
                                            <tr className="font-bold">
                                                <td colSpan="2" className="pt-2 text-right">Gesamt:</td>
                                                <td className="pt-2 text-right">{sessionDetails.totalEuro.toFixed(2)} €</td>
                                            </tr>
                                        </tfoot>
                                    </table>
                                </div>
                                {selectedBilledSession.structuredNotes && 
                                <div className="space-y-3">
                                    {Object.entries(selectedBilledSession.structuredNotes).map(([key, value]) => (
                                        <div key={key}>
                                            <h6 className="font-semibold text-sm text-gray-600 capitalize">{key.replace(/([A-Z])/g, ' $1').trim()}</h6>
                                            {Array.isArray(value) ? (
                                                 <ul className="list-disc list-inside pl-4 text-sm text-gray-800 bg-gray-50 p-2 rounded-md mt-1">
                                                    {value.map((item, i) => item && <li key={i}>{item}</li>)}
                                                </ul>
                                            ) : (
                                                <p className="text-sm text-gray-800 bg-gray-50 p-2 rounded-md mt-1 whitespace-pre-wrap">{value}</p>
                                            )}
                                        </div>
                                    ))}
                                </div>}
                            </div>
                        ) : (
                            <div className="flex items-center justify-center h-full bg-gray-50 rounded-lg">
                                <p className="text-gray-500">Wählen Sie links eine Sitzung aus, um die Details anzuzeigen.</p>
                            </div>
                        )}
                    </div>
                </div>
            )}

            {activeBillingSubTab === 'gesamt' && (
                <div>
                    <h4 className="text-lg font-semibold text-gray-700 mb-3">Gesamte Abrechnungsübersicht</h4>
                     <div className="bg-white rounded-lg shadow-md p-4">
                        <table className="w-full text-sm">
                            <thead className="text-left">
                                <tr className="border-b">
                                    <th className="py-2 px-3 font-semibold">EBM-Ziffer</th>
                                    <th className="py-2 px-3 font-semibold">Bezeichnung</th>
                                    <th className="py-2 px-3 font-semibold text-right">Anzahl</th>
                                    <th className="py-2 px-3 font-semibold text-right">Gesamt (EUR)</th>
                                </tr>
                            </thead>
                            <tbody>
                                {Object.keys(aggregatedEbm.ebmCounts).length > 0 ? 
                                    Object.entries(aggregatedEbm.ebmCounts).map(([nummer, details]) => {
                                        return (
                                            <tr key={nummer} className="border-b last:border-b-0">
                                                <td className="py-2 px-3 font-mono">{nummer}</td>
                                                <td className="py-2 px-3 text-gray-600">{details.titel}</td>
                                                <td className="py-2 px-3 text-right font-medium">{details.anzahl}</td>
                                                <td className="py-2 px-3 text-right font-mono">{(details.anzahl * details.euro).toFixed(2)} €</td>
                                            </tr>
                                        );
                                    })
                                    : (
                                    <tr>
                                        <td colSpan="4" className="text-center py-6 text-gray-500">Für diesen Patienten wurden noch keine Leistungen erfasst.</td>
                                    </tr>
                                )}
                            </tbody>
                             <tfoot className="border-t-2">
                                <tr>
                                    <td colSpan="3" className="pt-3 pr-3 text-right font-bold">Gesamtsumme:</td>
                                    <td className="pt-3 pr-3 text-right font-bold font-mono">{aggregatedEbm.totalEuro.toFixed(2)} €</td>
                                </tr>
                            </tfoot>
                        </table>
                    </div>
                </div>
            )}
        </div>
    );
};

export default AbrechnungsUebersicht;
