    const baseCode = upperCodeToFind.replace(/(\.-[*]|\.\*|[*]|-)$/, '');
    found = localIcd10Data[`${baseCode}.-*`] || localIcd10Data[`${baseCode}.*`] || localIcd10Data[baseCode];
    if (found) return found;
    const possibleCategoryCode = Object.keys(localIcd10Data).find(
        key => key.startsWith(upperCodeToFind) && (localIcd10Data[key].code_kategorie || localIcd10Data[key].code)
    );
    if (possibleCategoryCode) return localIcd10Data[possibleCategoryCode];
    return null;
  }, [localIcd10Data]);

