import React, { useState, useMemo } from 'react';

const Ptv1Form = ({ patient, therapistProfile, onGenerate }) => {
    const [formData, setFormData] = useState({
        versichertenstatus: '',
        arztnr: '',
        ausstellungsdatum: new Date().toLocaleDateString('de-DE'),
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const ptv1ImageUrl = '/ptv1-background.png';

    if (!patient) {
        return <div className="text-center text-gray-500 p-8">Bitte w√§hlen Sie oben einen Patienten aus, um das Formular zu laden.</div>;
    }

    return (
        <div className="ptv-form-container bg-white p-4">
            <style>{`
                .ptv-form-wrapper {
                    position: relative;
                    width: 794px;
                    height: 1123px;
                    margin: auto;
                    box-shadow: 0 0 10px rgba(0,0,0,0.1);
                    page-break-after: always;
                }
                .ptv-background {
                    width: 100%;
                    height: 100%;
                }
                .ptv-overlay {
                    position: absolute;
                    top: 0;
                    left: 0;
                    width: 100%;
                    height: 100%;
                    font-family: 'Courier New', Courier, monospace;
                    font-size: 10pt;
                }
                .ptv-field {
                    position: absolute;
                    background-color: transparent;
                    border: none;
                    padding: 0;
                    margin: 0;
                    resize: none;
                    overflow: hidden;
                }
                .patient-name { top: 85px; left: 90px; width: 300px; }
                .patient-geburtsdatum { top: 85px; left: 420px; width: 120px; }
                .kostentraegerkennung { top: 120px; left: 90px; width: 80px; }
                .versichertennr { top: 120px; left: 200px; width: 130px; }
                .versichertenstatus { top: 120px; left: 360px; width: 50px; }
                .bsnr { top: 150px; left: 90px; width: 130px; }
                .arztnr { top: 150px; left: 240px; width: 130px; }
                .ausstellungsdatum { top: 150px; left: 400px; width: 150px; }
                .krankenkasse { top: 180px; left: 90px; width: 500px; }

                @media print {
                    body * { visibility: hidden; }
                    .print-area, .print-area * { visibility: visible; }
                    .print-area { position: absolute; left: 0; top: 0; width: 100%; }
                    .ptv-form-wrapper { box-shadow: none; margin: 0; }
                    textarea.ptv-field, input.ptv-field { color: black !important; }
                }
            `}</style>

            <div className="ptv-form-wrapper print-area">
                <img src={ptv1ImageUrl} alt="PTV1 Formular" className="ptv-background" />
                <div className="ptv-overlay">
                    <div className="ptv-field patient-name">{patient.vorname} {patient.name}</div>
                    <div className="ptv-field patient-geburtsdatum">{new Date(patient.geburtsdatum).toLocaleDateString('de-DE')}</div>
                    <div className="ptv-field kostentraegerkennung">{patient.kostentraegerkennung}</div>
                    <div className="ptv-field versichertennr">{patient.versichertennummer}</div>
                    <input className="ptv-field versichertenstatus" name="versichertenstatus" value={formData.versichertenstatus} onChange={handleChange} />
                    <div className="ptv-field bsnr">{therapistProfile?.bsnr}</div>
                    <input className="ptv-field arztnr" name="arztnr" value={formData.arztnr} onChange={handleChange} />
                    <div className="ptv-field ausstellungsdatum">{formData.ausstellungsdatum}</div>
                    <div className="ptv-field krankenkasse">{patient.krankenkasse}</div>
                </div>
            </div>
            <div className="flex justify-end mt-6">
                <button onClick={() => onGenerate({ ...formData, patientDaten: patient })} className="px-6 py-2 text-white bg-green-600 rounded-md hover:bg-green-700 font-semibold">
                    Formular generieren
                </button>
            </div>
        </div>
    );
};

export default Ptv1Form;
