    if (!selectedPatient || !newSessionNotes.trim() || !userId) return;
    const collectionPath = getPatientsCollectionPath(); if (!collectionPath) return;
    const newSession = { date: newSessionDate, notes: newSessionNotes.trim() };
    const updatedSessions = [...(selectedPatient.sessions || []), newSession];
    updatedSessions.sort((a, b) => new Date(b.date) - new Date(a.date)); 
    const patientRef = doc(db, collectionPath, selectedPatient.id);
    try { await setDoc(patientRef, { sessions: updatedSessions }, { merge: true }); setNewSessionNotes(''); }
    catch (err) { console.error("Error adding session:", err); setError("Fehler beim Hinzufügen der Sitzung.");}
   };

