    const updatedHsclResults = selectedPatient.hscl11Results.filter(r => r.date !== dateToRemove);
    const patientRef = doc(db, collectionPath, selectedPatient.id);
    try { await setDoc(patientRef, { hscl11Results: updatedHsclResults }, { merge: true }); }
    catch (err) { console.error("Error removing HSCL result:", err); setError("Fehler beim Entfernen des HSCL-Ergebnisses."); }
  };
  const handleAddBdiResult = async () => { 
    if (!selectedPatient || !newBdiDate || !userId) return;
    const collectionPath = getPatientsCollectionPath(); if (!collectionPath) return;
    const score = parseInt(newBdiScore, 10);
    if (isNaN(score) || score < 0 || score > 63) { setError("Ungültiger BDI-II Wert (0-63)."); return; }
    const newResult = { date: newBdiDate, score: score };
    const updatedBdiResults = [...(selectedPatient.bdi2Results || []), newResult];
    updatedBdiResults.sort((a, b) => new Date(a.date) - new Date(b.date)); 
    const patientRef = doc(db, collectionPath, selectedPatient.id);
    try { await setDoc(patientRef, { bdi2Results: updatedBdiResults }, { merge: true });
      setNewBdiScore(''); setError(null); 
    } catch (err) { console.error("Error adding BDI-II result:", err); setError("Fehler beim Hinzufügen des BDI-II Ergebnisses."); }
  };
  const handleRemoveBdiResult = async (dateToRemove) => { 
    if (!selectedPatient || !userId) return;
    const collectionPath = getPatientsCollectionPath(); if (!collectionPath) return;
    const updatedBdiResults = selectedPatient.bdi2Results.filter(r => r.date !== dateToRemove);
    const patientRef = doc(db, collectionPath, selectedPatient.id);
    try { await setDoc(patientRef, { bdi2Results: updatedBdiResults }, { merge: true }); }
    catch (err) { console.error("Error removing BDI-II result:", err); setError("Fehler beim Entfernen des BDI-II Ergebnisses."); }
  };
  const handleFepItemChange = (index, value) => { 
    const newScores = [...fepItemScores];
    newScores[index] = value === '' ? 0 : parseInt(value, 10); 
    if (isNaN(newScores[index]) || newScores[index] < 0 || newScores[index] > 5) newScores[index] = 0; 
    setFepItemScores(newScores);
  };
  const handleAddFepResult = async () => { 
    if (!selectedPatient || !newFepDate || !userId) return;
    const collectionPath = getPatientsCollectionPath(); if (!collectionPath) return;
    const answeredCount = fepItemScores.filter(s => s >= 1 && s <= 5).length;
    if (answeredCount < 32) { setError("Bitte beantworten Sie mindestens 80% (32) der FEP-2 Items."); return; }
    const calculatedScales = calculateFep2Scores(fepItemScores);
    const newResult = { date: newFepDate, itemScores: [...fepItemScores], calculatedScales };
    const updatedFepResults = [...(selectedPatient.fep2Results || []), newResult];
    updatedFepResults.sort((a, b) => new Date(a.date) - new Date(b.date)); 
    const patientRef = doc(db, collectionPath, selectedPatient.id);
    try { await setDoc(patientRef, { fep2Results: updatedFepResults }, { merge: true });
      setFepItemScores(Array(40).fill(0)); setError(null); 
    } catch (err) { console.error("Error adding FEP-2 result:", err); setError("Fehler beim Hinzufügen des FEP-2 Ergebnisses."); }
  };
  const handleRemoveFepResult = async (dateToRemove) => { 
    if (!selectedPatient || !userId) return;
    const collectionPath = getPatientsCollectionPath(); if (!collectionPath) return;
    const updatedFepResults = selectedPatient.fep2Results.filter(r => r.date !== dateToRemove);
    const patientRef = doc(db, collectionPath, selectedPatient.id);
    try { await setDoc(patientRef, { fep2Results: updatedFepResults }, { merge: true }); }
    catch (err) { console.error("Error removing FEP-2 result:", err); setError("Fehler beim Entfernen des FEP-2 Ergebnisses."); }
  };
  const fetchSessionSummary = async (sessionNotes, sessionIndex) => { 
    setSessionSummaries(prev => ({ ...prev, [sessionIndex]: { ...prev[sessionIndex], isLoading: true, error: null } }));
    const prompt = `Fasse die folgenden Therapiesitzungsnotizen prägnant zusammen. Konzentriere dich auf die wichtigsten Themen, Interventionen und den Fortschritt des Patienten:\n\n"${sessionNotes}"`;
    try { const summary = await generateGeminiText(prompt); setSessionSummaries(prev => ({ ...prev, [sessionIndex]: { summary: summary, isLoading: false, error: null } })); } 
    catch (e) { setSessionSummaries(prev => ({ ...prev, [sessionIndex]: { ...prev[sessionIndex], isLoading: false, error: e.message } })); setError(`Fehler bei der Zusammenfassung: ${e.message}`); }
  };

  const showDiagnosisInfo = (diagnosisString) => {
    const sicherheitMatch = diagnosisString.match(/^\[([AVGZN])\]\s*/i);
    const sicherheit = sicherheitMatch ? sicherheitMatch[1].toUpperCase() : '';
    const codeAndName = sicherheitMatch ? diagnosisString.substring(sicherheitMatch[0].length) : diagnosisString;
    const codeMatch = codeAndName.match(/^([A-Z][0-9]{1,2}(\.[0-9A-Z]{1,2})?[\*\-\!]?)/i);
    const code = codeMatch ? codeMatch[0].toUpperCase() : codeAndName.split(':')[0].trim().toUpperCase();

    const localData = findIcd10CodeLocal(code);
    if (localData) {
        setDiagnosisInfo({ 
            code: localData.code || code, 
            name: localData.name || '', 
            beschreibung: localData.beschreibung || localData.beschreibung_kategorie || '', 
            inkl: localData.inkl || [], 
            exkl: localData.exkl || [], 
            hinweise: localData.hinweise || localData.hinweise_kategorie || [], 
            zusatz_info_code: localData.zusatz_info_code || '', 
            sicherheit: sicherheit, // Diagnosesicherheit hinzufügen
            isLoading: false, 
            error: null, 
            isOpen: true, 
            source: 'local' 
        });
    } else {
        fetchDiagnosisInfoGemini(code, sicherheit); // Sicherheit an Gemini-Funktion übergeben
    }
  };

  const fetchDiagnosisInfoGemini = async (diagnosisCode, sicherheit = '') => { 
    setDiagnosisInfo({ code: diagnosisCode, info: '', isLoading: true, error: null, isOpen: true, source: 'gemini', sicherheit: sicherheit });
    const prompt = `Erkläre die ICD-10 Diagnose '${diagnosisCode}'${sicherheit ? ` (Diagnosesicherheit: ${sicherheit})` : ''} kurz und verständlich für einen Laien. Nenne auch typische Symptome und ggf. wichtige Differentialdiagnosen oder Komorbiditäten, falls relevant. Antworte auf Deutsch.`;
    try { const info = await generateGeminiText(prompt); setDiagnosisInfo(prev => ({ ...prev, info: info, isLoading: false })); } 
    catch (e) { setDiagnosisInfo(prev => ({ ...prev, isLoading: false, error: e.message })); setError(`Fehler beim Abrufen der Diagnose-Info via Gemini: ${e.message}`);}
  };
  const handleKrankheitsmodellChange = async (fieldName, value) => {
    if (!selectedPatient || !userId) return;
    const collectionPath = getPatientsCollectionPath();
    if (!collectionPath) return;
    const patientRef = doc(db, collectionPath, selectedPatient.id);
    try {
        await setDoc(patientRef, { [fieldName]: value }, { merge: true });
    } catch (err) {
        console.error(`Error updating ${fieldName}:`, err);
        setError(`Fehler beim Speichern von ${fieldName}.`);
    }
  };


  const totalApprovedHours = patients.reduce((sum, p) => sum + (p.approvedHours || 0), 0);
  const totalConductedHours = patients.reduce((sum, p) => sum + (p.conductedHours || 0), 0);
  const overallProgressTarget = 600;
  const overallProgress = overallProgressTarget > 0 ? (totalConductedHours / overallProgressTarget) * 100 : 0;

  if (!isAuthReady) return <div className="flex justify-center items-center h-screen text-xl font-semibold text-gray-700">Authentifizierung wird initialisiert...</div>;
  if (isLoading && isAuthReady && userId) return <div className="flex justify-center items-center h-screen text-xl font-semibold text-gray-700">Lade Patientendaten...</div>;

  const bdiCutoffs = [ { y1: 0, y2: 8, label: "Keine Depression", color: "rgba(75, 192, 192, 0.2)" }, { y1: 9, y2: 13, label: "Minimale Depression", color: "rgba(54, 162, 235, 0.2)" }, { y1: 14, y2: 19, label: "Leichte Depression", color: "rgba(255, 206, 86, 0.2)" }, { y1: 20, y2: 28, label: "Mittelschwere Depression", color: "rgba(255, 159, 64, 0.2)" }, { y1: 29, y2: 63, label: "Schwere Depression", color: "rgba(255, 99, 132, 0.2)" }, ];
  const bdiAxisTicks = [0, 8, 13, 19, 28, 63]; 

  const inputStd = "mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm";
  const btnPrimary = "bg-indigo-600 hover:bg-indigo-700 text-white font-semibold py-2 px-4 rounded-md shadow-sm flex items-center justify-center";
  const btnDanger = "bg-red-500 hover:bg-red-600 text-white font-semibold py-2 px-3 rounded-md flex items-center";
  const btnInfo = "bg-sky-500 hover:bg-sky-600 text-white font-semibold py-1 px-2 text-xs rounded-md flex items-center";
  const textareaStd = "mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm min-h-[150px]";


  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-100 to-sky-100 font-sans">
      <header className="bg-indigo-700 text-white shadow-lg"><div className="container mx-auto px-4 sm:px-6 lg:px-8 py-4 flex justify-between items-center"><h1 className="text-3xl font-bold tracking-tight">Patientenverwaltung</h1>{userId && <p className="text-xs opacity-75">UserID: {userId}</p>}</div></header>
      <main className="container mx-auto p-4 sm:p-6 lg:p-8">
        <div className="mb-6 flex justify-between items-center"><h2 className="text-2xl font-semibold text-gray-800">Patientenübersicht</h2><button onClick={() => { setEditingPatient(null); setIsPatientModalOpen(true); }} className={`${btnPrimary} py-2 px-4`}><UserPlus className="w-5 h-5 mr-2" /> Patient hinzufügen</button></div>

        <div className="bg-white shadow-xl rounded-lg overflow-hidden mb-8"><div className="overflow-x-auto"><table className="w-full min-w-max table-auto text-left">
            <thead className="bg-gray-100 border-b border-gray-300"><tr>{['Name', 'Chiffre', 'Diagnosen', 'Therapie', 'Stunden', 'Fortschritt', 'Aktionen'].map(header => (<th key={header} className="py-3 px-4 text-sm font-semibold text-gray-600 uppercase tracking-wider">{header}</th>))}</tr></thead>
            <tbody className="divide-y divide-gray-200">
                {patients.length === 0 && !isLoading && (<tr><td colSpan="7" className="text-center py-10 text-gray-500">Keine Patienten gefunden. Fügen Sie einen neuen Patienten hinzu.</td></tr>)}
                {patients.map((patient) => { 
                    const progress = patient.approvedHours > 0 ? (patient.conductedHours / patient.approvedHours) * 100 : 0;
                    return ( 
                        <tr key={patient.id} className={`hover:bg-indigo-50 cursor-pointer ${selectedPatient?.id === patient.id ? 'bg-indigo-100' : ''}`} onClick={() => { setSelectedPatient(patient); setActiveDetailTab('details'); setSessionSummaries({});  }}>
                            <td className="py-3 px-4 whitespace-nowrap text-sm font-medium text-gray-800">{patient.name}</td>
                            <td className="py-3 px-4 whitespace-nowrap text-sm text-gray-600">{patient.chiffre}</td>
                            <td className="py-3 px-4 text-sm text-gray-600 max-w-xs">
                                <div className="flex flex-col space-y-1">
                                    {(patient.diagnoses || []).slice(0, 2).map((diag, idx) => (
                                        <span key={idx} 
                                              className="px-2 py-0.5 text-xs bg-blue-100 text-blue-700 rounded-full truncate inline-block hover:bg-blue-200 cursor-pointer"
                                              onClick={(e) => { e.stopPropagation(); showDiagnosisInfo(diag);}}
                                              title={`Info zu ${diag} anzeigen`}>
                                            {diag}
                                        </span>
                                    ))}
                                    {(patient.diagnoses || []).length > 2 && (
                                        <span className="text-xs text-gray-500 mt-1">...</span>
                                    )}
                                    {(patient.diagnoses || []).length === 0 && "-"}
                                </div>
                            </td>
                            <td className="py-3 px-4 whitespace-nowrap text-sm text-gray-600">{patient.therapyType}</td>
                            <td className="py-3 px-4 whitespace-nowrap text-sm text-gray-600 text-center">{`${patient.conductedHours} / ${patient.approvedHours}`}</td>
                            <td className="py-3 px-4 whitespace-nowrap">
                                <div className="w-full bg-gray-200 rounded-full h-2.5">
                                    <div className={`h-2.5 rounded-full ${progress > 80 ? 'bg-red-500' : progress > 50 ? 'bg-yellow-400' : 'bg-green-500'}`} style={{ width: `${Math.min(progress, 100)}%` }}></div>
                                </div>
                                <span className="text-xs text-gray-500 block text-center mt-1">{Math.round(progress)}%</span>
                            </td>
                            <td className="py-3 px-4 whitespace-nowrap text-sm space-x-2">
                                <button onClick={(e) => { e.stopPropagation(); setEditingPatient(patient); setIsPatientModalOpen(true); }} className="text-indigo-600 hover:text-indigo-800 transition duration-150 p-1 rounded hover:bg-indigo-100" title="Bearbeiten"><Edit3 className="w-5 h-5" /></button>
                                <button onClick={(e) => { e.stopPropagation(); openDeleteConfirmation(patient.id); }} className="text-red-500 hover:text-red-700 transition duration-150 p-1 rounded hover:bg-red-100" title="Löschen"><Trash2 className="w-5 h-5" /></button>
                            </td>
                        </tr>
                    );
                })}
            </tbody>
            <tfoot className="bg-gray-50 border-t-2 border-gray-300">
                <tr>
                    <td colSpan="4" className="py-3 px-4 text-sm font-semibold text-gray-700 text-right">Gesamt:</td>
                    <td className="py-3 px-4 text-sm font-semibold text-gray-700 text-center">{`${totalConductedHours} / ${totalApprovedHours}`}</td>
                    <td className="py-3 px-4 whitespace-nowrap" colSpan="2">
                        <div className="flex items-center">
                            <span className="text-xs text-gray-600 mr-3 w-auto">Fortschritt (600 Std.):</span>
                            <div className="w-full bg-gray-200 rounded-full h-2.5 flex-grow">
                                <div className="bg-blue-500 h-2.5 rounded-full" style={{ width: `${Math.min(overallProgress, 100)}%` }}></div>
                            </div>
                            <span className="text-xs text-gray-600 ml-2">{Math.round(overallProgress)}%</span>
                        </div>
                    </td>
                </tr>
            </tfoot>
        </table></div></div>

        {selectedPatient && (
          <div className="bg-white shadow-xl rounded-lg p-6 mb-8">
            <div className="flex justify-between items-center mb-1"><h3 className="text-2xl font-semibold text-indigo-700">{selectedPatient.name}</h3><button onClick={() => {setSelectedPatient(null); setSessionSummaries({}); }} className="text-gray-500 hover:text-gray-700"><XCircle className="w-7 h-7" /></button></div>

            <div className="border-b border-gray-200 mb-6">
                <nav className="-mb-px flex space-x-4 sm:space-x-8 overflow-x-auto" aria-label="Tabs">
                    {['details', 'krankheitsmodell', 'diagnostik'].map(tab => (
                        <button key={tab} onClick={() => setActiveDetailTab(tab)}
                            className={`whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm ${activeDetailTab === tab ? 'border-indigo-500 text-indigo-600' : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'}`}>
                            {tab === 'details' && 'Stammdaten & Sitzungen'} 
                            {tab === 'krankheitsmodell' && 'Krankheitsmodell'}
                            {tab === 'diagnostik' && 'Diagnostik'} 
                        </button>
                    ))}
                </nav>
            </div>

            {activeDetailTab === 'details' && (
              <div>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
                    <div><p className="text-sm text-gray-500">Chiffre</p><p className="text-lg text-gray-800">{selectedPatient.chiffre}</p></div>
                    <div><p className="text-sm text-gray-500">Therapieform</p><p className="text-lg text-gray-800">{selectedPatient.therapyType}</p></div>
                    <div><p className="text-sm text-gray-500">Genehmigte Stunden</p><p className="text-lg text-gray-800">{selectedPatient.approvedHours}</p></div>
                    <div>
                        <label htmlFor="conductedHoursDetail" className="block text-sm font-medium text-gray-500">Durchgeführte Stunden</label>
                        <div className="flex items-center mt-1">
                            <input type="number" id="conductedHoursDetail" 
                                   value={editableConductedHours} 
                                   onChange={(e) => setEditableConductedHours(e.target.value)}
                                   onBlur={(e) => {
                                       let finalHours = parseInt(e.target.value, 10);
                                       if (isNaN(finalHours) || finalHours < 0) {
                                           finalHours = 0; 
                                       } else if (selectedPatient && finalHours > selectedPatient.approvedHours) {
                                           finalHours = selectedPatient.approvedHours;
                                       }
                                       setEditableConductedHours(String(finalHours)); 
                                       if (selectedPatient) {
                                           handleUpdateConductedHours(selectedPatient.id, finalHours); 
                                       }
                                   }}
                                   min="0" 
                                   max={selectedPatient ? selectedPatient.approvedHours : undefined} 
                                   className={inputStd + " w-24"}/>
                            <span className="ml-2 text-sm text-gray-600">/ {selectedPatient.approvedHours}</span>
                        </div>
                    </div>
                </div>
                <div className="mb-6"> 
                  <h4 className="text-xl font-semibold text-gray-700 mb-3">ICD-10 F-Diagnosen</h4>
                  <div className="mb-3 p-4 bg-gray-50 rounded-md">
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-4 items-end">
                        <div>
                            <label htmlFor="newDiagnosisCode" className="block text-xs font-medium text-gray-700">Code (z.B. F32.2)</label>
                            <input type="text" id="newDiagnosisCode" value={newDiagnosisCode} onChange={(e) => setNewDiagnosisCode(e.target.value.toUpperCase())} placeholder="F-Code" className={`${inputStd} mt-1`}/>
                        </div>
                        <div>
                            <label htmlFor="newDiagnosisSicherheit" className="block text-xs font-medium text-gray-700">Sicherheit</label>
                            <select id="newDiagnosisSicherheit" value={newDiagnosisSicherheit} onChange={(e) => setNewDiagnosisSicherheit(e.target.value)} className={`${inputStd} mt-1`}>
                                <option value="G">G (Gesichert)</option>
                                <option value="V">V (Verdacht auf)</option>
                                <option value="A">A (Ausschluss)</option>
                                <option value="ZN">ZN (Zustand nach)</option>
                            </select>
                        </div>
                        <div className="md:col-span-1"> {/* Leere Spalte für Layout oder Button hier */} </div>
                        <div className="md:col-span-2">
                            <label htmlFor="newDiagnosisName" className="block text-xs font-medium text-gray-700">Manuelle Beschreibung (optional)</label>
                            <input type="text" id="newDiagnosisName" value={newDiagnosis} onChange={(e) => setNewDiagnosis(e.target.value)} placeholder="Eigene Beschreibung" className={`${inputStd} mt-1`}/>
                        </div>
                        <button onClick={handleAddDiagnosis} className={`${btnPrimary} bg-green-500 hover:bg-green-600 py-2 px-3 h-10 md:mt-6`}><PlusCircleIcon className="w-5 h-5 mr-1" /> Hinzufügen</button>
                    </div>
                    {lookedUpDiagnosisName && <p className="text-xs text-indigo-600 mt-1">Gefunden: {lookedUpDiagnosisName}</p>}
                  </div>
                  <ul className="space-y-1 mb-3">
                    {(selectedPatient.diagnoses || []).map((diagString, index) => ( 
                        <li key={index} className="flex justify-between items-center bg-gray-50 p-2 rounded-md">
                            <span className="text-sm text-gray-700">{diagString}</span>
                            <div className="flex items-center space-x-2">
                                <button onClick={() => showDiagnosisInfo(diagString)} className={`${btnInfo} p-1`} title="Info zu dieser Diagnose abrufen">
                                    <FileJson2 className="w-4 h-4 mr-1"/> Info
                                </button>
                                <button onClick={() => handleRemoveDiagnosis(diagString)} className={`${btnDanger} p-1 text-xs`}><Trash2 className="w-4 h-4" /></button>
                            </div>
                        </li>
                    ))}
                    {(selectedPatient.diagnoses || []).length === 0 && <p className="text-sm text-gray-500">Keine Diagnosen erfasst.</p>}
                  </ul>
                </div>
                <div> 
                  <h4 className="text-xl font-semibold text-gray-700 mb-3">Sitzungsdokumentation</h4>
                  <div className="bg-gray-50 p-4 rounded-lg mb-4"><div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-3"><div><label htmlFor="sessionDate" className="block text-sm font-medium text-gray-700">Datum</label><input type="date" id="sessionDate" value={newSessionDate} onChange={(e) => setNewSessionDate(e.target.value)} className={inputStd}/></div><div className="md:col-span-2"><label htmlFor="sessionNotes" className="block text-sm font-medium text-gray-700">Notizen</label><textarea id="sessionNotes" value={newSessionNotes} onChange={(e) => setNewSessionNotes(e.target.value)} rows="3" placeholder="Sitzungsnotizen..." className={inputStd}></textarea></div></div><button onClick={handleAddSession} className={`${btnPrimary} bg-blue-500 hover:bg-blue-600`}><PlusCircleIcon className="w-5 h-5 mr-2" /> Sitzung hinzufügen</button></div>
                  <h5 className="text-md font-semibold text-gray-600 mb-2">Protokollierte Sitzungen:</h5>
                  <div className="space-y-3 max-h-96 overflow-y-auto pr-2"> 
                    {(selectedPatient.sessions || []).map((session, index) => (
                        <div key={index} className="bg-white border border-gray-200 p-3 rounded-md shadow-sm">
                            <div className="flex justify-between items-start">
                                <p className="text-sm font-semibold text-gray-700">Datum: {new Date(session.date).toLocaleDateString('de-DE')}</p>
                                <button onClick={() => fetchSessionSummary(session.notes, index)} 
                                        disabled={sessionSummaries[index]?.isLoading}
                                        className={`${btnInfo} text-xs`} title="Sitzungsnotizen zusammenfassen">
                                    {sessionSummaries[index]?.isLoading ? 'Lädt...' : '✨ Zusammenf.'}
                                </button>
                            </div>
                            <p className="text-sm text-gray-600 whitespace-pre-wrap mt-1">{session.notes}</p>
                            {sessionSummaries[index]?.summary && !sessionSummaries[index]?.isLoading && (
                                <div className="mt-2 p-2 bg-sky-50 border border-sky-200 rounded-md text-sm text-sky-700">
                                    <p className="font-semibold">Zusammenfassung:</p>
                                    <p className="whitespace-pre-wrap">{sessionSummaries[index].summary}</p>
                                </div>
                            )}
                            {sessionSummaries[index]?.error && (
                                <p className="mt-2 text-xs text-red-500">Fehler bei Zusammenfassung: {sessionSummaries[index].error}</p>
                            )}
                        </div>
                    ))} 
                    {(selectedPatient.sessions || []).length === 0 && <p className="text-sm text-gray-500">Keine Sitzungen dokumentiert.</p>}
                  </div>
                </div>
              </div>
            )}

            {activeDetailTab === 'krankheitsmodell' && (
                <div className="space-y-6">
                    <div>
                        <label htmlFor="krankheitsanamnese" className="block text-lg font-semibold text-gray-700 mb-1">Krankheitsanamnese</label>
                        <textarea id="krankheitsanamnese" name="krankheitsanamnese" rows="6"
                                  className={textareaStd}
                                  value={selectedPatient.krankheitsanamnese || ""}
                                  onChange={(e) => setSelectedPatient(prev => ({...prev, krankheitsanamnese: e.target.value}))}
                                  onBlur={(e) => handleKrankheitsmodellChange('krankheitsanamnese', e.target.value)}
                                  placeholder="Hier die Krankheitsanamnese eintragen..."
                        ></textarea>
                    </div>
                    <div>
                        <label htmlFor="lebensgeschichte" className="block text-lg font-semibold text-gray-700 mb-1">Lebensgeschichte / Biographie</label>
                        <textarea id="lebensgeschichte" name="lebensgeschichte" rows="6"
                                  className={textareaStd}
                                  value={selectedPatient.lebensgeschichte || ""}
                                  onChange={(e) => setSelectedPatient(prev => ({...prev, lebensgeschichte: e.target.value}))}
                                  onBlur={(e) => handleKrankheitsmodellChange('lebensgeschichte', e.target.value)}
                                  placeholder="Wichtige Aspekte der Lebensgeschichte..."
                        ></textarea>
                    </div>
                    <div>
                        <label htmlFor="problemanalyseMakro" className="block text-lg font-semibold text-gray-700 mb-1">Problemanalyse auf Makroebene</label>
                        <textarea id="problemanalyseMakro" name="problemanalyseMakro" rows="6"
                                  className={textareaStd}
                                  value={selectedPatient.problemanalyseMakro || ""}
                                  onChange={(e) => setSelectedPatient(prev => ({...prev, problemanalyseMakro: e.target.value}))}
                                  onBlur={(e) => handleKrankheitsmodellChange('problemanalyseMakro', e.target.value)}
                                  placeholder="Analyse der übergeordneten Problembereiche..."
                        ></textarea>
                    </div>
                     <div>
                        <label htmlFor="therapieplan" className="block text-lg font-semibold text-gray-700 mb-1">Therapieplan</label>
                        <textarea id="therapieplan" name="therapieplan" rows="6"
                                  className={textareaStd}
                                  value={selectedPatient.therapieplan || ""}
                                  onChange={(e) => setSelectedPatient(prev => ({...prev, therapieplan: e.target.value}))}
                                  onBlur={(e) => handleKrankheitsmodellChange('therapieplan', e.target.value)}
                                  placeholder="Therapieziele und -maßnahmen..."
                        ></textarea>
                    </div>
                </div>
            )}

            {activeDetailTab === 'diagnostik' && (
              <div>
                <div className="border-b border-gray-200 mb-4">
                    <nav className="-mb-px flex space-x-6" aria-label="Diagnostik Sub-Tabs">
                        {['hscl11', 'bdi2', 'fep2'].map(subTab => (
                            <button key={subTab} onClick={() => setActiveDiagnostikSubTab(subTab)}
                                className={`whitespace-nowrap pb-2 px-1 border-b-2 font-medium text-sm ${activeDiagnostikSubTab === subTab ? 'border-purple-500 text-purple-600' : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'}`}>
                                {subTab === 'hscl11' && 'HSCL-11'}
                                {subTab === 'bdi2' && 'BDI-II'}
                                {subTab === 'fep2' && 'FEP-2'}
                            </button>
                        ))}
                    </nav>
                </div>

                {activeDiagnostikSubTab === 'hscl11' && ( 
                  <div>
                    <h4 className="text-xl font-semibold text-gray-700 mb-4">HSCL-11 Ergebnisse</h4>
                    <div className="bg-gray-50 p-4 rounded-lg mb-6 shadow">
                        <h5 className="text-md font-semibold text-gray-600 mb-3">Neues HSCL-11 Ergebnis</h5>
                        <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-3 items-end">
                            <div><label htmlFor="hsclDate" className="block text-sm font-medium text-gray-700">Datum</label><input type="date" id="hsclDate" value={newHsclDate} onChange={(e) => setNewHsclDate(e.target.value)} className={inputStd} /></div>
                            <div><label htmlFor="hsclAngst" className="block text-sm font-medium text-gray-700">Angst (0-20)</label><input type="number" id="hsclAngst" value={newHsclAngst} onChange={(e) => setNewHsclAngst(e.target.value)} min="0" max="20" className={inputStd} /></div>
                            <div><label htmlFor="hsclDepression" className="block text-sm font-medium text-gray-700">Depression (0-24)</label><input type="number" id="hsclDepression" value={newHsclDepression} onChange={(e) => setNewHsclDepression(e.target.value)} min="0" max="24" className={inputStd} /></div>
                            <button onClick={handleAddHsclResult} className={`${btnPrimary} bg-teal-500 hover:bg-teal-600 h-10`}><PlusCircleIcon className="w-5 h-5 mr-2" /> Speichern</button>
                        </div>
                    </div>
                    <div className="mb-6">
                        <h5 className="text-md font-semibold text-gray-600 mb-2">Protokollierte HSCL-11 Ergebnisse:</h5>
                        <ul className="space-y-2">
                            {(selectedPatient.hscl11Results || []).map((result, index) => ( <li key={index} className="flex justify-between items-center bg-white border p-3 rounded-md shadow-sm"><div><span className="font-medium">{new Date(result.date).toLocaleDateString('de-DE')}</span>: <span className="text-sm text-gray-600 ml-2">Angst: {result.angst}</span>, <span className="text-sm text-gray-600 ml-2">Depression: {result.depression}</span>, <span className="text-sm text-gray-600 ml-2">Gesamt: {result.cumulative}</span></div><button onClick={() => handleRemoveHsclResult(result.date)} className={`${btnDanger} p-1 text-xs`}><Trash2 className="w-4 h-4" /></button></li>))}
                            {(selectedPatient.hscl11Results || []).length === 0 && <p className="text-sm text-gray-500">Keine HSCL-11 Ergebnisse erfasst.</p>}
                        </ul>
                    </div>
                    {(selectedPatient.hscl11Results || []).length > 0 && (
                        <div><h5 className="text-md font-semibold text-gray-600 mb-3">Verlaufsgraph HSCL-11</h5>
                        <div className="bg-white p-4 rounded-lg shadow" style={{ height: '400px' }}>
                            <ResponsiveContainer width="100%" height="100%"><LineChart data={(selectedPatient.hscl11Results || []).map(r => ({...r, date: new Date(r.date).toLocaleDateString('de-DE')}))} margin={{ top: 5, right: 30, left: 0, bottom: 5 }}><CartesianGrid strokeDasharray="3 3" /><XAxis dataKey="date" /><YAxis domain={[0, 44]} /><Tooltip /><Legend /><Line type="monotone" dataKey="angst" name="Angst" stroke="#8884d8" activeDot={{ r: 8 }} /><Line type="monotone" dataKey="depression" name="Depression" stroke="#82ca9d" /><Line type="monotone" dataKey="cumulative" name="Gesamt" stroke="#ffc658" /></LineChart></ResponsiveContainer>
                        </div></div>
                    )}
                  </div> 
                )}
                {activeDiagnostikSubTab === 'bdi2' && ( 
                  <div>
                    <h4 className="text-xl font-semibold text-gray-700 mb-4">BDI-II Ergebnisse</h4>
                    <div className="bg-gray-50 p-4 rounded-lg mb-6 shadow">
                        <h5 className="text-md font-semibold text-gray-600 mb-3">Neues BDI-II Ergebnis</h5>
                        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-3 items-end">
                            <div><label htmlFor="bdiDate" className="block text-sm font-medium text-gray-700">Datum</label><input type="date" id="bdiDate" value={newBdiDate} onChange={(e) => setNewBdiDate(e.target.value)} className={inputStd} /></div>
                            <div><label htmlFor="bdiScore" className="block text-sm font-medium text-gray-700">BDI-II Wert (0-63)</label><input type="number" id="bdiScore" value={newBdiScore} onChange={(e) => setNewBdiScore(e.target.value)} min="0" max="63" className={inputStd} /></div>
                            <button onClick={handleAddBdiResult} className={`${btnPrimary} h-10`}><PlusCircleIcon className="w-5 h-5 mr-2" /> Speichern</button>
                        </div>
                    </div>
                    <div className="mb-6">
                        <h5 className="text-md font-semibold text-gray-600 mb-2">Protokollierte BDI-II Ergebnisse:</h5>
                        <ul className="space-y-2">
                            {(selectedPatient.bdi2Results || []).map((result, index) => ( <li key={index} className="flex justify-between items-center bg-white border p-3 rounded-md shadow-sm"><div><span className="font-medium">{new Date(result.date).toLocaleDateString('de-DE')}</span>: <span className="text-gray-700">Score {result.score}</span></div><button onClick={() => handleRemoveBdiResult(result.date)} className={`${btnDanger} p-1 text-xs`}><Trash2 className="w-4 h-4" /></button></li>))}
                            {(selectedPatient.bdi2Results || []).length === 0 && <p className="text-sm text-gray-500">Keine BDI-II Ergebnisse erfasst.</p>}
                        </ul>
                    </div>
                    {(selectedPatient.bdi2Results || []).length > 0 && (
                        <div><h5 className="text-md font-semibold text-gray-600 mb-3">Verlaufsgraph BDI-II</h5>
                        <div className="bg-white p-4 rounded-lg shadow" style={{ height: '400px' }}>
                            <ResponsiveContainer width="100%" height="100%">
                                <LineChart data={(selectedPatient.bdi2Results || []).map(r => ({...r, date: new Date(r.date).toLocaleDateString('de-DE')}))} margin={{ top: 5, right: 30, left: 0, bottom: 20 }}>
                                    <CartesianGrid strokeDasharray="3 3" />
                                    <XAxis dataKey="date" />
                                    <YAxis domain={[0, 63]} ticks={bdiAxisTicks} allowDataOverflow={true}/>
                                    <Tooltip />
                                    <Legend verticalAlign="top" height={36}/>
                                    {bdiCutoffs.map(cutoff => ( <ReferenceArea key={cutoff.label} y1={cutoff.y1} y2={cutoff.y2} fill={cutoff.color} strokeOpacity={0.3} label={<CustomBdiLabel text={cutoff.label} y={cutoff.y2 - (cutoff.y2-cutoff.y1)/2} />}/> )) }
                                    <Line type="monotone" dataKey="score" name="BDI-II Score" stroke="#3b82f6" strokeWidth={2} activeDot={{ r: 8 }} />
                                </LineChart>
                            </ResponsiveContainer>
                        </div></div>
                    )}
                  </div> 
                )}
                {activeDiagnostikSubTab === 'fep2' && ( 
                  <div>
                    <h4 className="text-xl font-semibold text-gray-700 mb-4">FEP-2 Erfassung & Auswertung</h4>
                    <div className="bg-gray-50 p-4 rounded-lg mb-6 shadow">
                        <h5 className="text-md font-semibold text-gray-600 mb-3">Neues FEP-2 Ergebnis hinzufügen</h5>
                        <div className="mb-4"><label htmlFor="fepDate" className="block text-sm font-medium text-gray-700">Datum der Erhebung</label><input type="date" id="fepDate" value={newFepDate} onChange={(e) => setNewFepDate(e.target.value)} className={`${inputStd} w-full md:w-1/3`} /></div>
                        <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-x-4 gap-y-3 mb-4">
                            {fepItemScores.map((score, index) => ( <div key={index}><label htmlFor={`fepItem${index+1}`} className="block text-xs font-medium text-gray-600">Item {index+1}</label><input type="number" id={`fepItem${index+1}`} value={score === 0 ? '' : score} onChange={(e) => handleFepItemChange(index, e.target.value)} min="1" max="5" placeholder="1-5" className={`${inputStd} px-2 py-1`}/></div>))}
                        </div>
                        <button onClick={handleAddFepResult} className={btnPrimary}><PlusCircleIcon className="w-5 h-5 mr-2" /> FEP-2 Ergebnis speichern</button>
                    </div>
                    <div className="space-y-4">
                        <h5 className="text-md font-semibold text-gray-600 mb-2">Protokollierte FEP-2 Ergebnisse:</h5>
                        {(selectedPatient.fep2Results || []).length === 0 && <p className="text-sm text-gray-500">Keine FEP-2 Ergebnisse erfasst.</p>}
                        {(selectedPatient.fep2Results || []).map((result, index) => (
                            <div key={index} className="bg-white border p-4 rounded-md shadow-sm">
                                <div className="flex justify-between items-center mb-2"><p className="font-medium text-indigo-600">Datum: {new Date(result.date).toLocaleDateString('de-DE')}</p><button onClick={() => handleRemoveFepResult(result.date)} className={`${btnDanger} p-1 text-xs`}><Trash2 className="w-4 h-4" /></button></div>
                                <div className="grid grid-cols-1 md:grid-cols-2 gap-x-4 gap-y-1 text-sm">
                                    {Object.entries(result.calculatedScales).map(([key, value]) => ( <div key={key} className="flex justify-between"><span className="text-gray-600 capitalize">{key.replace(/_/g, ' ')}:</span><span className="font-medium text-gray-800">{value === null ? 'N/A (zu wenige Items)' : value} {key === 'gesamtbelastung' && value !== null && (value > 2.49 ? <span className="ml-1 text-red-500">(Belastet)</span> : <span className="ml-1 text-green-500">(Nicht Belastet)</span>)}</span></div>))}
                                </div>
                            </div>
                        ))}
                    </div>
                  </div> 
                )}
              </div>
            )}
          </div>
        )}

        <Modal isOpen={isPatientModalOpen} onClose={() => { setIsPatientModalOpen(false); setEditingPatient(null); }} title={editingPatient ? 'Patient bearbeiten' : 'Neuen Patienten anlegen'}><PatientForm patient={editingPatient} onSave={handleSavePatient} onCancel={() => { setIsPatientModalOpen(false); setEditingPatient(null); }} /></Modal>
        <ConfirmationModal isOpen={isConfirmDeleteModalOpen} onClose={() => setIsConfirmDeleteModalOpen(false)} onConfirm={confirmDeletePatient} title="Patient löschen" message={`Möchten Sie den Patienten wirklich endgültig löschen?`} />

        <Modal isOpen={diagnosisInfo.isOpen} onClose={() => setDiagnosisInfo(prev => ({...prev, isOpen: false}))} title={`${diagnosisInfo.source === 'local' ? 'Lokale ICD-10 Info' : 'Gemini ICD-10 Info'}: ${diagnosisInfo.code || ''}`} size="3xl">
            {diagnosisInfo.isLoading && <p className="text-center py-4">Informationen werden geladen...</p>}
            {diagnosisInfo.error && <p className="text-red-600 bg-red-100 p-3 rounded-md">Fehler: {diagnosisInfo.error}</p>}
            {!diagnosisInfo.isLoading && diagnosisInfo.source === 'gemini' && diagnosisInfo.info && (
                <div className="whitespace-pre-wrap text-sm text-gray-700 leading-relaxed p-2">{diagnosisInfo.info}</div>
            )}
            {!diagnosisInfo.isLoading && diagnosisInfo.source === 'local' && (
                <div className="text-sm text-gray-700 leading-relaxed p-2 space-y-3">
                    <h3 className="text-lg font-semibold text-indigo-700">{diagnosisInfo.name} <span className="text-gray-500 text-base">({diagnosisInfo.code}) {diagnosisInfo.sicherheit && `[${diagnosisInfo.sicherheit}]`}</span></h3>
                    {diagnosisInfo.zusatz_info_code && <p className="text-xs text-gray-500">Zusatz: {diagnosisInfo.zusatz_info_code}</p>}
                    {diagnosisInfo.beschreibung && <p><strong className="font-medium text-gray-800">Beschreibung:</strong> {diagnosisInfo.beschreibung}</p>}
                    {diagnosisInfo.inkl && diagnosisInfo.inkl.length > 0 && (
                        <div><strong className="font-medium text-gray-800">Inklusionen:</strong><ul className="list-disc list-inside ml-4">{diagnosisInfo.inkl.map((item, i) => <li key={i}>{item}</li>)}</ul></div>
                    )}
                    {diagnosisInfo.exkl && diagnosisInfo.exkl.length > 0 && (
                        <div><strong className="font-medium text-gray-800">Exklusionen:</strong><ul className="list-disc list-inside ml-4">{diagnosisInfo.exkl.map((item, i) => <li key={i}>{item}</li>)}</ul></div>
                    )}
                    {diagnosisInfo.hinweise && diagnosisInfo.hinweise.length > 0 && (
                        <div><strong className="font-medium text-gray-800">Hinweise:</strong><ul className="list-disc list-inside ml-4">{diagnosisInfo.hinweise.map((item, i) => <li key={i}>{item}</li>)}</ul></div>
                    )}
                </div>
            )}
        </Modal>

        {error && (<div className="fixed bottom-4 right-4 p-4 max-w-sm text-sm text-red-700 bg-red-100 border border-red-400 rounded-md shadow-lg z-[100]">{error}<button onClick={() => setError(null)} className="ml-4 mt-2 sm:mt-0 sm:ml-2 px-2 py-1 text-red-900 underline rounded-md hover:bg-red-200 text-xs">Ok</button></div>)}
      </main>
      <footer className="text-center py-6 text-sm text-gray-500 border-t border-gray-200 mt-10">Patientenverwaltung &copy; {new Date().getFullYear()}</footer>
    </div>
  );
}

const CustomBdiLabel = (props) => {
    const { x, y, width, text } = props;
    if (typeof x !== 'number' || isNaN(x) || 
        typeof y !== 'number' || isNaN(y) ||
        typeof width !== 'number' || isNaN(width) || width <= 0) {
        return null; 
    }
    const labelX = x + width / 2;
    return ( <text x={labelX} y={y} fill="#666" textAnchor="middle" dy={-4} fontSize="10">{text}</text> );
};

export default App;
