/* global __GEMINI_API_KEY__ */
import React, { useState, useEffect, useCallback } from 'react';
import { initializeApp } from 'firebase/app';
import { getAuth, signInAnonymously, onAuthStateChanged } from 'firebase/auth'; 
import { getFirestore, collection, doc, addDoc, getDocs, setDoc, deleteDoc, onSnapshot, query, writeBatch } from 'firebase/firestore';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, ReferenceArea } from 'recharts';

// Lucide-React Icons
const UserPlus = ({ className = "w-6 h-6" }) => ( <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="M16 21v-2a4 4 0 0 0-4-4H6a4 4 0 0 0-4 4v2" /><circle cx="9" cy="7" r="4" /><line x1="19" x2="19" y1="8" y2="14" /><line x1="22" x2="16" y1="11" y2="11" /></svg>);
const Edit3 = ({ className = "w-6 h-6" }) => ( <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="M12 20h9" /><path d="M16.5 3.5a2.121 2.121 0 0 1 3 3L7 19l-4 1 1-4L16.5 3.5z" /></svg>);
const Trash2 = ({ className = "w-6 h-6" }) => ( <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><polyline points="3 6 5 6 21 6" /><path d="M19 6v14a2 2 0 0 1-2 2H7a2 2 0 0 1-2-2V6m3 0V4a2 2 0 0 1 2-2h4a2 2 0 0 1 2 2v2" /><line x1="10" y1="11" x2="10" y2="17" /><line x1="14" y1="11" x2="14" y2="17" /></svg>);
const PlusCircle = ({ className = "w-6 h-6" }) => ( <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><circle cx="12" cy="12" r="10" /><line x1="12" y1="8" x2="12" y2="16" /><line x1="8" y1="12" x2="16" y2="12" /></svg>);
const XCircle = ({ className = "w-6 h-6" }) => ( <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><circle cx="12" cy="12" r="10"></circle><line x1="15" y1="9" x2="9" y2="15"></line><line x1="9" y1="9" x2="15" y2="15"></line></svg>);
const Save = ({ className = "w-6 h-6" }) => ( <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="M19 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h11l5 5v11a2 2 0 0 1-2 2z" /><polyline points="17 21 17 13 7 13 7 21" /><polyline points="7 3 7 8 15 8" /></svg>);
const AlertTriangle = ({ className = "w-6 h-6" }) => ( <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="M10.29 3.86L1.82 18a2 2 0 0 0 1.71 3h16.94a2 2 0 0 0 1.71-3L13.71 3.86a2 2 0 0 0-3.42 0z"></path><line x1="12" y1="9" x2="12" y2="13"></line><line x1="12" y1="17" x2="12.01" y2="17"></line></svg>);
const InfoIcon = ({ className = "w-5 h-5" }) => ( <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><circle cx="12" cy="12" r="10"></circle><line x1="12" y1="16" x2="12" y2="12"></line><line x1="12" y1="8" x2="12.01" y2="8"></line></svg>);
const FileJson2 = ({ className = "w-5 h-5" }) => (<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="M4 22h14a2 2 0 0 0 2-2V7.5L14.5 2H6a2 2 0 0 0-2 2v4"/><path d="M14 2v6h6"/><path d="M4 12h1.6a2 2 0 0 0 1.9-3.4L6 6"/><path d="M4 18h1.6a2 2 0 0 1 1.9 3.4L6 24"/><path d="M12 12h1.6a2 2 0 0 1 1.9-3.4L14 6"/><path d="M12 18h1.6a2 2 0 0 0 1.9 3.4L14 24"/></svg>);

// Ihre Firebase Konfiguration:
const firebaseConfig = { 
  apiKey: "AIzaSyDZ_qqqKW7WDCZ9botek2VBwXNMQO3JYwU",
  authDomain: "psychotherapie0512.firebaseapp.com",
  projectId: "psychotherapie0512",
  storageBucket: "psychotherapie0512.firebasestorage.app", 
  messagingSenderId: "131279046361",
  appId: "1:131279046361:web:e5dd2d18b206fb820b60c8"
};

const app = initializeApp(firebaseConfig);
const auth = getAuth(app);
const db = getFirestore(app);
const firestoreAppId = 'default-patient-app-v4'; // Versions-Update für neue Datenstruktur

// Initial patient data
const initialPatientsData = [
  { 
    name: "Kern", 
    chiffre: "K040498", 
    therapyType: "LZT", 
    approvedHours: 60, 
    conductedHours: 24, 
    diagnoses: ["F00.0*"], 
    sessions: [], 
    hscl11Results: [], 
    bdi2Results: [], 
    fep2Results: [],
    krankheitsanamnese: "", 
    lebensgeschichte: "",   
    problemanalyseMakro: "" 
  },
];

// Modal, ConfirmationModal, PatientForm (wie zuvor, PatientForm stellt sicher, dass neue Felder in formData sind)
const Modal = ({ isOpen, onClose, title, children, size = "lg" }) => { if (!isOpen) return null; const sizeClasses = {sm: 'max-w-sm', md: 'max-w-md', lg: 'max-w-lg', xl: 'max-w-xl', '2xl': 'max-w-2xl', '3xl': 'max-w-3xl', '4xl': 'max-w-4xl', '5xl': 'max-w-5xl'}; return (<div className="fixed inset-0 bg-black bg-opacity-60 backdrop-blur-sm flex justify-center items-center z-50 p-4"><div className={`bg-white p-6 rounded-lg shadow-xl w-full ${sizeClasses[size]} max-h-[90vh] overflow-y-auto`}><div className="flex justify-between items-center mb-4"><h2 className="text-2xl font-semibold text-gray-800">{title}</h2><button onClick={onClose} className="text-gray-500 hover:text-gray-700"><XCircle className="w-7 h-7" /></button></div>{children}</div></div>);};
const ConfirmationModal = ({ isOpen, onClose, onConfirm, title, message }) => { if (!isOpen) return null; return (<Modal isOpen={isOpen} onClose={onClose} title={title} size="sm"><div className="text-center"><AlertTriangle className="mx-auto mb-4 w-12 h-12 text-red-500" /><p className="text-gray-700 mb-6">{message}</p><div className="flex justify-center space-x-4"><button onClick={onClose} className="px-4 py-2 text-sm font-medium text-gray-700 bg-gray-200 rounded-md hover:bg-gray-300 focus:outline-none">Abbrechen</button><button onClick={onConfirm} className="px-4 py-2 text-sm font-medium text-white bg-red-600 rounded-md hover:bg-red-700 focus:outline-none">Bestätigen</button></div></div></Modal>);};
const PatientForm = ({ patient, onSave, onCancel }) => {
  const [formData, setFormData] = useState(
    patient || { name: '', chiffre: '', therapyType: 'LZT', approvedHours: 0, conductedHours: 0, diagnoses: [], sessions: [], hscl11Results: [], bdi2Results: [], fep2Results: [], krankheitsanamnese: "", lebensgeschichte: "", problemanalyseMakro: "" }
  );
  useEffect(() => {
    if (patient) {
      setFormData({ 
        ...patient, 
        hscl11Results: patient.hscl11Results || [], 
        bdi2Results: patient.bdi2Results || [], 
        fep2Results: patient.fep2Results || [],
        krankheitsanamnese: patient.krankheitsanamnese || "",
        lebensgeschichte: patient.lebensgeschichte || "",
        problemanalyseMakro: patient.problemanalyseMakro || ""
      });
    } else {
      setFormData({ name: '', chiffre: '', therapyType: 'LZT', approvedHours: 0, conductedHours: 0, diagnoses: [], sessions: [], hscl11Results: [], bdi2Results: [], fep2Results: [], krankheitsanamnese: "", lebensgeschichte: "", problemanalyseMakro: "" });
    }
  }, [patient]);
  const handleChange = (e) => { const { name, value, type } = e.target; setFormData((prev) => ({ ...prev, [name]: type === 'number' ? parseInt(value, 10) || 0 : value })); };
  const handleSubmit = (e) => { e.preventDefault(); onSave(formData); };
  return ( <form onSubmit={handleSubmit} className="space-y-4"><div><label htmlFor="name" className="block text-sm font-medium text-gray-700">Name</label><input type="text" name="name" id="name" value={formData.name} onChange={handleChange} required className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm" /></div><div><label htmlFor="chiffre" className="block text-sm font-medium text-gray-700">Chiffre</label><input type="text" name="chiffre" id="chiffre" value={formData.chiffre} onChange={handleChange} required className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm" /></div><div><label htmlFor="therapyType" className="block text-sm font-medium text-gray-700">Therapieform</label><select name="therapyType" id="therapyType" value={formData.therapyType} onChange={handleChange} className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"><option value="LZT">Langzeittherapie (LZT)</option><option value="KZT">Kurzzeittherapie (KZT)</option></select></div><div><label htmlFor="approvedHours" className="block text-sm font-medium text-gray-700">Genehmigte Stunden</label><input type="number" name="approvedHours" id="approvedHours" value={formData.approvedHours} onChange={handleChange} min="0" className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm" /></div><div><label htmlFor="conductedHours" className="block text-sm font-medium text-gray-700">Durchgeführte Stunden</label><input type="number" name="conductedHours" id="conductedHours" value={formData.conductedHours} onChange={handleChange} min="0" className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm" /></div><div className="flex justify-end space-x-3 pt-4"><button type="button" onClick={onCancel} className="px-4 py-2 text-sm font-medium text-gray-700 bg-gray-100 rounded-md hover:bg-gray-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-500">Abbrechen</button><button type="submit" className="px-4 py-2 text-sm font-medium text-white bg-indigo-600 rounded-md hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 flex items-center"><Save className="w-4 h-4 mr-2" /> Speichern</button></div></form>);
};

// FEP-2 Definitionen und Berechnungslogik (wie zuvor)
const FEP2_ITEMS_TO_REVERSE = [1, 2, 5, 9, 14, 15, 19, 21, 22, 27, 29, 30, 39]; 
const FEP2_SKALEN_DEFINITION = { WOHLBEFINDEN: { items: [1, 5, 9, 14, 22, 30, 39], minItemsRequired: 7 }, BESCHWERDEN: { items: [4, 6, 7, 8, 10, 12, 23, 24, 31, 34, 38], minItemsRequired: 10 }, BESCHWERDEN_ANGST: { items: [4, 8, 12, 23], minItemsRequired: 4 }, BESCHWERDEN_DEPRESSIVITAET: { items: [7, 10, 24, 31, 34, 38], minItemsRequired: 5 }, INTERPERSONALE_BEZIEHUNG: { items: [11, 13, 16, 18, 20, 25, 26, 28, 32, 33, 36, 40], minItemsRequired: 11 }, INTERPERSONALE_INTROVERTIERT_SCHEU: { items: [28, 36, 40], minItemsRequired: 3 }, INTERPERSONALE_AUSNUTZBAR_NACHGIEBIG: { items: [13, 20, 26], minItemsRequired: 3 }, INTERPERSONALE_KONKURRIEREND_UNTERSTUETZEND: { items: [11, 16, 33], minItemsRequired: 3 }, INTERPERSONALE_SELBSTUNSICHER_ZURUECKHALTEND: { items: [18, 25, 32], minItemsRequired: 3 }, KONGRUENZ: { items: [2, 3, 15, 17, 19, 21, 27, 29, 35, 37], minItemsRequired: 8 }, };
const calculateFep2Scores = (itemScoresRaw) => { const itemScores = itemScoresRaw.map((score, index) => { const itemNum = index + 1; if (FEP2_ITEMS_TO_REVERSE.includes(itemNum) && score > 0 && score <=5) { return 6 - score; } return score; }); const answeredItemsCount = itemScores.filter(score => score > 0 && score <= 5).length; const calculatedScales = {}; if (answeredItemsCount >= 32) { const sum = itemScores.reduce((acc, score) => acc + (score > 0 && score <=5 ? score : 0), 0); calculatedScales.gesamtbelastung = answeredItemsCount > 0 ? parseFloat((sum / answeredItemsCount).toFixed(2)) : null; } else { calculatedScales.gesamtbelastung = null; } for (const skalaName in FEP2_SKALEN_DEFINITION) { const def = FEP2_SKALEN_DEFINITION[skalaName]; let sum = 0; let count = 0; def.items.forEach(itemNum => { const score = itemScores[itemNum - 1]; if (score > 0 && score <= 5) { sum += score; count++; } }); if (count >= def.minItemsRequired) { calculatedScales[skalaName.toLowerCase()] = parseFloat((sum / count).toFixed(2)); } else { calculatedScales[skalaName.toLowerCase()] = null; } } return calculatedScales; };

// Gemini API Hilfsfunktion (wie zuvor)
async function generateGeminiText(prompt) { let chatHistory = [{ role: "user", parts: [{ text: prompt }] }]; const payload = { contents: chatHistory }; const GEMINI_API_KEY = typeof __GEMINI_API_KEY__ !== 'undefined' ? __GEMINI_API_KEY__ : "AIzaSyCWQFze_8cKDZ6W2DTqfqIcvhcsKWfuZLw"; const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.0-flash:generateContent?key=${GEMINI_API_KEY}`; try { const response = await fetch(apiUrl, { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify(payload) }); if (!response.ok) { const errorData = await response.json(); console.error("Gemini API error details:", errorData); const errorMessage = errorData.error?.message || `API request failed with status ${response.status}`; throw new Error(errorMessage); } const result = await response.json(); if (result.candidates && result.candidates.length > 0 && result.candidates[0].content && result.candidates[0].content.parts && result.candidates[0].content.parts.length > 0) { return result.candidates[0].content.parts[0].text; } else if (result.candidates && result.candidates.length > 0 && result.candidates[0].finishReason === "SAFETY") { console.warn("Gemini API response blocked due to safety reasons."); return "Die Antwort wurde aufgrund von Sicherheitsrichtlinien blockiert."; } else { console.error("Unexpected Gemini API response structure:", result); throw new Error("Unerwartete Antwortstruktur von der API."); } } catch (error) { console.error("Error calling Gemini API:", error); throw error; } }

// Haupt-App Komponente
function App() {
  const [patients, setPatients] = useState([]);
  const [selectedPatient, setSelectedPatient] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null); 
  const [userId, setUserId] = useState(null);
  const [isAuthReady, setIsAuthReady] = useState(false);
  const [isPatientModalOpen, setIsPatientModalOpen] = useState(false);
  const [editingPatient, setEditingPatient] = useState(null);
  const [isConfirmDeleteModalOpen, setIsConfirmDeleteModalOpen] = useState(false);
  const [patientToDeleteId, setPatientToDeleteId] = useState(null);

  const [newDiagnosis, setNewDiagnosis] = useState('');
  const [newDiagnosisCode, setNewDiagnosisCode] = useState(''); 
  const [lookedUpDiagnosisName, setLookedUpDiagnosisName] = useState(''); 

  const [newSessionDate, setNewSessionDate] = useState(new Date().toISOString().split('T')[0]);
  const [newSessionNotes, setNewSessionNotes] = useState('');
  const [activeDetailTab, setActiveDetailTab] = useState('details');
  const [activeDiagnostikSubTab, setActiveDiagnostikSubTab] = useState('hscl11'); 

  const [newHsclDate, setNewHsclDate] = useState(new Date().toISOString().split('T')[0]);
  const [newHsclAngst, setNewHsclAngst] = useState(''); 
  const [newHsclDepression, setNewHsclDepression] = useState(''); 

  const [newBdiDate, setNewBdiDate] = useState(new Date().toISOString().split('T')[0]);
  const [newBdiScore, setNewBdiScore] = useState(''); 

  const [newFepDate, setNewFepDate] = useState(new Date().toISOString().split('T')[0]);
  const [fepItemScores, setFepItemScores] = useState(Array(40).fill(0)); 

  const [sessionSummaries, setSessionSummaries] = useState({}); 
  const [diagnosisInfo, setDiagnosisInfo] = useState({ code: '', name: '', beschreibung: '', inkl: [], exkl: [], hinweise: [], zusatz_info_code: '', isLoading: false, error: null, isOpen: false, source: '' });

  const [localIcd10Data, setLocalIcd10Data] = useState({}); 

  useEffect(() => {
    fetch('/icd10f_data.json') 
      .then(response => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status} - Stellen Sie sicher, dass 'icd10f_data.json' im 'public' Ordner liegt.`);
        }
        return response.json();
      })
      .then(data => {
        const indexedData = {};
        data.forEach(item => {
          if (item.code) { 
            indexedData[item.code.toUpperCase()] = item; 
          }
        });
        setLocalIcd10Data(indexedData);
        console.log("Lokale ICD-10 Daten geladen und indiziert.");
      })
      .catch(error => {
        console.error("Fehler beim Laden der lokalen ICD-10 Daten:", error);
        setError("Lokale ICD-10 Daten konnten nicht geladen werden. " + error.message);
      });
  }, []); 

  const findIcd10CodeLocal = useCallback((codeToFind) => {
    const upperCodeToFind = codeToFind.toUpperCase();
    let found = localIcd10Data[upperCodeToFind];
    if (found) return found;
    const baseCode = upperCodeToFind.replace(/(\.-\*|\.\*|\*|-)$/, '');
    found = localIcd10Data[`${baseCode}.-*`] || localIcd10Data[`${baseCode}.*`] || localIcd10Data[baseCode];
    if (found) return found;
    const possibleCategoryCode = Object.keys(localIcd10Data).find(
        key => key.startsWith(upperCodeToFind) && (localIcd10Data[key].code_kategorie || localIcd10Data[key].code)
    );
    if (possibleCategoryCode) return localIcd10Data[possibleCategoryCode];
    return null;
  }, [localIcd10Data]);

  useEffect(() => {
    if (newDiagnosisCode.trim() !== '') {
        const found = findIcd10CodeLocal(newDiagnosisCode.trim().toUpperCase());
        if (found && found.name) {
            setLookedUpDiagnosisName(found.name);
        } else {
            setLookedUpDiagnosisName('');
        }
    } else {
        setLookedUpDiagnosisName('');
    }
  }, [newDiagnosisCode, findIcd10CodeLocal]);


  const getPatientsCollectionPath = useCallback(() => { 
    if (!userId) return null; 
    return `/artifacts/${firestoreAppId}/users/${userId}/patients`; 
  }, [userId]);

  useEffect(() => { 
    const unsubscribe = onAuthStateChanged(auth, async (user) => { 
      if (user) { 
        setUserId(user.uid); 
      } else { 
        try { 
            await signInAnonymously(auth); 
        } catch (e) { 
          console.error("Error signing in anonymously: ", e); 
          setError("Fehler bei der anonymen Authentifizierung."); 
        } 
      } 
      setIsAuthReady(true); 
    }); 
    return () => unsubscribe(); 
  }, []);

  const seedInitialData = useCallback(async () => { 
    if (!userId) return; 
    const collectionPath = getPatientsCollectionPath(); 
    if (!collectionPath) return; 
    try { 
      const patientsCollection = collection(db, collectionPath); 
      const querySnapshot = await getDocs(patientsCollection); 
      if (querySnapshot.empty) { 
        const batch = writeBatch(db); 
        initialPatientsData.forEach(patientData => { 
          const newPatientRef = doc(patientsCollection); 
          batch.set(newPatientRef, { 
            ...patientData, 
            hscl11Results: patientData.hscl11Results || [], 
            bdi2Results: patientData.bdi2Results || [], 
            fep2Results: patientData.fep2Results || [],
            krankheitsanamnese: patientData.krankheitsanamnese || "",
            lebensgeschichte: patientData.lebensgeschichte || "",
            problemanalyseMakro: patientData.problemanalyseMakro || ""
          }); 
        }); 
        await batch.commit(); 
        console.log("Initiale Daten erfolgreich geschrieben.");
      } else {
        console.log("Vorhandene Patientendaten gefunden, Seed wird übersprungen.");
      }
    } catch (err) { 
      console.error("Error seeding initial data:", err); 
      setError("Fehler beim Laden der initialen Daten."); 
    } 
  }, [userId, getPatientsCollectionPath]);

  useEffect(() => {
    if (!isAuthReady || !userId) { 
      if(isAuthReady && !userId) setError("Authentifizierung fehlgeschlagen. Bitte laden Sie die Seite neu.");
      return; 
    }
    const collectionPath = getPatientsCollectionPath(); 
    if (!collectionPath) {
        console.log("Collection path ist nicht verfügbar für Firestore Listener.");
        return;
    }

    setIsLoading(true); 
    seedInitialData(); 

    const patientsQuery = query(collection(db, collectionPath));
    const unsubscribe = onSnapshot(patientsQuery, (snapshot) => {
      let patientsData = snapshot.docs.map(docSnap => ({ 
        id: docSnap.id, 
        ...docSnap.data(), 
        hscl11Results: docSnap.data().hscl11Results || [], 
        bdi2Results: docSnap.data().bdi2Results || [], 
        fep2Results: docSnap.data().fep2Results || [],
        krankheitsanamnese: docSnap.data().krankheitsanamnese || "",
        lebensgeschichte: docSnap.data().lebensgeschichte || "",
        problemanalyseMakro: docSnap.data().problemanalyseMakro || ""
      }));
      patientsData.sort((a, b) => a.name.localeCompare(b.name)); 
      setPatients(patientsData); 
      setIsLoading(false); 
    }, (err) => {
      console.error("Error fetching patients:", err);
      setError("Fehler beim Laden der Patientendaten. Stellen Sie sicher, dass Firestore korrekt konfiguriert ist.");
      setIsLoading(false);
    });

    return () => unsubscribe(); 
  }, [isAuthReady, userId, getPatientsCollectionPath, seedInitialData]); 

  useEffect(() => {
    if (selectedPatient && selectedPatient.id && patients.length > 0) {
        const currentPatientDataFromList = patients.find(p => p.id === selectedPatient.id);
        if (currentPatientDataFromList) {
            if (JSON.stringify(currentPatientDataFromList) !== JSON.stringify(selectedPatient)) {
                 setSelectedPatient(currentPatientDataFromList);
            }
        } else {
            setSelectedPatient(null);
        }
    }
  }, [patients, selectedPatient]); 


  const handleSavePatient = async (patientData) => { 
    if (!userId) { setError("Benutzer nicht authentifiziert."); return; } 
    const collectionPath = getPatientsCollectionPath(); 
    if (!collectionPath) return; 
    const dataToSave = { 
        ...patientData, 
        hscl11Results: patientData.hscl11Results || [], 
        bdi2Results: patientData.bdi2Results || [], 
        fep2Results: patientData.fep2Results || [],
        krankheitsanamnese: patientData.krankheitsanamnese || "",
        lebensgeschichte: patientData.lebensgeschichte || "",
        problemanalyseMakro: patientData.problemanalyseMakro || ""
    }; 
    try { 
      if (editingPatient && editingPatient.id) { 
        const patientRef = doc(db, collectionPath, editingPatient.id); 
        await setDoc(patientRef, dataToSave, { merge: true }); 
      } else { 
        await addDoc(collection(db, collectionPath), dataToSave); 
      } 
      setIsPatientModalOpen(false); 
      setEditingPatient(null); 
    } catch (err) { 
      console.error("Error saving patient:", err); 
      setError("Fehler beim Speichern des Patienten."); 
    } 
  };
  const openDeleteConfirmation = (patientId) => { setPatientToDeleteId(patientId); setIsConfirmDeleteModalOpen(true); };
  const confirmDeletePatient = async () => { 
    if (!patientToDeleteId || !userId) { setError("Löschvorgang fehlgeschlagen."); setIsConfirmDeleteModalOpen(false); return; } 
    const collectionPath = getPatientsCollectionPath(); 
    if (!collectionPath) return; 
    try { 
      const patientRef = doc(db, collectionPath, patientToDeleteId); 
      await deleteDoc(patientRef); 
      if (selectedPatient && selectedPatient.id === patientToDeleteId) { 
        setSelectedPatient(null); 
      } 
      setPatientToDeleteId(null); 
      setIsConfirmDeleteModalOpen(false); 
    } catch (err) { 
      console.error("Error deleting patient:", err); 
      setError("Fehler beim Löschen des Patienten."); 
      setIsConfirmDeleteModalOpen(false); 
    } 
  };

  const handleAddDiagnosis = async () => {
    const codeToAdd = newDiagnosisCode.trim().toUpperCase();
    if (!selectedPatient || !codeToAdd || !userId) return;
    const collectionPath = getPatientsCollectionPath(); if (!collectionPath) return;
    const diagnosisString = newDiagnosis.trim() ? `${codeToAdd}: ${newDiagnosis.trim()}` : (lookedUpDiagnosisName ? `${codeToAdd}: ${lookedUpDiagnosisName}` : codeToAdd);
    const updatedDiagnoses = [...(selectedPatient.diagnoses || []), diagnosisString];
    const patientRef = doc(db, collectionPath, selectedPatient.id);
    try { 
        await setDoc(patientRef, { diagnoses: updatedDiagnoses }, { merge: true }); 
        setNewDiagnosisCode(''); 
        setNewDiagnosis('');      
        setLookedUpDiagnosisName(''); 
    } 
    catch (err) { console.error("Error adding diagnosis:", err); setError("Fehler beim Hinzufügen der Diagnose.");}
  };
  const handleRemoveDiagnosis = async (diagnosisStringToRemove) => { 
    if (!selectedPatient || !userId) return;
    const collectionPath = getPatientsCollectionPath(); if (!collectionPath) return;
    const updatedDiagnoses = selectedPatient.diagnoses.filter(d => d !== diagnosisStringToRemove);
    const patientRef = doc(db, collectionPath, selectedPatient.id);
    try { await setDoc(patientRef, { diagnoses: updatedDiagnoses }, { merge: true }); }
    catch (err) { console.error("Error removing diagnosis:", err); setError("Fehler beim Entfernen der Diagnose.");}
  };

  const handleAddSession = async () => { 
    if (!selectedPatient || !newSessionNotes.trim() || !userId) return;
    const collectionPath = getPatientsCollectionPath(); if (!collectionPath) return;
    const newSession = { date: newSessionDate, notes: newSessionNotes.trim() };
    const updatedSessions = [...(selectedPatient.sessions || []), newSession];
    updatedSessions.sort((a, b) => new Date(b.date) - new Date(a.date)); 
    const patientRef = doc(db, collectionPath, selectedPatient.id);
    try { await setDoc(patientRef, { sessions: updatedSessions }, { merge: true }); setNewSessionNotes(''); }
    catch (err) { console.error("Error adding session:", err); setError("Fehler beim Hinzufügen der Sitzung.");}
   };
  const handleUpdateConductedHours = async (patient, hoursStr) => { 
    if (!patient || !userId) return;
    const hours = parseInt(hoursStr, 10);
    if (isNaN(hours) || hours < 0) {
        setError("Ungültige Stundenzahl.");
        const originalPatientData = patients.find(p => p.id === patient.id);
        if (originalPatientData) {
            setSelectedPatient(prev => ({...prev, conductedHours: originalPatientData.conductedHours}));
        }
        return;
    }
    const collectionPath = getPatientsCollectionPath(); if (!collectionPath) return;
    const patientRef = doc(db, collectionPath, patient.id);
    try { await setDoc(patientRef, { conductedHours: hours }, { merge: true }); } 
    catch (err) { console.error("Error updating conducted hours:", err); setError("Fehler beim Aktualisieren der Stunden.");}
  };
  const handleAddHsclResult = async () => { 
    if (!selectedPatient || !newHsclDate || !userId) return;
    const collectionPath = getPatientsCollectionPath(); if (!collectionPath) return;
    const angst = parseInt(newHsclAngst, 10);
    const depression = parseInt(newHsclDepression, 10);
    if (isNaN(angst) || angst < 0 || angst > 20 || isNaN(depression) || depression < 0 || depression > 24) {
        setError("Ungültige HSCL-Werte. Angst (0-20), Depression (0-24)."); return;
    }
    const newResult = { date: newHsclDate, angst: angst, depression: depression, cumulative: angst + depression };
    const updatedHsclResults = [...(selectedPatient.hscl11Results || []), newResult];
    updatedHsclResults.sort((a, b) => new Date(a.date) - new Date(b.date)); 
    const patientRef = doc(db, collectionPath, selectedPatient.id);
    try { await setDoc(patientRef, { hscl11Results: updatedHsclResults }, { merge: true });
      setNewHsclAngst(''); setNewHsclDepression(''); setError(null); 
    } catch (err) { console.error("Error adding HSCL result:", err); setError("Fehler beim Hinzufügen des HSCL-Ergebnisses."); }
  };
  const handleRemoveHsclResult = async (dateToRemove) => { 
    if (!selectedPatient || !userId) return;
    const collectionPath = getPatientsCollectionPath(); if (!collectionPath) return;
    const updatedHsclResults = selectedPatient.hscl11Results.filter(r => r.date !== dateToRemove);
    const patientRef = doc(db, collectionPath, selectedPatient.id);
    try { await setDoc(patientRef, { hscl11Results: updatedHsclResults }, { merge: true }); }
    catch (err) { console.error("Error removing HSCL result:", err); setError("Fehler beim Entfernen des HSCL-Ergebnisses."); }
  };
  const handleAddBdiResult = async () => { 
    if (!selectedPatient || !newBdiDate || !userId) return;
    const collectionPath = getPatientsCollectionPath(); if (!collectionPath) return;
    const score = parseInt(newBdiScore, 10);
    if (isNaN(score) || score < 0 || score > 63) { setError("Ungültiger BDI-II Wert (0-63)."); return; }
    const newResult = { date: newBdiDate, score: score };
    const updatedBdiResults = [...(selectedPatient.bdi2Results || []), newResult];
    updatedBdiResults.sort((a, b) => new Date(a.date) - new Date(b.date)); 
    const patientRef = doc(db, collectionPath, selectedPatient.id);
    try { await setDoc(patientRef, { bdi2Results: updatedBdiResults }, { merge: true });
      setNewBdiScore(''); setError(null); 
    } catch (err) { console.error("Error adding BDI-II result:", err); setError("Fehler beim Hinzufügen des BDI-II Ergebnisses."); }
  };
  const handleRemoveBdiResult = async (dateToRemove) => { 
    if (!selectedPatient || !userId) return;
    const collectionPath = getPatientsCollectionPath(); if (!collectionPath) return;
    const updatedBdiResults = selectedPatient.bdi2Results.filter(r => r.date !== dateToRemove);
    const patientRef = doc(db, collectionPath, selectedPatient.id);
    try { await setDoc(patientRef, { bdi2Results: updatedBdiResults }, { merge: true }); }
    catch (err) { console.error("Error removing BDI-II result:", err); setError("Fehler beim Entfernen des BDI-II Ergebnisses."); }
  };
  const handleFepItemChange = (index, value) => { 
    const newScores = [...fepItemScores];
    newScores[index] = value === '' ? 0 : parseInt(value, 10); 
    if (isNaN(newScores[index]) || newScores[index] < 0 || newScores[index] > 5) newScores[index] = 0; 
    setFepItemScores(newScores);
  };
  const handleAddFepResult = async () => { 
    if (!selectedPatient || !newFepDate || !userId) return;
    const collectionPath = getPatientsCollectionPath(); if (!collectionPath) return;
    const answeredCount = fepItemScores.filter(s => s >= 1 && s <= 5).length;
    if (answeredCount < 32) { setError("Bitte beantworten Sie mindestens 80% (32) der FEP-2 Items."); return; }
    const calculatedScales = calculateFep2Scores(fepItemScores);
    const newResult = { date: newFepDate, itemScores: [...fepItemScores], calculatedScales };
    const updatedFepResults = [...(selectedPatient.fep2Results || []), newResult];
    updatedFepResults.sort((a, b) => new Date(a.date) - new Date(b.date)); 
    const patientRef = doc(db, collectionPath, selectedPatient.id);
    try { await setDoc(patientRef, { fep2Results: updatedFepResults }, { merge: true });
      setFepItemScores(Array(40).fill(0)); setError(null); 
    } catch (err) { console.error("Error adding FEP-2 result:", err); setError("Fehler beim Hinzufügen des FEP-2 Ergebnisses."); }
  };
  const handleRemoveFepResult = async (dateToRemove) => { 
    if (!selectedPatient || !userId) return;
    const collectionPath = getPatientsCollectionPath(); if (!collectionPath) return;
    const updatedFepResults = selectedPatient.fep2Results.filter(r => r.date !== dateToRemove);
    const patientRef = doc(db, collectionPath, selectedPatient.id);
    try { await setDoc(patientRef, { fep2Results: updatedFepResults }, { merge: true }); }
    catch (err) { console.error("Error removing FEP-2 result:", err); setError("Fehler beim Entfernen des FEP-2 Ergebnisses."); }
  };
  const fetchSessionSummary = async (sessionNotes, sessionIndex) => { 
    setSessionSummaries(prev => ({ ...prev, [sessionIndex]: { ...prev[sessionIndex], isLoading: true, error: null } }));
    const prompt = `Fasse die folgenden Therapiesitzungsnotizen prägnant zusammen. Konzentriere dich auf die wichtigsten Themen, Interventionen und den Fortschritt des Patienten:\n\n"${sessionNotes}"`;
    try { const summary = await generateGeminiText(prompt); setSessionSummaries(prev => ({ ...prev, [sessionIndex]: { summary: summary, isLoading: false, error: null } })); } 
    catch (e) { setSessionSummaries(prev => ({ ...prev, [sessionIndex]: { ...prev[sessionIndex], isLoading: false, error: e.message } })); setError(`Fehler bei der Zusammenfassung: ${e.message}`); }
  };
  const showDiagnosisInfo = (diagnosisString) => {
    const codeMatch = diagnosisString.match(/^([A-Z][0-9]{1,2}(\.[0-9A-Z]{1,2})?[*\-!]?)/i); 
    const code = codeMatch ? codeMatch[0].toUpperCase() : diagnosisString.toUpperCase();
    const localData = findIcd10CodeLocal(code);
    if (localData) {
        setDiagnosisInfo({ code: localData.code || code, name: localData.name || '', beschreibung: localData.beschreibung || localData.beschreibung_kategorie || '', inkl: localData.inkl || [], exkl: localData.exkl || [], hinweise: localData.hinweise || localData.hinweise_kategorie || [], zusatz_info_code: localData.zusatz_info_code || '', isLoading: false, error: null, isOpen: true, source: 'local' });
    } else {
        fetchDiagnosisInfoGemini(code);
    }
  };
  const fetchDiagnosisInfoGemini = async (diagnosisCode) => { 
    setDiagnosisInfo({ code: diagnosisCode, info: '', isLoading: true, error: null, isOpen: true, source: 'gemini' });
    const prompt = `Erkläre die ICD-10 Diagnose '${diagnosisCode}' kurz und verständlich für einen Laien. Nenne auch typische Symptome und ggf. wichtige Differentialdiagnosen oder Komorbiditäten, falls relevant. Antworte auf Deutsch.`;
    try { const info = await generateGeminiText(prompt); setDiagnosisInfo(prev => ({ ...prev, info: info, isLoading: false })); } 
    catch (e) { setDiagnosisInfo(prev => ({ ...prev, isLoading: false, error: e.message })); setError(`Fehler beim Abrufen der Diagnose-Info via Gemini: ${e.message}`);}
  };
  const handleKrankheitsmodellChange = async (fieldName, value) => {
    if (!selectedPatient || !userId) return;
    const collectionPath = getPatientsCollectionPath();
    if (!collectionPath) return;
    const patientRef = doc(db, collectionPath, selectedPatient.id);
    try {
        await setDoc(patientRef, { [fieldName]: value }, { merge: true });
    } catch (err) {
        console.error(`Error updating ${fieldName}:`, err);
        setError(`Fehler beim Speichern von ${fieldName}.`);
    }
  };


  const totalApprovedHours = patients.reduce((sum, p) => sum + (p.approvedHours || 0), 0);
  const totalConductedHours = patients.reduce((sum, p) => sum + (p.conductedHours || 0), 0);
  const overallProgressTarget = 600;
  const overallProgress = overallProgressTarget > 0 ? (totalConductedHours / overallProgressTarget) * 100 : 0;

  if (!isAuthReady) return <div className="flex justify-center items-center h-screen text-xl font-semibold text-gray-700">Authentifizierung wird initialisiert...</div>;
  if (isLoading && isAuthReady && userId) return <div className="flex justify-center items-center h-screen text-xl font-semibold text-gray-700">Lade Patientendaten...</div>;

  const bdiCutoffs = [ { y1: 0, y2: 8, label: "Keine Depression", color: "rgba(75, 192, 192, 0.2)" }, { y1: 9, y2: 13, label: "Minimale Depression", color: "rgba(54, 162, 235, 0.2)" }, { y1: 14, y2: 19, label: "Leichte Depression", color: "rgba(255, 206, 86, 0.2)" }, { y1: 20, y2: 28, label: "Mittelschwere Depression", color: "rgba(255, 159, 64, 0.2)" }, { y1: 29, y2: 63, label: "Schwere Depression", color: "rgba(255, 99, 132, 0.2)" }, ];
  const bdiAxisTicks = [0, 8, 13, 19, 28, 63]; 

  const inputStd = "mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm";
  const btnPrimary = "bg-indigo-600 hover:bg-indigo-700 text-white font-semibold py-2 px-4 rounded-md shadow-sm flex items-center justify-center";
  const btnDanger = "bg-red-500 hover:bg-red-600 text-white font-semibold py-2 px-3 rounded-md flex items-center";
  const btnInfo = "bg-sky-500 hover:bg-sky-600 text-white font-semibold py-1 px-2 text-xs rounded-md flex items-center";
  const textareaStd = "mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm min-h-[150px]";


  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-100 to-sky-100 font-sans">
      <header className="bg-indigo-700 text-white shadow-lg"><div className="container mx-auto px-4 sm:px-6 lg:px-8 py-4 flex justify-between items-center"><h1 className="text-3xl font-bold tracking-tight">Patientenverwaltung</h1>{userId && <p className="text-xs opacity-75">UserID: {userId}</p>}</div></header>
      <main className="container mx-auto p-4 sm:p-6 lg:p-8">
        <div className="mb-6 flex justify-between items-center"><h2 className="text-2xl font-semibold text-gray-800">Patientenübersicht</h2><button onClick={() => { setEditingPatient(null); setIsPatientModalOpen(true); }} className={`${btnPrimary} py-2 px-4`}><UserPlus className="w-5 h-5 mr-2" /> Patient hinzufügen</button></div>

        <div className="bg-white shadow-xl rounded-lg overflow-hidden mb-8"><div className="overflow-x-auto"><table className="w-full min-w-max table-auto text-left">
            <thead className="bg-gray-100 border-b border-gray-300"><tr>{['Name', 'Chiffre', 'Therapie', 'Genehm. Std.', 'Durchgef. Std.', 'Fortschritt', 'Aktionen'].map(header => (<th key={header} className="py-3 px-4 text-sm font-semibold text-gray-600 uppercase tracking-wider">{header}</th>))}</tr></thead>
            <tbody className="divide-y divide-gray-200">
                {patients.length === 0 && !isLoading && (<tr><td colSpan="7" className="text-center py-10 text-gray-500">Keine Patienten gefunden. Fügen Sie einen neuen Patienten hinzu.</td></tr>)}
                {patients.map((patient) => { const progress = patient.approvedHours > 0 ? (patient.conductedHours / patient.approvedHours) * 100 : 0; return ( <tr key={patient.id} className={`hover:bg-indigo-50 cursor-pointer ${selectedPatient?.id === patient.id ? 'bg-indigo-100' : ''}`} onClick={() => { setSelectedPatient(patient); setActiveDetailTab('details'); setSessionSummaries({});  }}><td className="py-3 px-4 whitespace-nowrap text-sm font-medium text-gray-800">{patient.name}</td><td className="py-3 px-4 whitespace-nowrap text-sm text-gray-600">{patient.chiffre}</td><td className="py-3 px-4 whitespace-nowrap text-sm text-gray-600">{patient.therapyType}</td><td className="py-3 px-4 whitespace-nowrap text-sm text-gray-600 text-center">{patient.approvedHours}</td><td className="py-3 px-4 whitespace-nowrap text-sm text-gray-600 text-center">{patient.conductedHours}</td><td className="py-3 px-4 whitespace-nowrap"><div className="w-full bg-gray-200 rounded-full h-2.5"><div className={`h-2.5 rounded-full ${progress > 80 ? 'bg-red-500' : progress > 50 ? 'bg-yellow-400' : 'bg-green-500'}`} style={{ width: `${Math.min(progress, 100)}%` }}></div></div><span className="text-xs text-gray-500 block text-center mt-1">{Math.round(progress)}%</span></td><td className="py-3 px-4 whitespace-nowrap text-sm space-x-2"><button onClick={(e) => { e.stopPropagation(); setEditingPatient(patient); setIsPatientModalOpen(true); }} className="text-indigo-600 hover:text-indigo-800 transition duration-150 p-1 rounded hover:bg-indigo-100" title="Bearbeiten"><Edit3 className="w-5 h-5" /></button><button onClick={(e) => { e.stopPropagation(); openDeleteConfirmation(patient.id); }} className="text-red-500 hover:text-red-700 transition duration-150 p-1 rounded hover:bg-red-100" title="Löschen"><Trash2 className="w-5 h-5" /></button></td></tr>);})}
            </tbody>
            <tfoot className="bg-gray-50 border-t-2 border-gray-300"><tr><td colSpan="3" className="py-3 px-4 text-sm font-semibold text-gray-700 text-right">Gesamt:</td><td className="py-3 px-4 text-sm font-semibold text-gray-700 text-center">{totalApprovedHours}</td><td className="py-3 px-4 text-sm font-semibold text-gray-700 text-center">{totalConductedHours}</td><td className="py-3 px-4 whitespace-nowrap" colSpan="2"><div className="flex items-center"><span className="text-xs text-gray-600 mr-2 w-28">Fortschritt (600 Std.):</span><div className="w-full bg-gray-200 rounded-full h-2.5 flex-grow"><div className="bg-blue-500 h-2.5 rounded-full" style={{ width: `${Math.min(overallProgress, 100)}%` }}></div></div><span className="text-xs text-gray-600 ml-2">{Math.round(overallProgress)}%</span></div></td></tr></tfoot>
        </table></div></div>

        {selectedPatient && (
          <div className="bg-white shadow-xl rounded-lg p-6 mb-8">
            <div className="flex justify-between items-center mb-1"><h3 className="text-2xl font-semibold text-indigo-700">{selectedPatient.name}</h3><button onClick={() => {setSelectedPatient(null); setSessionSummaries({}); }} className="text-gray-500 hover:text-gray-700"><XCircle className="w-7 h-7" /></button></div>

            <div className="border-b border-gray-200 mb-6">
                <nav className="-mb-px flex space-x-4 sm:space-x-8 overflow-x-auto" aria-label="Tabs">
                    {['details', 'krankheitsmodell', 'diagnostik'].map(tab => (
                        <button key={tab} onClick={() => setActiveDetailTab(tab)}
                            className={`whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm ${activeDetailTab === tab ? 'border-indigo-500 text-indigo-600' : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'}`}>
                            {tab === 'details' && 'Stammdaten & Sitzungen'} 
                            {tab === 'krankheitsmodell' && 'Krankheitsmodell'}
                            {tab === 'diagnostik' && 'Diagnostik'} 
                        </button>
                    ))}
                </nav>
            </div>

            {activeDetailTab === 'details' && ( /* Inhalt für Stammdaten & Sitzungen */ <div> {/* ... (wie zuvor) ... */} </div> )}

            {activeDetailTab === 'krankheitsmodell' && (
                <div className="space-y-6">
                    <div>
                        <label htmlFor="krankheitsanamnese" className="block text-lg font-semibold text-gray-700 mb-1">Krankheitsanamnese</label>
                        <textarea id="krankheitsanamnese" name="krankheitsanamnese" rows="8"
                                  className={textareaStd}
                                  value={selectedPatient.krankheitsanamnese || ""}
                                  onChange={(e) => setSelectedPatient(prev => ({...prev, krankheitsanamnese: e.target.value}))}
                                  onBlur={(e) => handleKrankheitsmodellChange('krankheitsanamnese', e.target.value)}
                                  placeholder="Hier die Krankheitsanamnese eintragen..."
                        ></textarea>
                    </div>
                    <div>
                        <label htmlFor="lebensgeschichte" className="block text-lg font-semibold text-gray-700 mb-1">Lebensgeschichte / Biographie</label>
                        <textarea id="lebensgeschichte" name="lebensgeschichte" rows="8"
                                  className={textareaStd}
                                  value={selectedPatient.lebensgeschichte || ""}
                                  onChange={(e) => setSelectedPatient(prev => ({...prev, lebensgeschichte: e.target.value}))}
                                  onBlur={(e) => handleKrankheitsmodellChange('lebensgeschichte', e.target.value)}
                                  placeholder="Wichtige Aspekte der Lebensgeschichte..."
                        ></textarea>
                    </div>
                    <div>
                        <label htmlFor="problemanalyseMakro" className="block text-lg font-semibold text-gray-700 mb-1">Problemanalyse auf Makroebene</label>
                        <textarea id="problemanalyseMakro" name="problemanalyseMakro" rows="8"
                                  className={textareaStd}
                                  value={selectedPatient.problemanalyseMakro || ""}
                                  onChange={(e) => setSelectedPatient(prev => ({...prev, problemanalyseMakro: e.target.value}))}
                                  onBlur={(e) => handleKrankheitsmodellChange('problemanalyseMakro', e.target.value)}
                                  placeholder="Analyse der übergeordneten Problembereiche..."
                        ></textarea>
                    </div>
                </div>
            )}

            {activeDetailTab === 'diagnostik' && (
              <div>
                <div className="border-b border-gray-200 mb-4">
                    <nav className="-mb-px flex space-x-6" aria-label="Diagnostik Sub-Tabs">
                        {['hscl11', 'bdi2', 'fep2'].map(subTab => (
                            <button key={subTab} onClick={() => setActiveDiagnostikSubTab(subTab)}
                                className={`whitespace-nowrap pb-2 px-1 border-b-2 font-medium text-sm ${activeDiagnostikSubTab === subTab ? 'border-purple-500 text-purple-600' : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'}`}>
                                {subTab === 'hscl11' && 'HSCL-11'}
                                {subTab === 'bdi2' && 'BDI-II'}
                                {subTab === 'fep2' && 'FEP-2'}
                            </button>
                        ))}
                    </nav>
                </div>

                {activeDiagnostikSubTab === 'hscl11' && ( <div> {/* ... (HSCL-11 Inhalt wie zuvor) ... */} </div> )}
                {activeDiagnostikSubTab === 'bdi2' && ( <div> {/* ... (BDI-II Inhalt wie zuvor) ... */} </div> )}
                {activeDiagnostikSubTab === 'fep2' && ( <div> {/* ... (FEP-2 Inhalt wie zuvor) ... */} </div> )}
              </div>
            )}
          </div>
        )}

        <Modal isOpen={isPatientModalOpen} onClose={() => { setIsPatientModalOpen(false); setEditingPatient(null); }} title={editingPatient ? 'Patient bearbeiten' : 'Neuen Patienten anlegen'}><PatientForm patient={editingPatient} onSave={handleSavePatient} onCancel={() => { setIsPatientModalOpen(false); setEditingPatient(null); }} /></Modal>
        <ConfirmationModal isOpen={isConfirmDeleteModalOpen} onClose={() => setIsConfirmDeleteModalOpen(false)} onConfirm={confirmDeletePatient} title="Patient löschen" message={`Möchten Sie den Patienten wirklich endgültig löschen?`} />

        <Modal isOpen={diagnosisInfo.isOpen} onClose={() => setDiagnosisInfo(prev => ({...prev, isOpen: false}))} title={`${diagnosisInfo.source === 'local' ? 'Lokale ICD-10 Info' : 'Gemini ICD-10 Info'}: ${diagnosisInfo.code || ''}`} size="3xl">
            {diagnosisInfo.isLoading && <p className="text-center py-4">Informationen werden geladen...</p>}
            {diagnosisInfo.error && <p className="text-red-600 bg-red-100 p-3 rounded-md">Fehler: {diagnosisInfo.error}</p>}
            {!diagnosisInfo.isLoading && diagnosisInfo.source === 'gemini' && diagnosisInfo.info && (
                <div className="whitespace-pre-wrap text-sm text-gray-700 leading-relaxed p-2">{diagnosisInfo.info}</div>
            )}
            {!diagnosisInfo.isLoading && diagnosisInfo.source === 'local' && (
                <div className="text-sm text-gray-700 leading-relaxed p-2 space-y-3">
                    <h3 className="text-lg font-semibold text-indigo-700">{diagnosisInfo.name} <span className="text-gray-500 text-base">({diagnosisInfo.code})</span></h3>
                    {diagnosisInfo.zusatz_info_code && <p className="text-xs text-gray-500">Zusatz: {diagnosisInfo.zusatz_info_code}</p>}
                    {diagnosisInfo.beschreibung && <p><strong className="font-medium text-gray-800">Beschreibung:</strong> {diagnosisInfo.beschreibung}</p>}
                    {diagnosisInfo.inkl && diagnosisInfo.inkl.length > 0 && (
                        <div><strong className="font-medium text-gray-800">Inklusionen:</strong><ul className="list-disc list-inside ml-4">{diagnosisInfo.inkl.map((item, i) => <li key={i}>{item}</li>)}</ul></div>
                    )}
                    {diagnosisInfo.exkl && diagnosisInfo.exkl.length > 0 && (
                        <div><strong className="font-medium text-gray-800">Exklusionen:</strong><ul className="list-disc list-inside ml-4">{diagnosisInfo.exkl.map((item, i) => <li key={i}>{item}</li>)}</ul></div>
                    )}
                    {diagnosisInfo.hinweise && diagnosisInfo.hinweise.length > 0 && (
                        <div><strong className="font-medium text-gray-800">Hinweise:</strong><ul className="list-disc list-inside ml-4">{diagnosisInfo.hinweise.map((item, i) => <li key={i}>{item}</li>)}</ul></div>
                    )}
                </div>
            )}
        </Modal>

        {error && (<div className="fixed bottom-4 right-4 p-4 max-w-sm text-sm text-red-700 bg-red-100 border border-red-400 rounded-md shadow-lg z-[100]">{error}<button onClick={() => setError(null)} className="ml-4 mt-2 sm:mt-0 sm:ml-2 px-2 py-1 text-red-900 underline rounded-md hover:bg-red-200 text-xs">Ok</button></div>)}
      </main>
      <footer className="text-center py-6 text-sm text-gray-500 border-t border-gray-200 mt-10">Patientenverwaltung &copy; {new Date().getFullYear()}</footer>
    </div>
  );
}

const CustomBdiLabel = (props) => {
    const { x, y, width, text } = props;
    if (typeof x !== 'number' || isNaN(x) || 
        typeof y !== 'number' || isNaN(y) ||
        typeof width !== 'number' || isNaN(width) || width <= 0) {
        return null; 
    }
    const labelX = x + width / 2;
    return ( <text x={labelX} y={y} fill="#666" textAnchor="middle" dy={-4} fontSize="10">{text}</text> );
};

export default App;
