import React, { useState, useEffect, useCallback } from 'react';

// Icons
const SaveIcon = ({ className = "w-4 h-4" }) => <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="M19 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h11l5 5v11a2 2 0 0 1-2 2z" /><polyline points="17 21 17 13 7 13 7 21" /><polyline points="7 3 7 8 15 8" /></svg>;
const XCircle = ({ className = "w-6 h-6" }) => (<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><circle cx="12" cy="12" r="10" /><line x1="15" y1="9" x2="9" y2="15" /><line x1="9" y1="9" x2="15" y2="15" /></svg>);

// Test-specific input forms
const TestInputForm = ({ testId, onResultsChange }) => {
    const [results, setResults] = useState({});

    const handleChange = (e) => {
        const { name, value, type } = e.target;
        // FIX: Nest the individual score values inside a 'scores' object
        const newResults = {
            ...results,
            scores: {
                ...(results.scores || {}),
                [name]: type === 'number' ? parseFloat(value) || 0 : value
            }
        };
        setResults(newResults);
        onResultsChange(newResults);
    };

    // Stellt sicher, dass die Felder zurückgesetzt werden, wenn sich der Test ändert.
    useEffect(() => {
        setResults({});
        onResultsChange({});
    }, [testId, onResultsChange]);

    const inputStyle = "mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-teal-500 sm:text-sm";

    switch (testId) {
        case 'hscl11':
            return (
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-4">
                    <div>
                        <label className="block text-sm font-medium text-gray-700">Angstwert (GSI)</label>
                        <input type="number" step="0.1" name="angstwert" value={results.scores?.angstwert || ''} onChange={handleChange} className={inputStyle} />
                    </div>
                    <div>
                        <label className="block text-sm font-medium text-gray-700">Depressionswert (GSI)</label>
                        <input type="number" step="0.1" name="depressionswert" value={results.scores?.depressionswert || ''} onChange={handleChange} className={inputStyle} />
                    </div>
                </div>
            );
        case 'bdi2':
            return (
                 <div className="mt-4">
                    <label className="block text-sm font-medium text-gray-700">BDI-II Summenwert</label>
                    <input type="number" name="summenwert" value={results.scores?.summenwert || ''} onChange={handleChange} className={inputStyle} />
                </div>
            );
        default:
            return <p className="mt-4 text-sm text-gray-500">Für diesen Test ist noch kein Eingabeformular definiert. Sie können ihn trotzdem ohne spezifische Werte speichern.</p>;
    }
};

const ZentraleTestErfassungModal = ({ isOpen, onClose, onSave, testInfo, patients }) => {
    const [selectedPatientId, setSelectedPatientId] = useState('');
    const [testDate, setTestDate] = useState(new Date().toISOString().split('T')[0]);
    const [testResults, setTestResults] = useState({});

    // Korrektur: `setTestResults` zur dependency array hinzugefügt, um den Lint-Warning zu beheben.
    const memoizedSetTestResults = useCallback(setTestResults, [setTestResults]);

    useEffect(() => {
        if (isOpen) {
            setSelectedPatientId('');
            setTestDate(new Date().toISOString().split('T')[0]);
            setTestResults({});
        }
    }, [isOpen]);

    if (!isOpen || !testInfo) return null;

    const handleSave = () => {
        if (!selectedPatientId) {
            alert("Bitte wählen Sie einen Patienten aus.");
            return;
        }

        const testData = {
            testId: testInfo.id,
            results: Object.keys(testResults).length > 0 ? testResults : { placeholder: true, score: 0 },
            date: testDate
        };

        onSave(selectedPatientId, testData);
        onClose();
    };

    const inputStyle = "block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-teal-500 sm:text-sm";
    const btnPrimary = "bg-teal-600 hover:bg-teal-700 text-white font-semibold py-2 px-4 rounded-md shadow-sm flex items-center justify-center transition-colors disabled:bg-gray-400";

    return (
        <div className="fixed inset-0 bg-black bg-opacity-60 backdrop-blur-sm flex justify-center items-center z-[99] p-4">
            <div className="bg-white p-6 rounded-lg shadow-xl w-full max-w-2xl">
                <div className="flex justify-between items-center mb-4">
                    <h2 className="text-2xl font-semibold text-gray-800">Test erfassen: {testInfo.name}</h2>
                    <button onClick={onClose} className="text-gray-500 hover:text-gray-700">
                        <XCircle className="w-7 h-7" />
                    </button>
                </div>
                <div className="space-y-6">
                    <div>
                        <label htmlFor="patient-select" className="block text-sm font-medium text-gray-700">Patient*in</label>
                        <select
                            id="patient-select"
                            value={selectedPatientId}
                            onChange={(e) => setSelectedPatientId(e.target.value)}
                            className={inputStyle}
                        >
                            <option value="" disabled>Bitte wählen...</option>
                            {patients.map(p => (
                                <option key={p.id} value={p.id}>{p.name}, {p.vorname} ({p.chiffre})</option>
                            ))}
                        </select>
                    </div>
                    <div>
                        <label htmlFor="test-date" className="block text-sm font-medium text-gray-700">Testdatum</label>
                        <input
                            type="date"
                            id="test-date"
                            value={testDate}
                            onChange={(e) => setTestDate(e.target.value)}
                            className={inputStyle}
                        />
                    </div>
                    <div className="border-t pt-4">
                         <label className="block text-sm font-medium text-gray-700">Ergebnisse</label>
                        <TestInputForm 
                            testId={testInfo.id}
                            onResultsChange={memoizedSetTestResults}
                        />
                    </div>
                </div>
                <div className="flex justify-end space-x-3 pt-6 mt-6 border-t">
                    <button type="button" onClick={onClose} className="px-4 py-2 text-sm font-medium text-gray-700 bg-gray-100 rounded-md hover:bg-gray-200">Abbrechen</button>
                    <button 
                        onClick={handleSave}
                        disabled={!selectedPatientId}
                        className={btnPrimary}
                    >
                        <SaveIcon className="w-4 h-4 mr-2" /> Testergebnis speichern
                    </button>
                </div>
            </div>
        </div>
    );
};

export default ZentraleTestErfassungModal;
