import React, { useState, useMemo } from 'react';
import { DndProvider, useDrag, useDrop } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import { motion, AnimatePresence } from 'framer-motion';
import DiagnostikTimelineChart from './DiagnostikTimelineChart'; // Import der neuen Chart-Komponente

const PlusCircle = ({ className = "w-5 h-5" }) => <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><circle cx="12" cy="12" r="10"></circle><line x1="12" y1="8" x2="12" y2="16"></line><line x1="8" y1="12" x2="16" y2="12"></line></svg>;
const CheckCircle = ({ className = "w-5 h-5" }) => <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="M22 11.08V12a10 10 0 1 1-5.93-9.14"></path><polyline points="22 4 12 14.01 9 11.01"></polyline></svg>;
const GripVertical = ({ className = "w-5 h-5" }) => <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><circle cx="9" cy="12" r="1"></circle><circle cx="9" cy="5" r="1"></circle><circle cx="9" cy="19" r="1"></circle><circle cx="15" cy="12" r="1"></circle><circle cx="15" cy="5" r="1"></circle><circle cx="15" cy="19" r="1"></circle></svg>;
const Trash2 = ({ className = "w-5 h-5" }) => (<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><polyline points="3 6 5 6 21 6" /><path d="M19 6v14a2 2 0 0 1-2 2H7a2 2 0 0 1-2-2V6m3 0V4a2 2 0 0 1 2-2h4a2 2 0 0 1 2 2v2" /><line x1="10" y1="11" x2="10" y2="17" /><line x1="14" y1="11" x2="14" y2="17" /></svg>);


const ItemTypes = {
    TEST: 'test',
};

const DraggableTest = ({ test }) => {
    const [{ isDragging }, drag] = useDrag(() => ({
        type: ItemTypes.TEST,
        item: { id: test.testInstanceId, testType: test.testType }, 
        collect: (monitor) => ({
            isDragging: !!monitor.isDragging(),
        }),
    }));

    return (
        <motion.div
            ref={drag}
            layoutId={test.testInstanceId}
            initial={{ opacity: 0, y: -10 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 10 }}
            style={{ opacity: isDragging ? 0.5 : 1 }}
            className="p-2.5 bg-white rounded-lg border border-gray-200 shadow-sm cursor-grab active:cursor-grabbing flex items-center gap-2"
        >
            <GripVertical className="text-gray-400" />
            <div>
                <p className="font-semibold text-sm text-gray-800">{test.name}</p>
                <p className="text-xs text-gray-500">
                    Vom: {new Date(test.date).toLocaleDateString('de-DE')}
                </p>
            </div>
        </motion.div>
    );
};

const DropZone = ({ period, onDropTest, children, className, isOver }) => {
    const [, drop] = useDrop(() => ({
        accept: ItemTypes.TEST,
        drop: (item) => onDropTest(item, period.id),
    }));

    return (
        <div
            ref={drop}
            className={`${className} rounded-xl border-2 transition-all duration-200 ${
                isOver ? 'border-teal-500 bg-teal-50' : 'border-dashed border-gray-300'
            }`}
        >
            {children}
        </div>
    );
};

const DiagnostikZuordnung = ({ patient, availableTests, testCategories, onAddTest, onShowTestDetails, onAssignTestToPeriod, diagnostikAnalysis, onDeleteTest }) => {
    const [showSuccess, setShowSuccess] = useState(null);
    const [dropHighlight, setDropHighlight] = useState(null);

    const allTests = useMemo(() => {
        const tests = [];
        availableTests.forEach(testDef => {
            const results = patient[`${testDef.id}Results`] || [];
            results.forEach(result => {
                const instanceId = result.testInstanceId || `test_${testDef.id}_${new Date(result.date).getTime()}`;
                tests.push({
                    ...testDef,
                    ...result,
                    testInstanceId: instanceId,
                    testType: testDef.id,
                    billingPeriodId: result.billingPeriodId === undefined ? null : result.billingPeriodId,
                });
            });
        });
        return tests.sort((a, b) => new Date(b.date) - new Date(a.date));
    }, [patient, availableTests]);

    const chartData = useMemo(() => {
        if (!allTests || allTests.length === 0) return [];
        const groupedByDate = allTests.reduce((acc, test) => {
            const date = new Date(test.date).toLocaleDateString('de-DE');
            if (!acc[date]) {
                acc[date] = [];
            }
            acc[date].push(test);
            return acc;
        }, {});

        return Object.entries(groupedByDate).map(([date, tests]) => ({
            date,
            count: tests.length,
            tests: tests
        })).sort((a,b) => new Date(a.date.split('.').reverse().join('-')) - new Date(b.date.split('.').reverse().join('-')));
    }, [allTests]);

    const handleDropTest = (droppedItem, targetPeriodId) => {
        onAssignTestToPeriod(droppedItem.id, droppedItem.testType, targetPeriodId);
        const testToMove = allTests.find(t => t.testInstanceId === droppedItem.id);
        if (testToMove) {
             setShowSuccess(`Test "${testToMove.name}" wurde zugeordnet.`);
             setTimeout(() => setShowSuccess(null), 2500);
        }
        setDropHighlight(null);
    };

    const unassignedTests = useMemo(() => allTests.filter(t => t.billingPeriodId === null || t.billingPeriodId === undefined), [allTests]);

    return (
        <DndProvider backend={HTML5Backend}>
            <div className="space-y-8">
                {/* 3. ZEITSTRAHL (NEU) */}
                <div>
                    <h3 className="text-xl font-bold text-gray-800 mb-4">Diagnostik-Verlauf</h3>
                    <DiagnostikTimelineChart chartData={chartData} testCategories={testCategories} />
                </div>

                {/* 1. NEUEN TEST HINZUFÜGEN (VERSCHOBEN) */}
                <div>
                    <h3 className="text-xl font-bold text-gray-800 mb-4">Neuen Test für diesen Patienten hinzufügen</h3>
                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                        {availableTests.map(test => (
                            <div key={test.id} className="bg-white p-4 rounded-lg border flex justify-between items-center shadow-sm">
                                <div>
                                    <p className="font-semibold text-gray-800">{test.name}</p>
                                    <p className="text-xs text-gray-500">EBM: {test.ebm}</p>
                                </div>
                                <button onClick={() => onAddTest(test)} className="text-teal-600 hover:bg-teal-100 p-2 rounded-full transition-colors">
                                    <PlusCircle />
                                </button>
                            </div>
                        ))}
                    </div>
                </div>

                {/* 2. TESTDIAGNOSTIK ZUORDNUNG (BESTEHEND) */}
                <div>
                    <h3 className="text-xl font-bold text-gray-800 mb-4">Testdiagnostik Zuordnung</h3>
                     <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 p-4 bg-gray-50 rounded-2xl border">
                        {/* Linke Spalte: Abrechnungszeiträume & Frei */}
                        <div className="lg:col-span-2 grid grid-cols-1 md:grid-cols-2 gap-4">
                           {(patient?.billingPeriods || []).map(period => {
                                const analysis = diagnostikAnalysis[period.id] || { pointsUsed: 0, pointsLimit: 0 };
                                const testsInPeriod = allTests.filter(t => t.billingPeriodId === period.id);
                                return (
                                <div key={period.id} onDragOver={() => setDropHighlight(period.id)} onDragLeave={() => setDropHighlight(null)}>
                                    <DropZone period={period} onDropTest={handleDropTest} isOver={dropHighlight === period.id}>
                                         <div className="p-4">
                                            <h4 className="font-bold text-gray-700">{period.name}</h4>
                                            <p className="text-xs text-gray-500 mb-2">Datum: {new Date(period.date).toLocaleDateString('de-DE')}</p>
                                            <div className="w-full bg-gray-200 rounded-full h-2.5 mt-1">
                                                <div className="bg-teal-500 h-2.5 rounded-full" style={{ width: `${Math.min((analysis.pointsUsed / analysis.pointsLimit) * 100, 100)}%` }}></div>
                                            </div>
                                            <p className="text-xs text-gray-600 mt-1 text-right font-mono">{analysis.pointsUsed} / {analysis.pointsLimit} Punkte</p>
                                        </div>
                                        <div className="p-2 space-y-2 min-h-[60px]">
                                            <AnimatePresence>
                                                {testsInPeriod.map(test => <DraggableTest key={test.testInstanceId} test={test} />)}
                                            </AnimatePresence>
                                        </div>
                                    </DropZone>
                                </div>
                            )})}
                            {/* Frei-Zone */}
                             <div onDragOver={() => setDropHighlight('frei')} onDragLeave={() => setDropHighlight(null)}>
                                <DropZone period={{id: 'frei'}} onDropTest={handleDropTest} isOver={dropHighlight === 'frei'}>
                                    <div className="p-4"><h4 className="font-bold text-gray-700">Abrechnungsfrei</h4> <p className="text-xs text-gray-500">Tests hier ablegen, die nicht abgerechnet werden sollen.</p></div>
                                    <div className="p-2 space-y-2 min-h-[60px]">
                                        <AnimatePresence>
                                            {allTests.filter(t => t.billingPeriodId === 'frei').map(test => <DraggableTest key={test.testInstanceId} test={test} />)}
                                        </AnimatePresence>
                                    </div>
                                </DropZone>
                            </div>
                        </div>

                        {/* Rechte Spalte: Nicht zugeordnete Tests */}
                         <div onDragOver={() => setDropHighlight(null)} onDragLeave={() => setDropHighlight(null)}>
                             <DropZone period={{id: null}} onDropTest={handleDropTest} isOver={dropHighlight === null}>
                                 <div className="p-4"><h4 className="font-bold text-gray-700">Nicht zugeordnete Tests</h4></div>
                                 <div className="p-2 space-y-2 min-h-[150px]">
                                    {unassignedTests.length > 0 ? (
                                        <AnimatePresence>
                                            {unassignedTests.map(test => <DraggableTest key={test.testInstanceId} test={test} />)}
                                        </AnimatePresence>
                                    ) : (
                                        <div className="flex items-center justify-center h-full min-h-[100px]">
                                            <p className="text-sm text-gray-500 text-center p-4">Alle Tests sind zugeordnet!</p>
                                        </div>
                                    )}
                                 </div>
                             </DropZone>
                         </div>
                    </div>
                </div>

                {/* Success Message */}
                <AnimatePresence>
                    {showSuccess && (
                        <motion.div
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            exit={{ opacity: 0, y: 20 }}
                            className="fixed bottom-5 right-5 flex items-center px-4 py-2 bg-gray-800 text-white rounded-lg shadow-lg z-50 text-sm"
                        >
                            <CheckCircle className="mr-2 h-5 w-5 text-green-400" /> {showSuccess}
                        </motion.div>
                    )}
                </AnimatePresence>
            </div>
        </DndProvider>
    );
};

export default DiagnostikZuordnung;
