import React, { useState, useEffect, useMemo } from 'react';

// Icons
const SaveIcon = ({ className = "w-4 h-4" }) => <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="M19 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h11l5 5v11a2 2 0 0 1-2 2z" /><polyline points="17 21 17 13 7 13 7 21" /><polyline points="7 3 7 8 15 8" /></svg>;
const PlusIcon = ({ className = "w-4 h-4" }) => <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><line x1="12" y1="5" x2="12" y2="19"></line><line x1="5" y1="12" x2="19" y2="12"></line></svg>;
const TrashIcon = ({ className = "w-4 h-4" }) => <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><polyline points="3 6 5 6 21 6"></polyline><path d="M19 6v14a2 2 0 0 1-2 2H7a2 2 0 0 1-2-2V6m3 0V4a2 2 0 0 1 2-2h4a2 2 0 0 1 2 2v2"></path></svg>;

const BillingModal = ({ event, ebmData, onSave, onClose }) => {
    const [step, setStep] = useState(1);
    const [ebmEntries, setEbmEntries] = useState([]);
    const [ebmSearch, setEbmSearch] = useState('');
    const [selectedEbm, setSelectedEbm] = useState(null);
    const [anzahl, setAnzahl] = useState(1);
    const [faktor, setFaktor] = useState(1);

    const [notes, setNotes] = useState({
        thema: '',
        patientenbericht: '',
        therapeutischeInterventionen: '',
        gesamteindruck: '',
        risikoabschaetzung: 'Auf Nachfrage wurden suizidale Gedanken, Impulse sowie Handlungsabsichten glaubhaft und eindeutig verneint. Die Absprachefähigkeit des Patienten ist gegeben. Es besteht kein Anhalt für eine akute Eigen- oder Fremdgefährdung.',
        hausaufgaben: ''
    });
    const [isSuicidal, setIsSuicidal] = useState(false);

    const riskTextStandard = 'Auf Nachfrage wurden suizidale Gedanken, Impulse sowie Handlungsabsichten glaubhaft und eindeutig verneint. Die Absprachefähigkeit des Patienten ist gegeben. Es besteht kein Anhalt für eine akute Eigen- oder Fremdgefährdung.';

    // *** HIER IST DIE FEHLENDE FUNKTION ***
    const handleNoteChange = (e) => {
        const { name, value } = e.target;
        setNotes(prev => ({ ...prev, [name]: value }));
    };

    useEffect(() => {
        setNotes(prev => ({ ...prev, risikoabschaetzung: isSuicidal ? '' : riskTextStandard }));
    }, [isSuicidal]);

    const searchResults = useMemo(() => {
        if (!ebmSearch) return [];
        return ebmData.filter(item => 
            item.Nummer.startsWith(ebmSearch) || 
            item.Titel.toLowerCase().includes(ebmSearch.toLowerCase())
        ).slice(0, 5);
    }, [ebmSearch, ebmData]);

    const handleSelectEbm = (item) => {
        setSelectedEbm(item);
        setEbmSearch(`${item.Nummer} - ${item.Titel}`);
    };

    const handleAddEbmEntry = () => {
        if (selectedEbm) {
            setEbmEntries(prev => [...prev, { ...selectedEbm, anzahl: anzahl, faktor: faktor }]);
            setSelectedEbm(null);
            setEbmSearch('');
            setAnzahl(1);
            setFaktor(1);
        }
    };

    const handleDeleteEbmEntry = (index) => {
        setEbmEntries(prev => prev.filter((_, i) => i !== index));
    };

    const totalPoints = ebmEntries.reduce((sum, entry) => {
        const punkte = parseInt(entry.Punkte, 10) || 0;
        return sum + (punkte * entry.anzahl * entry.faktor);
    }, 0);

    const handleSave = () => {
        const chiffreMatch = event.summary.match(/[A-Z][0-9]{6}/);
        const billingData = {
            eventId: event.id,
            eventSummary: event.summary,
            eventStart: event.start.dateTime || event.start.date,
            chiffre: chiffreMatch ? chiffreMatch[0] : null,
            ebmEntries: ebmEntries.map(e => ({ nummer: e.Nummer, anzahl: e.anzahl, faktor: e.faktor })),
            notes,
        };
        onSave(billingData);
    };

    const inputStyle = "block w-full px-3 py-2 bg-gray-50 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-red-500 focus:border-red-500 sm:text-sm";
    const textareaStyle = `${inputStyle} min-h-[90px] resize-y`;
    const accentButton = "bg-red-600 text-white font-semibold py-2 px-4 rounded-md shadow-sm hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500 flex items-center justify-center";

    return (
        <div className="p-1">
            <div className="flex justify-center items-center space-x-4 mb-6">
                <div className={`flex items-center ${step >= 1 ? 'text-red-600' : 'text-gray-400'}`}>
                    <div className={`w-8 h-8 rounded-full flex items-center justify-center font-bold border-2 ${step >= 1 ? 'bg-red-600 border-red-700 text-white' : 'bg-gray-200 border-gray-300'}`}>1</div>
                    <span className="ml-2 font-semibold">Leistungen</span>
                </div>
                <div className={`h-0.5 w-16 ${step >= 2 ? 'bg-red-500' : 'bg-gray-200'}`}></div>
                <div className={`flex items-center ${step >= 2 ? 'text-red-600' : 'text-gray-400'}`}>
                    <div className={`w-8 h-8 rounded-full flex items-center justify-center font-bold border-2 ${step >= 2 ? 'bg-red-600 border-red-700 text-white' : 'bg-gray-200 border-gray-300'}`}>2</div>
                    <span className="ml-2 font-semibold">Dokumentation</span>
                </div>
            </div>

            {step === 1 && (
                <div className="animate-fade-in space-y-4">
                    <div className="p-4 border rounded-lg bg-white">
                        <h3 className="font-semibold text-lg text-gray-800 mb-3">Leistung hinzufügen</h3>
                        <div className="relative">
                            <input type="text" placeholder="EBM-Ziffer oder Titel suchen..." value={ebmSearch} onChange={e => { setEbmSearch(e.target.value); setSelectedEbm(null); }} className={inputStyle} />
                            {searchResults.length > 0 && ebmSearch && !selectedEbm && (
                                <ul className="absolute z-10 w-full bg-white border border-gray-300 rounded-md mt-1 shadow-lg max-h-48 overflow-y-auto">
                                    {searchResults.map(item => <li key={item.Nummer} onClick={() => handleSelectEbm(item)} className="px-3 py-2 cursor-pointer hover:bg-red-50">{item.Nummer} - {item.Titel}</li>)}
                                </ul>
                            )}
                        </div>
                        <div className="grid grid-cols-3 gap-3 mt-3">
                            <div><label className="text-xs font-medium text-gray-600">Anzahl</label><input type="number" value={anzahl} onChange={e => setAnzahl(Math.max(1, parseInt(e.target.value, 10) || 1))} min="1" className={`${inputStyle} text-center`} /></div>
                            <div><label className="text-xs font-medium text-gray-600">Faktor</label><input type="number" step="0.1" value={faktor} onChange={e => setFaktor(parseFloat(e.target.value) || 1)} min="1" className={`${inputStyle} text-center`} /></div>
                            <div className="self-end"><button onClick={handleAddEbmEntry} disabled={!selectedEbm} className="w-full bg-gray-700 text-white p-2 rounded-md hover:bg-gray-800 disabled:bg-gray-300 flex items-center justify-center"><PlusIcon className="mr-1"/> Hinzufügen</button></div>
                        </div>
                    </div>
                    <div className="p-4 border rounded-lg bg-white mt-4">
                        <h3 className="font-semibold text-lg text-gray-800 mb-3">Abzurechnende Leistungen</h3>
                        <div className="space-y-2">
                        {ebmEntries.map((entry, index) => (
                            <div key={index} className="flex justify-between items-center bg-gray-50 p-2 rounded-md">
                                <div>
                                    <p className="font-semibold">{entry.Nummer} <span className="text-gray-600 font-normal">({entry.Titel})</span></p>
                                    <p className="text-xs text-gray-500">Anzahl: {entry.anzahl}, Faktor: {entry.faktor}</p>
                                </div>
                                <div className="flex items-center">
                                    <span className="mr-4 font-mono text-gray-800">{(parseInt(entry.Punkte, 10) * entry.anzahl * entry.faktor).toFixed(0)} Pkt.</span>
                                    <button onClick={() => handleDeleteEbmEntry(index)} className="text-red-500 hover:text-red-700 p-1"><TrashIcon /></button>
                                </div>
                            </div>
                        ))}
                        </div>
                        {ebmEntries.length === 0 && <p className="text-sm text-gray-500 text-center py-4">Noch keine Leistungen hinzugefügt.</p>}
                        <div className="text-right font-bold text-xl mt-4 pt-4 border-t">Gesamt: {totalPoints.toFixed(0)} Punkte</div>
                    </div>
                </div>
            )}

            {step === 2 && (
                <div className="animate-fade-in space-y-3 p-4 border rounded-lg bg-white">
                     <h3 className="font-semibold text-lg text-gray-800">Sitzungsdokumentation</h3>
                    <div><label className="text-sm font-medium text-gray-600">Thema der Sitzung</label><input name="thema" value={notes.thema} onChange={handleNoteChange} className={inputStyle} /></div>
                    <div><label className="text-sm font-medium text-gray-600">Patientenbericht</label><textarea name="patientenbericht" value={notes.patientenbericht} onChange={handleNoteChange} className={textareaStyle} /></div>
                    <div><label className="text-sm font-medium text-gray-600">Therapeutische Interventionen</label><textarea name="therapeutischeInterventionen" value={notes.therapeutischeInterventionen} onChange={handleNoteChange} className={textareaStyle} /></div>
                    <div><label className="text-sm font-medium text-gray-600">Gesamteindruck</label><textarea name="gesamteindruck" value={notes.gesamteindruck} onChange={handleNoteChange} className={textareaStyle} /></div>
                    <div>
                        <label className="text-sm font-medium text-gray-600">Risikoabschätzung (Suizidalität)</label>
                        <div className="flex items-center space-x-4 mt-1 mb-2">
                             <label className="flex items-center"><input type="radio" checked={!isSuicidal} onChange={() => setIsSuicidal(false)} className="h-4 w-4 text-red-600 border-gray-300 focus:ring-red-500"/> <span className="ml-2 text-sm">Nein</span></label>
                             <label className="flex items-center"><input type="radio" checked={isSuicidal} onChange={() => setIsSuicidal(true)} className="h-4 w-4 text-red-600 border-gray-300 focus:ring-red-500"/> <span className="ml-2 text-sm">Ja, Interventionsbedarf</span></label>
                        </div>
                        <textarea name="risikoabschaetzung" value={notes.risikoabschaetzung} onChange={handleNoteChange} disabled={!isSuicidal} className={`${textareaStyle} ${!isSuicidal ? 'bg-gray-100' : ''}`} />
                    </div>
                    <div><label className="text-sm font-medium text-gray-600">Hausaufgaben</label><input name="hausaufgaben" value={notes.hausaufgaben} onChange={handleNoteChange} className={inputStyle} /></div>
                </div>
            )}

            <div className="flex justify-between pt-6 mt-6 border-t">
                <button onClick={() => step > 1 ? setStep(step - 1) : onClose()} className="px-4 py-2 text-sm font-medium text-gray-700 bg-gray-200 rounded-md hover:bg-gray-300">
                    {step === 1 ? 'Abbrechen' : 'Zurück'}
                </button>
                {step === 1 && <button onClick={() => setStep(2)} className={accentButton}>Weiter</button>}
                {step === 2 && <button onClick={handleSave} className={`${accentButton} bg-green-600 hover:bg-green-700`}><SaveIcon className="mr-2"/> Abrechnung speichern</button>}
            </div>
        </div>
    );
};

export default BillingModal;
