import React, { useState, useMemo, useEffect } from 'react';

const EditIcon = ({ className = "w-4 h-4" }) => <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="M11 4H4a2 2 0 0 0-2 2v14a2 2 0 0 0 2 2h14a2 2 0 0 0 2-2v-7"></path><path d="M18.5 2.5a2.121 2.121 0 0 1 3 3L12 15l-4 1 1-4 9.5-9.5z"></path></svg>;
const TrashIcon = ({ className = "w-4 h-4" }) => <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><polyline points="3 6 5 6 21 6"></polyline><path d="M19 6v14a2 2 0 0 1-2 2H7a2 2 0 0 1-2-2V6m3 0V4a2 2 0 0 1 2-2h4a2 2 0 0 1 2 2v2"></path></svg>;

const DIAGNOSTIK_PUNKTE = { '35600': 34, '35601': 39, '35602': 56 };

const AbrechnungsUebersicht = ({ patient, availableTests, onAddBillingPeriod, diagnostikAnalysis, onEditSession, onDeleteSession, onDeleteBillingPeriod }) => {    const [selectedPeriodId, setSelectedPeriodId] = useState(null);
    const [selectedTherapySession, setSelectedTherapySession] = useState(null);

    useEffect(() => {
        if (patient?.billingPeriods?.length > 0 && !selectedPeriodId && !selectedTherapySession) {
            setSelectedPeriodId(patient.billingPeriods[0].id);
        }
    }, [patient, selectedPeriodId, selectedTherapySession]);

    const selectedPeriodDetails = useMemo(() => {
        if (!selectedPeriodId || !diagnostikAnalysis) return null;
        return diagnostikAnalysis[selectedPeriodId];
    }, [selectedPeriodId, diagnostikAnalysis]);

    const testDetailsMap = useMemo(() => {
        if (!availableTests) return new Map();
        const map = new Map();
        availableTests.forEach(test => {
            map.set(test.id, { name: test.name, ebm: test.ebm, points: DIAGNOSTIK_PUNKTE[test.ebm] || 0 });
        });
        return map;
    }, [availableTests]);

    const therapySessions = useMemo(() => {
        return (patient.sessions || [])
            .filter(s => s.structuredNotes && s.structuredNotes.sitzungsnummer)
            .sort((a, b) => new Date(b.date) - new Date(a.date));
    }, [patient.sessions]);

    return (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div className="md:col-span-1 space-y-4">
                <div>
                    <h4 className="text-lg font-bold text-gray-800 mb-2">AbrechnungszeitrÃ¤ume</h4>
                    <div className="space-y-1 max-h-[30vh] overflow-y-auto pr-2">
                        {/* HIER DIE ÃNDERUNG STARTET */}
                        {(patient.billingPeriods || []).length > 0 ? patient.billingPeriods.map((period) => (
                            <div
                                key={period.id}
                                onClick={() => { setSelectedPeriodId(period.id); setSelectedTherapySession(null); }}
                                className={`p-2 rounded-md cursor-pointer text-xs transition-colors flex justify-between items-center ${selectedPeriodId === period.id ? 'bg-teal-600 text-white' : 'bg-gray-100 hover:bg-teal-50 text-gray-800'}`}
                            >
                                <div>
                                    <p className="font-semibold">{period.name}</p>
                                    <p className={selectedPeriodId === period.id ? 'text-teal-200' : 'text-gray-500'}>
                                        Datum: {new Date(period.date).toLocaleDateString('de-DE')}
                                    </p>
                                </div>
                                {/* LÃSCH-BUTTON HINZUGEFÃGT */}
                                <button 
                                    onClick={(e) => { 
                                        e.stopPropagation(); // Verhindert, dass der Zeitraum ausgewÃ¤hlt wird
                                        onDeleteBillingPeriod(period.id);
                                    }} 
                                    className="p-1 hover:bg-red-500 hover:text-white rounded-full text-gray-400"
                                    title="Diesen Zeitraum lÃ¶schen"
                                >
                                    <TrashIcon className="w-4 h-4"/>
                                </button>
                            </div>
                        )) : <p className="text-xs text-gray-500 p-3 bg-gray-50 rounded-md">Keine ZeitrÃ¤ume definiert.</p>}
                        {/* HIER ENDET DIE ÃNDERUNG */}
                    </div>
                     <button
                        onClick={onAddBillingPeriod}
                        className="mt-3 w-full bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-3 rounded-md text-sm flex items-center justify-center transition-colors"
                    >
                        Abrechnungszeitraum hinzufÃ¼gen
                    </button>
                </div>
                 <div>
                    <h4 className="text-lg font-bold text-gray-800 mb-2">Therapiesitzungen</h4>
                     <div className="space-y-1 max-h-[30vh] overflow-y-auto pr-2">
                         {therapySessions.map(session => (
                             <div key={session.id} onClick={() => { setSelectedTherapySession(session); setSelectedPeriodId(null); }}
                                 className={`p-2 rounded-md cursor-pointer text-xs transition-colors flex justify-between items-center ${selectedTherapySession?.id === session.id ? 'bg-teal-600 text-white' : 'bg-gray-100 hover:bg-teal-50 text-gray-800'}`}>
                                 <p className="font-semibold">Sitzung {session.structuredNotes.sitzungsnummer} - {new Date(session.date).toLocaleDateString('de-DE')}</p>
                                  <div className="flex items-center flex-shrink-0">
                                    <button onClick={(e) => { e.stopPropagation(); onEditSession(session); }} className="p-1 hover:bg-white/20 rounded-full"><EditIcon className="w-4 h-4"/></button>
                                    <button onClick={(e) => { e.stopPropagation(); onDeleteSession(session.id); }} className="p-1 hover:bg-white/20 rounded-full"><TrashIcon className="w-4 h-4"/></button>
                                </div>
                             </div>
                         ))}
                     </div>
                </div>
            </div>

            <div className="md:col-span-2">
                {selectedPeriodDetails ? (
                    <div className="bg-white p-4 rounded-lg shadow-sm border space-y-4">
                        <h4 className="text-lg font-semibold text-gray-700">Details fÃ¼r: {selectedPeriodDetails.name}</h4>
                        <div className="bg-blue-50 border border-blue-200 rounded-lg p-3">
                            <h5 className="font-semibold text-blue-800 mb-2">Zugeordnete Tests</h5>
                            {(selectedPeriodDetails.tests && selectedPeriodDetails.tests.length > 0) ? (
                                <table className="w-full text-sm">
                                    <thead>
                                        <tr className="border-b border-blue-200"><th className="py-1 text-left font-semibold">Test</th><th className="py-1 text-left font-semibold">Datum</th><th className="py-1 text-right font-semibold">Punkte</th></tr>
                                    </thead>
                                    <tbody>
                                        {selectedPeriodDetails.tests.map((test, i) => {
                                            const details = testDetailsMap.get(test.testType);
                                            return (
                                                <tr key={i} className="border-b last:border-b-0 border-blue-100">
                                                    <td className="py-2">{details?.name || test.testType}</td>
                                                    <td className="py-2">{new Date(test.date).toLocaleDateString('de-DE')}</td>
                                                    <td className="py-2 text-right font-mono">{details?.points || 0}</td>
                                                </tr>
                                            );
                                        })}
                                    </tbody>
                                    <tfoot>
                                        <tr className="font-bold border-t-2 border-blue-300"><td colSpan="2" className="pt-2 text-right">Gesamt verbraucht:</td><td className="pt-2 text-right font-mono">{selectedPeriodDetails.pointsUsed}</td></tr>
                                        <tr className="font-bold"><td colSpan="2" className="pt-1 text-right text-gray-600">Limit:</td><td className="pt-1 text-right font-mono text-gray-600">{selectedPeriodDetails.pointsLimit}</td></tr>
                                    </tfoot>
                                </table>
                            ) : <p className="text-sm text-gray-500">Diesem Zeitraum sind noch keine Tests zugeordnet.</p>}
                        </div>
                    </div>
                ) : selectedTherapySession ? (
                     <div className="bg-white p-4 rounded-lg shadow-sm border space-y-4">
                         <h4 className="text-lg font-semibold text-gray-700">Details fÃ¼r Sitzung {selectedTherapySession.structuredNotes.sitzungsnummer} vom {new Date(selectedTherapySession.date).toLocaleDateString('de-DE')}</h4>
                         {Object.entries(selectedTherapySession.structuredNotes).map(([key, value]) => (
                             <div key={key}>
                                 <h6 className="font-semibold text-sm text-gray-600 capitalize">{key.replace(/([A-Z])/g, ' $1').trim()}</h6>
                                 <p className="text-sm text-gray-800 bg-gray-50 p-2 rounded-md mt-1 whitespace-pre-wrap">{String(value)}</p>
                             </div>
                         ))}
                    </div>
                ) : (
                    <div className="flex items-center justify-center h-full bg-gray-50 rounded-lg border">
                        <p className="text-gray-500">WÃ¤hlen Sie links einen Eintrag aus, um die Details anzuzeigen.</p>
                    </div>
                )}
            </div>
        </div>
    );
};

export default AbrechnungsUebersicht;
