// src/components/FEP2.js

import React, { useState, useRef } from 'react';

// Benötigte Icons (kopiert aus App.js)
const PlusCircleIcon = ({ className = "w-6 h-6" }) => ( <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><circle cx="12" cy="12" r="10" /><line x1="12" y1="8" x2="12" y2="16" /><line x1="8" y1="12" x2="16" y2="12" /></svg>);
const Trash2 = ({ className = "w-6 h-6" }) => ( <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><polyline points="3 6 5 6 21 6" /><path d="M19 6v14a2 2 0 0 1-2 2H7a2 2 0 0 1-2-2V6m3 0V4a2 2 0 0 1 2-2h4a2 2 0 0 1 2 2v2" /><line x1="10" y1="11" x2="10" y2="17" /><line x1="14" y1="11" x2="14" y2="17" /></svg>);

// FEP-2 spezifische Logik
const FEP2_ITEMS_TO_REVERSE = [1, 2, 5, 9, 14, 15, 19, 21, 22, 27, 29, 30, 39]; 
const FEP2_SKALEN_DEFINITION = { WOHLBEFINDEN: { items: [1, 5, 9, 14, 22, 30, 39], minItemsRequired: 7 }, BESCHWERDEN: { items: [4, 6, 7, 8, 10, 12, 23, 24, 31, 34, 38], minItemsRequired: 10 }, BESCHWERDEN_ANGST: { items: [4, 8, 12, 23], minItemsRequired: 4 }, BESCHWERDEN_DEPRESSIVITAET: { items: [7, 10, 24, 31, 34, 38], minItemsRequired: 5 }, INTERPERSONALE_BEZIEHUNG: { items: [11, 13, 16, 18, 20, 25, 26, 28, 32, 33, 36, 40], minItemsRequired: 11 }, INTERPERSONALE_INTROVERTIERT_SCHEU: { items: [28, 36, 40], minItemsRequired: 3 }, INTERPERSONALE_AUSNUTZBAR_NACHGIEBIG: { items: [13, 20, 26], minItemsRequired: 3 }, INTERPERSONALE_KONKURRIEREND_UNTERSTUETZEND: { items: [11, 16, 33], minItemsRequired: 3 }, INTERPERSONALE_SELBSTUNSICHER_ZURUECKHALTEND: { items: [18, 25, 32], minItemsRequired: 3 }, KONGRUENZ: { items: [2, 3, 15, 17, 19, 21, 27, 29, 35, 37], minItemsRequired: 8 }, };
const calculateFep2Scores = (itemScoresRaw) => { const itemScores = itemScoresRaw.map((score, index) => { const itemNum = index + 1; if (FEP2_ITEMS_TO_REVERSE.includes(itemNum) && score > 0 && score <=5) { return 6 - score; } return score; }); const answeredItemsCount = itemScores.filter(score => score > 0 && score <= 5).length; const calculatedScales = {}; if (answeredItemsCount >= 32) { const sum = itemScores.reduce((acc, score) => acc + (score > 0 && score <=5 ? score : 0), 0); calculatedScales.gesamtbelastung = answeredItemsCount > 0 ? parseFloat((sum / answeredItemsCount).toFixed(2)) : null; } else { calculatedScales.gesamtbelastung = null; } for (const skalaName in FEP2_SKALEN_DEFINITION) { const def = FEP2_SKALEN_DEFINITION[skalaName]; let sum = 0; let count = 0; def.items.forEach(itemNum => { const score = itemScores[itemNum - 1]; if (score > 0 && score <= 5) { sum += score; count++; } }); if (count >= def.minItemsRequired) { calculatedScales[skalaName.toLowerCase()] = parseFloat((sum / count).toFixed(2)); } else { calculatedScales[skalaName.toLowerCase()] = null; } } return calculatedScales; };


const FEP2Component = ({ patient, onSaveResult, onDeleteResult, setAppError }) => {
    // State und Refs sind jetzt hier gekapselt
    const [newFepDate, setNewFepDate] = useState(new Date().toISOString().split('T')[0]);
    const [fepItemScores, setFepItemScores] = useState(Array(40).fill(''));
    const fepInputRefs = useRef([]);

    // Benötigte CSS-Klassen für die Konsistenz
    const inputStd = "mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm";
    const btnPrimary = "bg-indigo-600 hover:bg-indigo-700 text-white font-semibold py-2 px-4 rounded-md shadow-sm flex items-center justify-center";
    const btnDanger = "bg-red-500 hover:bg-red-600 text-white font-semibold py-2 px-3 rounded-md flex items-center";

    const handleFepItemChange = (index, value) => { 
        if (!/^[1-5]?$/.test(value)) return;

        const newScores = [...fepItemScores];
        newScores[index] = value;
        setFepItemScores(newScores);

        if (value.length === 1 && index < 39 && fepInputRefs.current[index + 1]) {
            fepInputRefs.current[index + 1].focus();
        }
    };

    const handleAddFep = () => {
        const parsedScores = fepItemScores.map(s => parseInt(s, 10) || 0);
        const answeredCount = parsedScores.filter(s => s >= 1 && s <= 5).length;

        if (answeredCount < 32) {
            setAppError("Bitte beantworten Sie mindestens 80% (32) der FEP-2 Items.");
            return;
        }

        const calculatedScales = calculateFep2Scores(parsedScores);
        const newResult = { date: newFepDate, itemScores: parsedScores, calculatedScales };

        // Ruft die von App.js übergebene Speicherfunktion auf
        onSaveResult(newResult);

        // Formular in dieser Komponente zurücksetzen
        setFepItemScores(Array(40).fill(''));
        setAppError(null);
    };

    return (
        <div>
            <h4 className="text-xl font-semibold text-gray-700 mb-4">FEP-2 Erfassung & Auswertung</h4>
            <div className="bg-gray-50 p-4 rounded-lg mb-6 shadow">
                <h5 className="text-md font-semibold text-gray-600 mb-3">Neues FEP-2 Ergebnis hinzufügen</h5>
                <div className="mb-4">
                    <label htmlFor="fepDate" className="block text-sm font-medium text-gray-700">Datum der Erhebung</label>
                    <input type="date" id="fepDate" value={newFepDate} onChange={(e) => setNewFepDate(e.target.value)} className={`${inputStd} w-full md:w-1/3`} />
                </div>
                <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-x-4 gap-y-3 mb-4">
                    {fepItemScores.map((score, index) => ( 
                        <div key={index}>
                            <label htmlFor={`fepItem${index+1}`} className="block text-xs font-medium text-gray-600">Item {index+1}</label>
                            <input 
                                ref={el => fepInputRefs.current[index] = el}
                                type="number" 
                                id={`fepItem${index+1}`} 
                                value={score} 
                                onChange={(e) => handleFepItemChange(index, e.target.value)} 
                                placeholder="1-5" 
                                className={`${inputStd} px-2 py-1 text-center`}
                            />
                        </div>
                    ))}
                </div>
                <button onClick={handleAddFep} className={btnPrimary}><PlusCircleIcon className="w-5 h-5 mr-2" /> FEP-2 Ergebnis speichern</button>
            </div>
            <div className="space-y-4">
                <h5 className="text-md font-semibold text-gray-600 mb-2">Protokollierte FEP-2 Ergebnisse:</h5>
                {(patient.fep2Results || []).length === 0 && <p className="text-sm text-gray-500">Keine FEP-2 Ergebnisse erfasst.</p>}
                {(patient.fep2Results || []).map((result, index) => (
                    <div key={index} className="bg-white border p-4 rounded-md shadow-sm">
                        <div className="flex justify-between items-center mb-2">
                            <p className="font-medium text-indigo-600">Datum: {new Date(result.date).toLocaleDateString('de-DE')}</p>
                            <button onClick={() => onDeleteResult(result.date)} className={`${btnDanger} p-1 text-xs`}><Trash2 className="w-4 h-4" /></button>
                        </div>
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-x-4 gap-y-1 text-sm">
                            {Object.entries(result.calculatedScales).map(([key, value]) => ( 
                                <div key={key} className="flex justify-between">
                                    <span className="text-gray-600 capitalize">{key.replace(/_/g, ' ')}:</span>
                                    <span className="font-medium text-gray-800">
                                        {value === null ? 'N/A' : value} 
                                        {key === 'gesamtbelastung' && value !== null && (value > 2.49 ? <span className="ml-1 text-red-500">(Belastet)</span> : <span className="ml-1 text-green-500">(Unbelastet)</span>)}
                                    </span>
                                </div>
                            ))}
                        </div>
                    </div>
                ))}
            </div>
        </div> 
    );
};

export default FEP2Component;
