import React, { useState } from 'react';

const Ptv1Form = ({ patient, therapistProfile, onGenerate }) => {
    const [formData, setFormData] = useState({
        therapieform: 'Verhaltenstherapie',
        behandlungsart: '',
        antragstyp: '',
        sprechstunde: false,
        sprechstundeDatum: '',
        stationaer: false,
        vergangeneBehandlung: false,
        kostentraeger: '',
        krankenkasseAdresse: '',
    });

    const today = new Date().toLocaleDateString('de-DE');

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        const newValue = type === 'checkbox' ? checked : value;
        setFormData(prev => ({ ...prev, [name]: newValue }));
    };

    const ptv1ImageUrl = '/Pictures/ptv1-background.png';

    if (!patient) {
        return <div className="text-center text-gray-500 p-8">Bitte wählen Sie oben einen Patienten aus.</div>;
    }

    return (
        <div className="ptv-form-container bg-white p-4">
            <style>{`
                .ptv-wrapper {
                    position: relative;
                    width: 794px;
                    height: 1123px;
                    margin: auto;
                }
                .ptv-background {
                    width: 100%;
                    height: 100%;
                }
                .ptv-overlay {
                    position: absolute;
                    top: 0;
                    left: 0;
                    width: 100%;
                    height: 100%;
                    font-family: 'Courier New', Courier, monospace;
                    font-size: 10pt;
                }
                .ptv-field {
                    position: absolute;
                    background: transparent;
                    border: none;
                    font-size: 10pt;
                }
                .name { top: 102px; left: 70px; width: 250px; }
                .geburtsdatum { top: 112px; left: 255px; width: 80px; }
                .versichertennr { top: 162px; left: 160px; width: 150px; }
                .krankenkasse { top: 52px; left: 55px; width: 150px; }
                .bsnr { top: 125px; left: 110px; width: 100px; }
                .arztnr { top: 125px; left: 300px; width: 100px; }
                .datum { top: 189px; left: 255px; width: 80px; }
                .kreuz-vt { top: 377px; left: 70px; font-size: 16pt; }
                .kreuz-art-einzel { top: 377px; left: 255px; font-size: 16pt; }
                .kreuz-art-gruppe { top: 398px; left: 255px; font-size: 16pt; }
                .kreuz-art-kombi { top: 420px; left: 255px; font-size: 16pt; }
                .kreuz-antrag-erst { top: 455px; left: 70px; font-size: 16pt; }
                .kreuz-antrag-folge { top: 455px; left: 250px; font-size: 16pt; }
            `}</style>

            <div className="ptv-wrapper">
                <img src={ptv1ImageUrl} alt="PTV1 Formular" className="ptv-background" />
                <div className="ptv-overlay">
                    <div className="ptv-field name">{patient.name}, {patient.vorname}</div>
                    <div className="ptv-field geburtsdatum">{new Date(patient.geburtsdatum).toLocaleDateString('de-DE')}</div>
                    <div className="ptv-field versichertennr">{patient.versichertennummer}</div>
                    <div className="ptv-field krankenkasse">{patient.krankenkasse}</div>
                    <div className="ptv-field bsnr">{therapistProfile?.bsnr}</div>
                    <div className="ptv-field arztnr">{therapistProfile?.arztnr}</div>
                    <div className="ptv-field datum">{today}</div>

                    {/* Automatische Kreuzchen */}
                    <div className="ptv-field kreuz-vt">✓</div>
                    {formData.behandlungsart === 'einzel' && <div className="ptv-field kreuz-art-einzel">✓</div>}
                    {formData.behandlungsart === 'gruppe' && <div className="ptv-field kreuz-art-gruppe">✓</div>}
                    {formData.behandlungsart === 'kombination' && <div className="ptv-field kreuz-art-kombi">✓</div>}

                    {formData.antragstyp === 'erst' && <div className="ptv-field kreuz-antrag-erst">✓</div>}
                    {formData.antragstyp === 'folge' && <div className="ptv-field kreuz-antrag-folge">✓</div>}
                </div>
            </div>

            <div className="mt-6 space-y-2">
                <label className="block">
                    Art der Behandlung:
                    <select name="behandlungsart" value={formData.behandlungsart} onChange={handleChange} className="ml-2">
                        <option value="">-- wählen --</option>
                        <option value="einzel">Einzel</option>
                        <option value="gruppe">Gruppe</option>
                        <option value="kombination">Kombination</option>
                    </select>
                </label>
                <label className="block">
                    Antragstyp:
                    <select name="antragstyp" value={formData.antragstyp} onChange={handleChange} className="ml-2">
                        <option value="">-- wählen --</option>
                        <option value="erst">Erstantrag</option>
                        <option value="folge">Folgeantrag</option>
                    </select>
                </label>
            </div>

            <div className="flex justify-end mt-6">
                <button onClick={() => onGenerate({ ...formData, patientDaten: patient })} className="px-6 py-2 text-white bg-green-600 rounded-md hover:bg-green-700 font-semibold">
                    Formular generieren
                </button>
            </div>
        </div>
    );
};

export default Ptv1Form;
