export const SCL90REntryForm = ({ onSave, patientAge, patientGender }) => {
    const [itemScores, setItemScores] = useState(Array(90).fill(''));
    const inputRefs = useRef([]);
    const [useCustomDate, setUseCustomDate] = useState(false);
    const [testDate, setTestDate] = useState(new Date().toISOString().split('T')[0]);

    const handleItemChange = (index, value) => {
        if (/^[0-4]?$/.test(value)) {
            const newScores = [...itemScores];
            newScores[index] = value;
            setItemScores(newScores);
            if (value.length === 1 && index < 89 && inputRefs.current[index + 1]) {
                inputRefs.current[index + 1].focus();
            }
        }
    };
    
    const handleSaveAndCalculate = () => {
        const parsedAge = parseInt(patientAge, 10);
        const parsedGender = parseInt(patientGender, 10) || 1; 

        try {
            const parsedItemScores = itemScores.map((score, index) => {
                const val = parseInt(score, 10);
                if (isNaN(val) || val < 0 || val > 4) {
                    throw new Error(`Ungültiger Wert für Item ${index + 1}. Bitte geben Sie eine Zahl von 0-4 ein.`);
                }
                return val;
            });
            
            const kategorie = getAgeCategory(parsedAge);
            const rawScores = {};
            for (const scaleName in scales) {
                rawScores[scaleName] = scales[scaleName].reduce((sum, itemNum) => sum + (parsedItemScores[itemNum - 1] || 0), 0);
            }
            let totalSum = parsedItemScores.reduce((sum, score) => sum + score, 0);
            let positiveSymptomTotal = parsedItemScores.filter(score => score > 0).length;
            const gsi = totalSum / 90;
            const pst = positiveSymptomTotal;
            const psdi = pst > 0 ? totalSum / pst : 0;
            const tScores = {
                'Somatisierung': T_Skala1(rawScores['Somatisierung'], kategorie, parsedGender),
                'Zwanghaftigkeit': T_Skala2(rawScores['Zwanghaftigkeit'], kategorie, parsedGender),
                'Unsicherheit im Sozialkontakt': T_Skala3(rawScores['Unsicherheit im Sozialkontakt'], kategorie, parsedGender),
                'Depressivität': T_Skala4(rawScores['Depressivität'], kategorie, parsedGender),
                'Ängstlichkeit': T_Skala5(rawScores['Ängstlichkeit'], kategorie, parsedGender),
                'Aggressivität/Feindseligkeit': T_Skala6(rawScores['Aggressivität/Feindseligkeit'], kategorie, parsedGender),
                'Phobische Angst': T_Skala7(rawScores['Phobische Angst'], kategorie, parsedGender),
                'Paranoides Denken': T_Skala8(rawScores['Paranoides Denken'], kategorie, parsedGender),
                'Psychotizismus': T_Skala9(rawScores['Psychotizismus'], kategorie, parsedGender),
                'GSI': T_GSI(gsi, kategorie, parsedGender),
                'PSDI': T_PSDI(psdi, kategorie, parsedGender),
                'PST': T_PST(pst, kategorie, parsedGender),
            };
            
            onSave({ 
                results: { rawScores, tScores, globalScores: { gsi, psdi, pst }, itemScores: parsedItemScores },
                date: new Date(testDate).toISOString()
            });

        } catch (e) {
            alert(e.message);
        }
    };

    const inputStyle = "mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-teal-500 focus:border-teal-500 sm:text-sm";

    return(
        <div className="space-y-4">
             <div className="grid grid-cols-5 md:grid-cols-10 gap-x-4 gap-y-2">
                {itemScores.map((score, index) => (
                    <div key={index} className="flex flex-col">
                        <label htmlFor={`item-${index}`} className="mb-1 text-xs font-medium text-gray-600">Item {index + 1}</label>
                        <input ref={el => inputRefs.current[index] = el} type="number" id={`item-${index}`} value={score} onChange={(e) => handleItemChange(index, e.target.value)} min="0" max="4" className="w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm h-10 px-2 text-center"/>
                    </div>
                ))}
            </div>

            <div className="space-y-2 p-3 bg-gray-50 rounded-md border">
                <div className="flex items-center">
                    <input type="checkbox" id="custom-date-toggle" checked={useCustomDate} onChange={(e) => setUseCustomDate(e.target.checked)} className="h-4 w-4 text-teal-600 border-gray-300 rounded focus:ring-teal-500"/>
                    <label htmlFor="custom-date-toggle" className="ml-2 block text-sm text-gray-900">Anderes Testdatum verwenden</label>
                </div>
                {useCustomDate && (
                    <div>
                        <label htmlFor="test-date" className="block text-xs font-medium text-gray-600 mb-1">Benutzerdefiniertes Testdatum</label>
                        <input type="date" id="test-date" value={testDate} onChange={(e) => setTestDate(e.target.value)} className={inputStyle}/>
                    </div>
                )}
            </div>

            <div className="flex justify-end">
                <button onClick={handleSaveAndCalculate} className="px-4 py-2 text-sm text-white bg-green-600 rounded-md hover:bg-green-700">Ergebnisse speichern</button>
            </div>
        </div>
    );
};

const SCL90RComponent = ({ patient, onDeleteTest }) => {
    const [viewingResult, setViewingResult] = useState(null);
    const [expandedScales, setExpandedScales] = useState({});
    const [activeScoreFilter, setActiveScoreFilter] = useState(null);

    useEffect(() => {
        const completedResults = (patient.scl90rResults || []).filter(r => r.status !== 'pending');
        if (completedResults.length > 0) {
            const sorted = [...completedResults].sort((a,b) => new Date(b.date) - new Date(a.date));
            setViewingResult(sorted[0]);
        } else {
            setViewingResult(null);
        }
    }, [patient.scl90rResults]);
    
    
    const getBarColor = (tValue) => {
        if (tValue === null || tValue === undefined) return 'transparent';
        if (tValue >= 60) {
            const intensity = Math.min((tValue - 60) / 30, 1); 
            const red = Math.round(239 - intensity * 149);
            const green = Math.round(68 - intensity * 48);
            const blue = Math.round(68 - intensity * 48);
            return `rgb(${red}, ${green}, ${blue})`;
        }
        if (tValue >= 55) {
            const intensity = (tValue - 55) / 4;
            const green = Math.round(191 - intensity * 46);
            return `rgb(251, ${green}, 36)`;
        }
        const intensity = Math.max(0, (tValue - 20) / 35);
        const colorValue = Math.round(209 - intensity * 100);
        return `rgb(${colorValue}, ${colorValue}, ${colorValue})`;
    };
    
    const renderResultView = (resultData) => {
        if (!resultData || !resultData.results) return null;

        const { rawScores, tScores, globalScores, itemScores: resultItemScores } = resultData.results;
        
        const chartData = [...Object.keys(scales).map(key => ({name: key, shortName: key.substring(0, 7) + '.', 'T-Wert': tScores[key], 'Rohwert': rawScores[key],})),{ name: 'spacer', 'T-Wert': null, shortName: '' },{ name: 'GSI', shortName: 'GSI', 'T-Wert': tScores.GSI, 'Rohwert': globalScores.gsi.toFixed(2) },{ name: 'PST', shortName: 'PST', 'T-Wert': tScores.PST, 'Rohwert': globalScores.pst },{ name: 'PSDI', shortName: 'PSDI', 'T-Wert': tScores.PSDI, 'Rohwert': globalScores.psdi.toFixed(2) },];
        const significantScales = Object.entries(tScores).filter(([key]) => scales[key] && tScores[key] >= 55).sort(([, valA], [, valB]) => valB - valA);

        const CustomTooltipContent = ({ active, payload }) => {
            if (active && payload && payload.length) {
                const data = payload[0].payload;
                if (!data.name || data.name === 'spacer') return null;
                return (
                    <div className="bg-white p-4 border rounded-md shadow-lg max-w-xs text-left">
                        <p className="font-bold text-gray-800">{data.name}</p>
                        <p className="text-sm text-gray-600"><span className="font-semibold">T-Wert:</span> {data['T-Wert']} | <span className="font-semibold">Rohwert:</span> {data['Rohwert']}</p>
                        {scaleDetails[data.name] && <p className="mt-2 text-xs text-gray-500">{scaleDetails[data.name].inhalt}</p>}
                    </div>
                );
            }
            return null;
        };

        const GlobalScore = ({ label, value, tValue, interpretation }) => {
            const colorClass = tValue >= 60 ? 'bg-red-100 text-red-800' : tValue >= 55 ? 'bg-yellow-100 text-yellow-800' : 'bg-gray-100 text-gray-800';
            return (
                <div className={`p-3 rounded-md ${colorClass}`}>
                    <div className="flex justify-between items-center"><span className="font-semibold">{label}</span><span className="font-bold text-lg">{value} (T={tValue})</span></div>
                    <p className="text-sm mt-1">{interpretation}</p>
                </div>
            );
        };
        
        return (
            <div className="bg-white p-6 rounded-2xl shadow-lg mt-4">
                 <h2 className="text-2xl font-semibold mb-6 text-gray-800 border-b pb-3">Ergebnisse vom {new Date(resultData.date).toLocaleDateString('de-DE')}</h2>
                 <div className="mb-8">
                    <h3 className="text-xl font-semibold mb-4 text-gray-700">T-Werte Profil</h3>
                     <ResponsiveContainer width="100%" height={400}>
                         <BarChart data={chartData} margin={{ top: 5, right: 20, left: 0, bottom: 60 }}>
                             <CartesianGrid strokeDasharray="3 3" />
                             <XAxis dataKey="shortName" angle={-60} textAnchor="end" interval={0} height={80} style={{ fontSize: '10px' }} />
                             <YAxis domain={[20, 90]} />
                             <Tooltip content={<CustomTooltipContent />} cursor={{fill: 'rgba(239, 246, 255, 0.5)'}}/>
                             <ReferenceLine y={60} label={{ value: 'Auffällig (T≥60)', position: 'insideTopLeft', fill: '#ef4444', fontSize: 12 }} stroke="red" strokeDasharray="3 3" />
                             <ReferenceLine y={55} label={{ value: 'Grenzwertig (T≥55)', position: 'insideTopLeft', fill: '#f59e0b', fontSize: 12 }} stroke="orange" strokeDasharray="3 3" />
                             <Bar dataKey="T-Wert">{chartData.map((entry, index) => (<Cell key={`cell-${index}`} fill={getBarColor(entry['T-Wert'])} />))}</Bar>
                         </BarChart>
                     </ResponsiveContainer>
                </div>
                 <div className="mt-8">
                    <h3 className="text-xl font-semibold mb-4 text-gray-700">Zusammenfassende Interpretation</h3>
                    <div className="space-y-4">
                        <div>
                            <h4 className="font-bold text-lg mb-2">Globale Kennwerte</h4>
                            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                                <GlobalScore label="GSI" value={globalScores.gsi.toFixed(2)} tValue={tScores.GSI} interpretation={scaleDetails.GSI.inhalt} />
                                <GlobalScore label="PST" value={globalScores.pst} tValue={tScores.PST} interpretation={scaleDetails.PST.inhalt} />
                                <GlobalScore label="PSDI" value={globalScores.psdi.toFixed(2)} tValue={tScores.PSDI} interpretation={scaleDetails.PSDI.inhalt} />
                            </div>
                        </div>

                        <div>
                            <h4 className="font-bold text-lg mb-2 mt-4">Auffällige Skalen (T-Wert ≥ 55)</h4>
                            {significantScales.length > 0 ? (
                                <ul className="space-y-2">
                                    {significantScales.map(([key, value]) => {
                                        const colorClass = value >= 60 ? 'bg-red-100' : 'bg-yellow-100';
                                        const scaleItems = scales[key];
                                        return (
                                            <li key={key} className={`p-3 rounded-md transition-all duration-300 ${colorClass}`}>
                                                <div className="flex justify-between items-center cursor-pointer" onClick={() => setExpandedScales(prev => ({...prev, [key]: !prev[key]}))}>
                                                    <span className="font-semibold">{key}</span>
                                                    <span className="font-bold">T = {value}</span>
                                                </div>
                                                <p className="text-sm text-gray-600 mt-1 italic">{scaleDetails[key].inhalt}</p>
                                                {expandedScales[key] && (
                                                    <div className="mt-2 pt-2 border-t border-gray-400 text-sm text-gray-700 space-y-3">
                                                         <div><span className="font-semibold">Typische Symptome:</span> {scaleDetails[key].symptome}</div>
                                                         <div><span className="font-semibold">Klinische Bedeutung:</span> {scaleDetails[key].bedeutung}</div>
                                                         <div className="mt-2">
                                                             <p className="font-semibold">Item-Antworten:</p>
                                                            <ul className="list-disc list-inside space-y-1 mt-1">
                                                                {scaleItems.map(itemNum => (
                                                                    <li key={itemNum} className="text-xs">
                                                                        <strong>Item {itemNum}:</strong> {itemDetails[itemNum] || 'Item-Text nicht gefunden'} = <strong>{resultItemScores[itemNum - 1]}</strong>
                                                                    </li>
                                                                ))}
                                                            </ul>
                                                         </div>
                                                    </div>
                                                )}
                                            </li>
                                        );
                                    })}
                                </ul>
                            ) : ( <p className="text-sm text-gray-600">Keine klinisch auffälligen oder grenzwertigen Werte.</p>)}
                        </div>
                        <div className="mt-6">
                             <h4 className="font-bold text-lg mb-2">Items nach Score filtern</h4>
                             <div className="flex space-x-2 mb-2">
                                 {[4, 3, 2, 1, 0].map(scoreValue => (
                                     <button key={scoreValue} onClick={() => setActiveScoreFilter(activeScoreFilter === scoreValue ? null : scoreValue)}
                                             className={`px-3 py-1 text-sm rounded-full ${activeScoreFilter === scoreValue ? 'bg-indigo-600 text-white' : 'bg-gray-200 text-gray-700'}`}>
                                         Score {scoreValue}
                                     </button>
                                 ))}
                             </div>
                             {activeScoreFilter !== null && (
                                 <div className="p-3 bg-gray-50 rounded-md">
                                     <ul className="list-disc list-inside columns-2 sm:columns-3 md:columns-4">
                                         {resultItemScores.map((score, index) => parseInt(score, 10) === activeScoreFilter && (<li key={index} className="text-sm"><strong>Item {index + 1}:</strong> {itemDetails[index + 1] || 'Unbekanntes Item'}</li>)).filter(Boolean)}
                                     </ul>
                                 </div>
                             )}
                        </div>
                    </div>
                 </div>
            </div>
        );
    };
    
    const completedResults = (patient.scl90rResults || []).filter(r => r.status !== 'pending').sort((a,b) => new Date(b.date) - new Date(a.date));

    return (
        <div>
            <h4 className="text-xl font-semibold text-gray-700 mb-4">SCL-90-R Auswertungen</h4>
            
             <div className="mt-4">
                 <h5 className="text-md font-semibold text-gray-600 mb-2">Protokollierte Ergebnisse:</h5>
                 {completedResults.length === 0 ? (
                    <p className="text-sm text-gray-500">Keine abgeschlossenen SCL-90-R Ergebnisse für diesen Patienten erfasst.</p>
                ) : (
                    <ul className="space-y-2">
                        {completedResults.map((result, index) => (
                            <li key={result.testInstanceId || index} className={`p-3 rounded-md shadow-sm border flex justify-between items-center cursor-pointer hover:bg-indigo-50 ${viewingResult?.date === result.date ? 'bg-indigo-100 border-indigo-300' : 'bg-white'}`} onClick={() => setViewingResult(result)}>
                                <div>
                                    <span className="font-medium text-indigo-600">Datum: {new Date(result.date).toLocaleDateString('de-DE')}</span>
                                    <span className="ml-4 text-sm text-gray-600">GSI: <span className="font-bold">{result.results?.globalScores?.gsi?.toFixed(2)} (T={result.results?.tScores?.GSI})</span></span>
                                </div>
                                <button onClick={(e) => { e.stopPropagation(); onDeleteTest(result.testInstanceId, 'scl90r'); }} className="text-red-500 hover:text-red-700 transition duration-150 p-1 rounded hover:bg-red-100" title="Dieses Ergebnis löschen">
                                    <Trash2 className="w-5 h-5" />
                                </button>
                            </li>
                        ))}
                    </ul>
                )}
            </div>
             
            {viewingResult ? renderResultView(viewingResult) : <div className="mt-8 text-center text-gray-500">Bitte wählen Sie ein Ergebnis aus der Liste aus, um die Details anzuzeigen.</div>}
        </div>
    );
};

export default SCL90RComponent